  col += gcol;
  col = mix(col, hcol, smoothstep(0.0, -aa, d));
  col = mix(col, fbcol, smoothstep(0.0, -aa, abs(d+0.01)-0.01));
  col *= smoothstep(1.75, 0.5, length(pp));

  col = aces_approx(col); 
  col = sqrt(col); 
  return col;
}

