float Noise(in vec3 p)
{
    vec3 i = floor(p);
        vec3 f = fract(p); 
        f *= f * (3.0-2.0*f);

    return mix(
                mix(mix(hash13(i + vec3(0.,0.,0.)), hash13(i + vec3(1.,0.,0.)),f.x),
                        mix(hash13(i + vec3(0.,1.,0.)), hash13(i + vec3(1.,1.,0.)),f.x),
                        f.y),
                mix(mix(hash13(i + vec3(0.,0.,1.)), hash13(i + vec3(1.,0.,1.)),f.x),
                        mix(hash13(i + vec3(0.,1.,1.)), hash13(i + vec3(1.,1.,1.)),f.x),
                        f.y),
                f.z);

}

// simple mouse rotate and zoom for shader
#define pi 3.14159265359 

mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

vec2 mouseRotZoom(vec2 uv) {
    // allow mouse zoom and rotate    
    vec2 mouse = (iMouse.xy == vec2(0.)) ? vec2(1.0,0.72) : iMouse.xy/iResolution.xy;
    uv.xy *= r2d(-(mouse.x)*pi*2.);
    uv *= (1./(5.0*mouse.y));
    return uv;
}


vec3 hsb2rgb( in vec3 c )
{
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return (c.z * mix( vec3(1.0), rgb, c.y));
}

#define UV_NOISE_DIV  (13.0/(mouse2.x/mouse2.y))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse2 = (iMouse.xy == vec2(0.)) ? vec2(1.0) : iMouse.xy/iResolution.xy;

    vec2 uv = fragCoord / iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    uv = mouseRotZoom(uv);

    float time = iTime * 0.2;
    vec3 color = vec3(0.0);
    for(int i = 0; i < 100; i++)
    {
        vec2 seed = vec2(float(i) * 40.33*Noise(vec3(uv.x/UV_NOISE_DIV,uv.y/UV_NOISE_DIV,0.1*time/200.)), float(i) * 1000.0) + time*(0.03 + sin(time*0.001) * 0.03) ;
        vec2 pos = fract(sin(seed) * 43.5453)-0.55;
        float HoleDist = length(vec2(0.0) - uv);
        pos = mix(pos, uv, smoothstep(-2.0, 2.3, HoleDist));
        float brightness = 0.8 / length(uv - pos);
        vec3 starColor = vec3(brightness * (0.002 + (abs(sin(time )) * 0.001)));
        starColor *= sin(time * brightness) * 0.001 + 0.0019;

        starColor *= 2.0*hsb2rgb( vec3(seed.x, 1.0, 1.0) );
        color += starColor;
    }
    float Hole = (80.3 ) * (length(uv/0.4)-1.0);
    color = mix(color, vec3(-0.0), Hole);

    // Output to screen
    fragColor = vec4(color,1.0);
}

