#define SHADOW_STEPS 32
#define SHADOW_SOFT 5.

#define BOUNCES 0
#define ROUGHNESS 0.1
#define SAMPLES 1

#define EXPOSURE 4.4
#define GAMMA 2.2

#define PI   3.1415926535
#define PI_2 1.5707963267

// Set to 32 or higher for more clarity
#define FOLD_STEPS 10

float linear_rgb_c(float v) {
  if (v <= 0.04045) {
    return (v / 12.92);
  }
  else {
    return pow((v + 0.005) / 1.055f, 2.4);
  }
}

vec3 linear_rgb(vec3 rgba) {
  return pow(rgba, vec3(2.2));
}
#define R_2_2 1.0f / 2.2f
vec3 nonlinear_rgb(vec3 rgb) {
    return pow(rgb, vec3(R_2_2));
}

vec3 rgb_to_xyz(vec3 rgba) {
  vec3 srgb = linear_rgb(rgba);
  return vec3(
    dot(vec3(0.4124564, 0.3575761, 0.1804375), srgb),
    dot(vec3(0.2126729, 0.7151522, 0.0721750), srgb),
    dot(vec3(0.0193339, 0.1191920, 0.9503041), srgb));
}

vec3 xyz_to_rgb(vec3 xyz) {
  return nonlinear_rgb(
    vec3(dot(vec3(3.2404542, -1.5371385, -0.4985314), xyz),
      dot(vec3(-0.9692660, 1.8760108, 0.0415560), xyz),
      dot(vec3(0.0556434, -0.2040259, 1.0572252), xyz)));
}

