//https://iquilezles.org/articles/distfunctions/
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float fsqrt(float x) {
    float c = 1.41421356237;
    float x0 = c * x;
    float nx = 0.5*(x0 + (x / x0));
    return 0.5*(nx + (x / nx));
}

float flength(vec3 p) {
    float s = max(max(abs(p.x),abs(p.y)), abs(p.z));
    vec3 np = p / s;
    return fsqrt(dot(np,np)) * s;
}


float atan2(float y, float x) {
    return atan(y / (x+0.000001)) + (PI_2*sign(y)*sign(x)*(sign(x) - 1.));
}
// 315, 45, 135, 225
#define pi_7_4 PI * 7. / 4.
#define pi_1_4 PI * 1. / 4.
#define pi_3_4 PI * 3. / 4.
#define pi_5_4 PI * 5. / 4.
vec2 map_disk_lookup(vec2 p) {
    float theta = atan2(p.y, p.x);
    float radius = fsqrt(p.x * p.x + p.y * p.y);
    if (pi_7_4 < theta && theta < pi_1_4) {   
         return vec2(p.x,pi_1_4 * (p.y / p.x));
    } else if (pi_1_4 < theta && theta <= pi_3_4) {
         return vec2(p.y,pi_1_4 * (2. - (p.x / p.y)));
    } else if (pi_3_4 < theta && theta <= pi_5_4) {
         return vec2(-p.x,pi_1_4 * (4. + (p.y / p.x)));
    } else {
         return vec2(-p.y,pi_1_4 * (6. - (p.x / p.y)));
    }
}

vec4 box_fold(vec4 z, float l) {
    return vec4(clamp(z.xyz, -l, (l))* 2. - z.xyz, z.w) ;
}

vec4 ball_fold(vec4 z, vec2 m) {
    float r2 = dot(z.xyz, z.xyz);
    if (r2 < m.x) {
        return z * m.y / m.x;
    } else if (r2 < m.y) {
        return z * m.y / r2;   
    } else {
        return z;
    }
}

vec2 fold(vec2 p, float theta) {
    vec2 n = vec2(cos(-theta), sin(-theta));
    float v = min(0., dot(p, n));
    return p - (2. * v * n);
}


vec3 fold3d(vec3 p, vec3 theta) {
    vec3 np = p;
    np.xy = fold(np.xy, theta.x);
    np.xz = fold(np.xz, theta.y);
    np.yz = fold(np.yz, theta.z);
    return np;
}
vec4 menger(vec3 point, Inputs inputs) {
    vec3  pos = point * 0.5 + 0.5;
    //let ( x, y, z) = (pos.0,pos.1,pos.2);

    float xx = abs(pos.x - 0.5) - 0.5;
    float yy = abs(pos.y - 0.5) - 0.5;
    float zz = abs(pos.z - 0.5) - 0.5;

    float d1 = max(xx,max(yy,zz));
    float d = d1;
    float p = 1.;

    int i = 0;
    int steps = 3;
    while (i < steps) {
        i += 1;
        float xa = mod(3. * pos.x * p, 3.) ;
        float ya = mod(3. * pos.y * p, 3.);
        float za = mod(3. * pos.z * p, 3.);
        p *= 3.;

        xx = 0.5 - abs(xa - 1.5);
        yy = 0.5 - abs(ya - 1.5); 
        zz = 0.5 - abs(za - 1.5);

        d1 = min(max(xx,zz),min(max(xx,yy),max(yy,zz))) / p;
        d = max(d,d1);

    }

    return vec4(1.,1.,1., d * 2.);
}

vec4 mandelbulb(vec3 p, Inputs inputs) {
    vec3 c = p.xzy;
    c.z = -c.z;
    vec3 z = c;
     //c = inputs.j;
    int i = 0;
    float r = 0.;
    float dr = 1.;
    float s = 0.;
    while (i < 16) {
        r = dot(z, z);
        if (r > 16.) break;
        r = fsqrt(r);
        float theta = acos(z.z / r);
        float phi = atan2(z.y, z.x);
       float r2 = r * r;
       float r4 = r2 * r2;
        float r5 = r4 * r;
        dr = r5*  6. * dr +1.;
        float zr = r5 * r;
        theta *= 6.;
        phi *= 6.;

        z = zr * vec3(
            sin(theta) * cos(phi),
            sin(phi) * sin(theta),
            cos(theta)
        ) + c;

        //z = zr * vec3( cos(phi)*cos(theta), sin(theta), sin(phi) ) + c ;

        i += 1;
        s += exp(-r);
    }
    r = length(z);
    float theta = s * PI * 2.;
    float red = (sin(theta) + 1.0) * 0.5;
    float blu = (cos(theta) + 1.0) * 0.5;
    float grn = (red + blu) * 0.5;
    vec3 color =  clamp(vec3(red, grn, blu), 0.0, 1.0);
    //color = vec3(1.);
    return vec4(color, 0.25 * r * log(r) / abs(dr ));
}
vec4 mandelbox2(vec3 p, Inputs inputs) {
    vec3 c = p;
    vec3 last_z = vec3(0.);
    vec4 z = vec4(c, 1.);
    int counter = 0;
    int min_period = 1;
    int i = 0;
    //vec3 point = vec3(0.);
    float mn = 1000.;
    float mx = -1000.;
    float avg = 0.;
    while (i < FOLD_STEPS) {

        z.xyz = fold3d(z.xyz, inputs.b * PI * 2.);
        //z = box_fold(z, inputs.a.z);
        z.xyz = clamp(z.xyz, -inputs.a.z, inputs.a.z)* 2. - z.xyz;
        z = ball_fold(z, vec2(inputs.a.x, inputs.a.y));
        //z.xyz = fold3d(z.xyz, inputs.b);

        //z = inputs.a.w * ball_fold(box_fold(z, inputs.a.z), vec2(inputs.a.x, inputs.a.y));
        z.xyz = z.xyz * inputs.a.w + inputs.j;
        z.w = abs(inputs.a.w) * z.w + 1.;

        //float nm = flength(z.xyz - c);
        float nm = flength(c)/flength(z.xyz);
        if (nm > mx) {mx = nm;}
        if (nm < mn) {mn = nm;}
        avg += nm;
        i += 1;
    }
    avg /= float(i);
    float m = length(z.xyz);
    float x = m / abs(z.w);
    //float x2 =  0.1 - abs( sdTorus(z.xyz, vec2(0.,1.1))) ;
    //vec4 res = menger(0.25*z.xyz, inputs);
    //return vec4(res.xyz,abs(res.w/z.w));
    float v = (avg-mn) / (mx-mn);
    float theta = 4. * v  * PI * 2.;
    float red = (sin(theta) + 1.0) * 0.5;
    float blu = (cos(theta) + 1.0) * 0.5;
    float grn = (red + blu) * 0.5;
    return vec4(red,grn,blu,x);
}



float de(vec3 p, Inputs inputs) {
    return mandelbox2(p, inputs).w;
}

vec3 set_color(vec3 p, Inputs inputs) {
    return rgb_to_xyz(mandelbox2(p, inputs).rgb);
}
float huh(float d) {
//return STOP_DIST+STOP_DIST * (log(d+1.) / log(2.));// 
return STOP_DIST + max(0., 0.01*(1.-exp2(-(d)*0.01)));

    //return STOP_DIST + STOP_DIST * (1. - (1. / (1.+exp(d*d))));
    //return STOP_DIST * (1. - (1. / (1.+exp(d))));
}
float trace(Inputs inputs, int steps, float sd, vec3 ro, vec3 rd, vec3 co) {
    float d = sd;
    vec3 p = ro + (sd * rd);
    int s = 0;
    while (s < steps) {
        p = ro + (d * rd);
        float de = de(p, inputs);
        d += de;
        if (de < huh(length(p-co)) ) break;
        if (d > MAX_DIST) break;
        s+=1;
    }
    return d;
}


vec3 ortho(vec3 v)
{
    return abs(v.x) > abs(v.z) ? vec3(-v.y, v.x, 0.0)
                               : vec3(0.0, -v.z, v.y);
}


vec3 calcNormal( Inputs inputs, float d, vec3 p) // for function f(p)
{
    const float h = NORM_SCALE; // replace by an appropriate value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*de( p + k.xyy*h, inputs ) + 
                      k.yyx*de( p + k.yyx*h, inputs ) + 
                      k.yxy*de( p + k.yxy*h, inputs ) + 
                      k.xxx*de( p + k.xxx*h, inputs ) );
}


vec3 norm(Inputs inputs, float d, vec3 p) {
    return normalize(vec3(
        de(p + vec3(STOP_DIST, 0.,0.), inputs),
        de(p + vec3(0.,STOP_DIST, 0.), inputs),
        de(p + vec3(0.,0., STOP_DIST), inputs)
    ) - d );
}

// Wang hash
uint hash(inout uint  state) {
	state = (state ^ 61U) ^ (state >> 16U);
	state = state * 9U;
	state = state ^ (state >> 4);
	state = state * 0x27d4eb2dU;
	state = state ^ (state >> 15);
	return state;
}

float random_float_01(inout uint  state) {
    return float(hash(state)) / float(0xFFFFFFFFU);
}

vec3 sphere_rand(inout uint  state) {
    float z = random_float_01(state) * 2. - 1.;
    float theta = random_float_01(state) * 2. * PI;
    float mu2sqrt = fsqrt(1. - (z * z));
    return vec3(
        mu2sqrt * cos(theta),
        mu2sqrt * sin(theta),
        z
    );
}

float schlick(float ior, float theta) {
    float coeff = (1. - ior) / (1. + ior);
    coeff *= coeff;
    return coeff + ((1.- coeff)*pow(1. - theta,5.));
}

float ao(Inputs inputs, int steps, float sd, vec3 ro, vec3 rd, vec3 co) {
    float d = sd ;
    int i = 0;
    float mn = 0.;
    float mx = 0.;
    while (i < steps) {
        vec3 p = ro + (rd * float(i + 1) * AO_SCALE);
        float de = 0.125 * de(p, inputs);
        float decay = 1. / pow(2.,float(i + 1));
        mn += de *decay;
        mx +=  float(i + 1) * AO_SCALE * decay ; 

        i += 1;
    }
    return   clamp(mn/mx, 0., 1.);
}

float soft_shadow(Inputs inputs, int steps, float sd, vec3 ro, vec3 rd, vec3 co)
{
	float res = 1.0;
    float d = sd;
    for( int i=SHADOW_STEPS; i>0; i-- )
    {
        vec3 p = ro + rd*d;
		float dist = de(p, inputs);
        res = min( res, SHADOW_SOFT*dist/d );
        d += dist;
        if( res<huh(length(co - p))|| d>MAX_DIST ) break;
    }
    res = clamp( res, 0.0, 1.0 );
    return res*res*(3.0-2.0*res);
}

//https://iquilezles.org/articles/fog/
vec3 applyFog( in vec3  rgb,      // original color of the pixel
               in float d, // camera to point distance
               in vec3  rayDir,   // camera to point vector
               in vec3  sunDir )  // sun light direction
{
    float fogAmount = 1.0 - exp( -d*1. );
    float sunAmount = max( dot( rayDir, sunDir ), 0.0 );
    vec3  fogColor  = mix( vec3(0.5,0.6,0.7), // bluish
                           vec3(1.0,0.9,0.7), // yellowish
                           pow(sunAmount,8.0) );
    return mix( rgb, fogColor, fogAmount );
}

vec3 skybox_test(vec3 ro, vec3 rd) {
    vec3 sky_dir = normalize(vec3(0.0, 1., 0.));
    vec3 sun_dir = normalize(vec3(0.0, 0., -1.));

    float grnrd = max(0., -dot(rd, sky_dir));
    float skyrd = max(0., dot(rd, sky_dir));
    float sunrd = max(0., dot(rd, sun_dir));

    vec3 bmax = vec3( 92., 151., 247.) / 255.;
    vec3 bmin = pow(bmax, vec3(0.2)) / 3.;

    vec3 omax = vec3(207., 130., 6.) / 255.;
    vec3 omin = omax;

    float sky_pow = pow(skyrd, 0.5);
    float sun_pow = pow(sunrd, 1.);

    return 
    sky_pow * mix(5.*mix(omin, bmax, sky_pow ), 5.*mix(bmax, omax, sun_pow), sky_pow);
    //mix(vec3(0.),
        //mix(

            //pow((sun_pow ),8./(sky_pow) )
        //), pow((sky_pow ), 0.75));
}
vec3 rt(Inputs inputs, inout uint rand, vec3 ro, vec3 rd) {
    vec3 co = ro;
    vec3 throughput = vec3(1.);
    //vec3 il = vec3(0.);
    float t = 1.;
    vec3 lp = vec3(-t,t, -3.);
    int b = 0;

    while (b < BOUNCES+1) {
        float d = trace(inputs, MAX_STEPS, 0., ro, rd, co);
        vec3 n = vec3(0.);
        vec3 p = ro + (rd * d);
        float tde = de(p, inputs);
        float rough = ROUGHNESS;
        float hh = huh(length(co-p));
        vec3 nrd = rd;
        if (tde <  hh*MAX_DIST) {
            n = calcNormal(inputs, tde, p);
            p += n * hh;

            vec3 ord = normalize(ro - p);
            rough = (1. - rough) * mix(0., 1., schlick(1.5, max(0.,-dot(ord, n))));
            vec3 sphere = sphere_rand(rand);
            vec3 hemi = sphere * sign(dot(sphere, n));
            vec3 ray_diff = normalize(hemi);
            vec3 ray_spec = rd - (2. * n * dot(rd,n));

            nrd = normalize((1. -rough) * ray_diff + ( rough) * ray_spec);

            throughput *= pow(max(0., 1.0-acos(dot(nrd,n))/PI),4.);


            vec3 color = mix(vec3(1.),set_color(p, inputs),1. -  rough);
            #if (BOUNCES == 0)
            float ao = ao(inputs, AO_STEPS, STOP_DIST, p, n, co);
            float soft_shadow = soft_shadow(inputs, SHADOW_STEPS, STOP_DIST, p, nrd, co);
            color *= soft_shadow;
            color += 0.1 * vec3( ao);
            #endif
            throughput *= (color);

            //throughput *= mix(color*texture(iChannel0, n).w,vec3(texture(iChannel0, n).xyz),rough)   + 0.2 * ao * color;
        } else {
            break;
        }
        rd = nrd;
        if (b > 2) {
            float pp = max(max(throughput.x, throughput.y), throughput.z);
            if (random_float_01(rand) > pp) {
                break;
            }
            throughput *= 1. / pp;
        }
        ro = p;

        b+=1;
    }
    return throughput*rgb_to_xyz(skybox_test(ro, rd));
    //return throughput;
}

#define ACES
#define ACES_IN  mat3(vec3(0.59719f, 0.35458f, 0.04823f),vec3(0.07600f, 0.90834f, 0.01566f),vec3(0.02840f, 0.13383f, 0.83777f))
#define ACES_OUT mat3(vec3(1.60475f,-0.53108f,-0.07367f),vec3(-0.10208f,1.10813f,-0.00605f),vec3(-0.00327f,-0.07276f,1.07602f))
vec3 post_process(vec3 color) {
    color *= EXPOSURE;
    #ifdef REINHARDT
    color = color / (1.f + color);
    #endif
    #ifdef EXP_TM
    color = 1.f - exp2(-color*EXPOSURE);
    color.xyz = mix(color.xyz, smoothstep(0.f,1.f, color.xyz), .7f);
    #endif
    #ifdef ACES
    vec3 col = (color).xyz * ACES_IN;
    vec3 a = col * (col + 0.0245786f) - 0.000090537f;
    vec3 b = col * (0.983729f * col + 0.4329510f) + 0.238081f;
    color = (a / b) * ACES_OUT;
    #endif
    return xyz_to_rgb(color);
//  return xyz_to_rgb(1. - exp2(-color*EXPOSURE));
}

float tridist(float f) {
    float o = f * 2. - 1.;
    float v = o * fsqrt(abs(o));
    v = max(v, -1.f);
    return v - sign(o);
}
float fract_fr(float x) {
    return x - floor(x);
}
vec2 plastic(int s)  {
    float g = 1.324717957244f;
    float a1 = 1.f / g;
    float a2 = 1.f / (g * g);
    return vec2(
        tridist(fract_fr(0.5f + a1 * float(s + 1))),
        tridist(fract_fr(0.5f + a2 * float(s + 1)))
    );
}

float sdPlane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}  

float plane_intersect(vec3 po, vec3 n, vec3 rd, vec3 ro) {
    float denom = max(0., -dot(n, rd));
    if (denom > 0.)
    {
        float t = max(0., -dot(po - ro, n)) / denom;
        if (t >= 0.)
        {
            return t;
        }
    }
    return 100.;
}

