float easeOutQuad(float x) {
    return 1.0 - (1.0 - x) * (1.0 - x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec3 col = vec3(0);

    float f = 20.0;
    uv.xy *= f;
    vec2 ipos = ceil(uv);
    uv = fract(uv);

    float t = iTime - ipos.x * 0.02;
    float fx = sin(t * 2.0 - ipos.x) + 0.4;
    float dx = 2.0 * cos(2.0 * t - ipos.x);

    float fadingF = 1.0;
    if (dx < 1.5) {
        fadingF = easeOutQuad(fx * 0.5);
        col += (step(uv.x, min(fx, 1.0)) - step(uv.x + (f / iResolution.y), min(fx, 1.0))) * fadingF;
    } else
        col += (step(uv.x, fx) - step(uv.x + (f / iResolution.y), fx));

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Steel Spin by NuSan
// PC 4k intro made for Novoque 2020

// Unfortunately, the GPU Synthesizer I made cannot be ported on shadertoy, as it uses a second audio pass to compute reverbs
// So only soundcloud for now ...

// Original Tools: Leviathan, custom GPU synth, Shader Minifier, Crinkler
// https://www.pouet.net/prod.php?which=85684
// https://youtu.be/OjZVSqhReqA

// if sound doesn't start or seems desynchronised:
// try clicking pause/start button in the "soundcloud" square in the bottom right
// then press rewind just under the shader picture on the left

///////////////////////
// POST-PROCESS PASS //
///////////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{		
	vec2 frag = fragCoord.xy;
	vec2 uv = frag/iResolution.xy;

	float time = iTime-2.25;

    vec3 col = texture(iChannel0, uv).xyz;

    // Bloom computation
    vec3 cumul = vec3(0);
	for(float i=-2.; i<=2.5; ++i) {
		for(float j=-2.; j<=2.5; ++j) {
			vec4 cur = textureLod(iChannel1, uv + (vec2(i,j))*306./vec2(80000.,80000.), iResolution.y>7200. ? 60. : 4.);
			cumul += cur.xyz;
		}
	}

    // use more bloom for brighter values
	col += cumul * clamp(dot(cumul.xyz,vec3(.01))-.3,0.,1.)*0.005;

    // 'tone mapping'
    col = smoothstep(0.1,1.,col);
    col = pow(col,vec3(.65));

    // fade in / fade out
    col *= sat(time*5.) * sat(231.-time);

	fragColor = vec4(col, 1);
}

