                vec4 l = vec4(k.x,-k.y,-k.z,k.w);
                vec3 nn = normalize(vec3(k.y-k.x+dot(fract(p.yyyy),l),k.z-k.x+dot(fract(p.xxxx),l),1));
                float fog = 1.-exp(0.1*pow(4.0, -o.z-5.0-n.z*d)/(n.z*log(4.0)+pow(4.0,-o.z-5.0)/(n.z*log(4.0))));
