float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    float v = fract((p3.x + p3.y) * p3.z);
    return v;
}
float noise(in vec2 p) {
    vec2 k = fract(p);
    vec2 f = floor(p);
    k = k*k*(3.0-2.0*k);
    return mix(mix(hash12(f), hash12(f+vec2(1, 0)), k.x),
               mix(hash12(f+vec2(0, 1)), hash12(f+vec2(1, 1)), k.x), k.y);
}
float fbm(in vec2 p, in int n) {
    float v = 0.0;
    vec2 k = vec2(0.5, 2.0);
    for (float i = 0.; i < float(n); i += 1.) {
        v -= pow(noise(p*(k.x*=2.0)+i), 1.5)*(k.y*=0.5);
    }
    return v*0.5;
}
float get(in vec2 p, in int detail) {
    return pow(fbm(p*0.03, detail), 2.0)*50.-12.0;
}

