    float scale = 1.;

    for (int i = 0; i < 8; i++) {
        uv.x -= 0.5;
        uv *= 3.;

        uv.x = abs(uv.x);
        uv.x -= 0.5;

        d = dot(uv, n);
        uv -= n * min(0., d) * 2.;

        scale *= 3.;
    }


    //col += vec3(uv.x, uv.y, 0.);
    col += texture(iChannel0, rot(iTime / 20.) * uv / scale * zoom - M).rgb;

    d = abs(uv.y) + max(0., abs(uv.x) - 1.);
    //col += smoothstep(0.01 * scale, 0., d);


    // Output to screen
    fragColor = vec4(col, 1.0);
}

