vec3 getcolor(float h) {
  if (!key(CHAR_C)) h = pow(h,0.75);
  if (key(CHAR_H)) h = pow(h,1.75);
  if (key(CHAR_M)) return vec3(h);
  if (key(CHAR_X)) return h2rgb(h);
  vec3 colors[] =
    vec3[](vec3(0.996078,0.152941,0.0705882),
           vec3(0.988235,0.376471,0.0392157),
           vec3(0.984314,0.6,0.00784314),
           vec3(0.988235,0.8,0.101961),
           vec3(0.996078,0.996078,0.2),
           vec3(0.698039,0.843137,0.196078),

           vec3(0.4,0.690196,0.196078),
           vec3(0.203922,0.486275,0.596078),
           vec3(0.00784314,0.278431,0.996078),
           vec3(0.266667,0.141176,0.839216),
           vec3(0.52549,0.00392157,0.686275),
           vec3(0.760784,0.0784314,0.376471));
  vec3 c = colors[int(12.0*h)%12];
  c = pow(c,vec3(2.2));
  return c;
}

float uint2float(uint n) {
  // Visually, both ways give the same results, AFAICT.
  //return float(n)/exp2(32.0); // The lazy way.
  float x = uintBitsToFloat((n >> 9) | 0x3f800000u);
  return x - 1.0;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  float lwidth = 0.0015;
  vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
  float px = fwidth(p.x);
  vec3 col = vec3(0.25);
  float border = 0.03;
  float w = iResolution.x/iResolution.y-border;
  float h = 1.0-border;
  vec2 a = vec2(-w,h), b = vec2(w,h), c = vec2(-w,-h), d = vec2(w,-h);
  if (abs(p.x) <= w && abs(p.y) <= h) {
    uint h = 0x12345678u;
    int N = 10;
    for (int i = 0; i < N; i++) {
      float r0 = uint2float(h);
      float r1 = uint2float(xorshift(h));
      vec2 k = vec2(0.2);
      if (iMouse.x > 0.0) k = 0.5*iMouse.xy/iResolution.xy;
      r0 = 0.5+k.x*sin(0.1*iTime+2.0*PI*r0);
      r1 = 0.5+k.y*sin(0.0618*iTime+2.0*PI*r1);
      // a -- b
      // |    |
      // c -- d
      if (distance(a,b)+distance(c,d) > distance(a,c)+distance(b,d)) {
        vec2 e = r0*a+(1.0-r0)*b;
        vec2 f = r1*c+(1.0-r1)*d;
        if (linedist(p,e,f) > 0.0) { a = e; c = f; }
        else { b = e; d = f; h ^= 1u; }
      } else {
        vec2 e = r0*a+(1.0-r0)*c;
        vec2 f = r1*b+(1.0-r1)*d;
        if (linedist(p,e,f) < 0.0) { a = e; b = f; }
        else { c = e; d = f; h ^= 1u; }
      }
      h = ihash(h);
    }
    col = getcolor(uint2float(h));
  }
  col *= smoothstep(0.0,1.4*px,quaddist(p,a,b,c,d)-lwidth);
  col = pow(col,vec3(0.4545));
  if (alert) col.r = 1.0;
  fragColor = vec4(col,1);
}

