float segment0(vec2 p, vec2 a, vec2 b) {
  p -= a; b -= a;
  float h = dot(p,b)/dot(b,b);
  h = clamp(h, 0.0, 1.0);
  return length(p-b*h);
}

float segment(vec2 p, vec2 a, vec2 b) {
  return segment0(p,a,b);
  return min(segment0(p,a,b),segment0(p,-a,-b));
}

// From Chris Wellons: https://nullprogram.com/blog/2018/07/31/
uint ihash(uint x) {
  x += 0x12345678u;
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}

float rand(ivec2 p) {
  uint h = ihash(uint(p.x)^ihash(uint(p.y)));
  return float(h)/exp2(32.0);
}

float rand(int n) {
  uint h = ihash(uint(n));
  return float(h)/exp2(32.0);
}

vec3 h2rgb(float h) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  return rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
}

vec3 hsv2rgb(float h, float s, float v) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  // x(3-2x) = 3x-2x, f'(x) = 6x-6x = 6x(1-x)
  // f'(x) = 1-x, f = 0.5*(3.0*x-x)
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing       
  return v * mix( vec3(1.0), rgb, s);
}

float quaddist(vec2 p, vec2 a, vec2 b, vec2 c, vec2 d) {
  return min(min(segment(p,a,b),segment(p,c,d)),
             min(segment(p,a,c),segment(p,b,d)));
}

float linedist(vec2 p, vec2 a, vec2 b) {
  p -= a; b -= a;
  b = vec2(-b.y,b.x);
  return dot(p,b);
}

uint xorshift(uint value) {
  //return ihash(value);
  // Xorshift*32
  // By George Marsaglia: http://www.jstatsoft.org/v08/i14/paper
  value ^= value << 13;
  value ^= value >> 17;
  value ^= value << 5;
  return value;
}

