#define FOV 60.
#define shutAp .05
#define blurAmt .001
#define fcsDist 3.
const vec3 rightVec = vec3(1.,0.,0.),
upVec = vec3(0.,1.,0.),
outVec = vec3(0.,0.,1.);

#define skyBrightness 1.

#define numLvls 8
#define spp 1.

const float dv = 1./tan(d2r(FOV)*.5);

float sphDist(vec3 p,float r){
    return length(p)-r;
}

float boxDist(vec3 p,vec3 b){
    vec3 q = abs(p)-b;
    return length(max(q,0.))+min(max(q.x,max(q.y,q.z)),0.);
}

#define fractalLvls 4
float map(vec3 p,out vec3 col,out float r) {
    col = vec3(1.);
    r = .5;
    vec3 q = vec3(0.,0.,3.5);
    float d = sphDist(p-q,2.);
    return d;
}

vec3 getNor(vec3 p){
    vec2 k = vec2(1.,-1.);
    vec4 a;
    //float d0 = map(p);
    float h = c_EpsSmall;
    return normalize(
        k.xyy*map(p+k.xyy*h,a.xyz,a.w)+
        k.yyx*map(p+k.yyx*h,a.xyz,a.w)+
        k.yxy*map(p+k.yxy*h,a.xyz,a.w)+
        k.xxx*map(p+k.xxx*h,a.xyz,a.w)
        );
}

Hit worldInt(vec3 ro,vec3 rd,World objs) {
    Hit h;
    h.len = -1.;
    h.type = -1;

    vec3 col;
    float r;
    float dT = 0.;
    for(int i = 0;i<300;i++){
        vec3 p = dT*rd+ro;
        float d1 = map(p,col,r);
        dT+=abs(d1);
        if(abs(d1)<c_SurfSmall&&dT>3.*c_SurfSmall) {h.len = dT; break;}
        if(dT>10.) break;
    }

    h.nor = getNor(h.len*rd+ro);

    h.mat = MatPBR(col,vec3(0.),r,0.);

    return h;
}

vec3 getSky(vec3 rd,float t) {
    return skyBrightness*SRGBToLinear(texture(iChannel1,rd).xyz);
    //return vec3(.3,.625,.95);
}

vec3 scatter(out vec3 ro, inout vec3 rd, MatPBR m,vec3 p,in vec3 n){
    ro = p;
    float r = m.roughness, met = m.metalness;

    vec3 N = HSampleGGX(n,r*r);

    rd = reflect(rd,N);

    #if 0
    vec3 F0 = mix(vec3(.04),m.albedo,met);
    vec3 F = fresnelSchlick(md(N,-rd),F0);
    return mix(m.albedo,,F);
    #else
    return m.albedo;
    #endif
}

Sphere new(vec3 p,float rad,vec3 alb,float rog,vec3 ems,float met) {
    return Sphere(p,rad,MatPBR(alb,ems,rog,met));
}

void mainImage( out vec4 o, in vec2 i ) {
    o = texelFetch(iChannel0,ivec2(i),0);
    float framespp = float(iFrame)*spp;

    vec3 ttcol = vec3(0.);

    for(float j = 0.; j < spp; j++) {
        initR(i,framespp + j);

        // 1 for circular AA
        #if 0
        vec2 u = rand2();
        u.y *= 2.*c_PI;
        vec2 jitter = .8*sqrt(u.x)*vec2(cos(u.y),sin(u.y));

        vec2 pixT = (-iR+ 2.*(i + jitter)) / iR.y;
        #else
        vec2 jitter = 1.5*rand2()-.75;

        vec2 pixT = (-iR+ 2.*(i + jitter)) / iR.y;
        #endif
        vec3 er = normalize(vec3(pixT,dv));

        float ctime = iTime - rand()*shutAp;

        // Objects

        Sphere sphs[c_w_sphs] = Sphere[c_w_sphs](
        new(vec3(0.,0.,3.5),1.,vec3(1.,.8,.5),.8,vec3(0.),1.),
        new(vec3(0.,-101.,0.),100.,vec3(1.),.3,vec3(0.),0.),
        new(vec3(0.,2.5,3.),.75,vec3(.5),.75,vec3(1.),0.)
        );

        // Automatic code continues
        World W;
        W.sphs = sphs;

        vec3 rd = er.x*rightVec+ er.y*upVec + er.z*outVec;
        vec3 ro = vec3(0.);

        vec3 go = blurAmt*vec3(2.*rand2()-1.,0.);
        vec3 gd = normalize(er*fcsDist - go);
        rd = normalize(rd + gd.x*rightVec + gd.y*upVec);
        ro += go.x*rightVec + go.y*upVec;

        vec3 col = vec3(0.), mask = vec3(1.), oldNor = rd;

        for(int k = 0; k < numLvls; k++){
            // Intersection
            Hit inter = worldInt(ro,rd,W);

            // Sky
            if(inter.len == -1.) {col+=getSky(rd,ctime)*mask;break;}

            // Data Retrieval
            MatPBR mat = inter.mat;
            vec3 p = inter.len*rd+ro;


            // Coloring, masking, and scattering
            col += mat.ems*mask;
            mask *= scatter(ro,rd,mat,p,inter.nor);
        }

        ttcol += col;
    }

    if(ttcol.x!=ttcol.x) ttcol.x = 0.;
    if(ttcol.y!=ttcol.y) ttcol.y = 0.;
    if(ttcol.z!=ttcol.z) ttcol.z = 0.;
    o += vec4(ttcol / spp,1.);
}

