vec3 LessThan(vec3 f, float val) {
    return vec3(
        (f.x < val) ? 1. : 0.,
        (f.y < val) ? 1. : 0.,
        (f.z < val) ? 1. : 0.);
}

vec3 linearToSRGB(vec3 rgb) {
    rgb = clamp(rgb, 0., 1.);

    return mix(
        pow(rgb, vec3(1. / 2.4)) * 1.055 - .055,
        rgb * 12.92,
        LessThan(rgb, .0031308)
    );
}

vec3 SRGBToLinear(vec3 rgb) {
    rgb = clamp(rgb, 0., 1.);

    return mix(
        pow(((rgb + .055) / 1.055), vec3(2.4)),
        rgb / 12.92,
        LessThan(rgb, 0.04045)
    );
}

vec3 ACESFilm(vec3 x) {
    float a = 2.51;
    float b = .03;
    float c = 2.43;
    float d = .59;
    float e = .14;
    return clamp((x*(a*x + b)) / (x*(c*x + d) + e), 0., 1.);
}

float sphInt(in vec3 ro,vec3 rd,Sphere sph,out bool ff){
    ro -= sph.pos;
    float b = dot( ro, rd );
    float c = dot( ro, ro ) - sph.rad*sph.rad;
    float h = b*b - c;
    if( h<0. ) return -1.;
    h = sqrt( h );
    if(length(ro)<=sph.rad){
        ff = false;
        return -b+h;
    }else{
        ff = true;
        return -b-h;
    }
}

vec3 orient(vec3 S,vec3 N){
    vec3 up = abs(N.z) < .999 ? vec3(0.,0.,1.) : vec3(1.,0.,0.);
    vec3 tangent = normalize(cross(up,N));
    vec3 bitangent = cross(N,tangent);
    return tangent*S.x + bitangent*S.y + N*S.z;
}

// Generated bu ChatGPT
vec3 HSampleGGX(vec3 n,float r){
    float a2 = r*r;
    float phi = 2.*c_PI*rand();
    float ry = rand();
    float cosTheta = sqrt((1.-ry)/(1.+(a2*a2-1.)*ry));
    float sinTheta = sqrt(1.-cosTheta*cosTheta);

    vec3 h = vec3(sinTheta*cos(phi),sinTheta*sin(phi),cosTheta);
    return orient(h,n);
}


float distributionGGX(vec3 N, vec3 H, float r) {
    float a2 = r*r*r*r;
    float NdotH  = md(N,H);
    float NdotH2 = NdotH*NdotH;

    float denom = (NdotH2*(a2-1.) + 1.);
    denom *= c_PI*denom;

    return a2/denom;
}

float geometrySchlickGGX(float NdotV, float r0) {
    float r1 = r0 + 1.;
    float k = r1*r1 / 8.;

    float denom = NdotV*(1. - k) + k;

    return NdotV / denom;
}
float geometrySmith(vec3 N, vec3 V, vec3 L, float r){
    float NdotV = md(N,V);
    float NdotL = md(N,L);
    float ggx2  = geometrySchlickGGX(NdotV,r);
    float ggx1  = geometrySchlickGGX(NdotL,r);

    return ggx1*ggx2;
}

vec3 fresnelSchlick(float cosTheta, vec3 F0) {
    return F0 + (1. - F0)*pow(clamp(1.- cosTheta,0.,1.),5.);
}  


vec3 cosineWeighted(vec3 N) {
    float u1 = rand(), u2 = rand();
    float r = sqrt(u1),
    theta = 2.*c_PI*u2;

    float x = r*cos(theta), y = r*sin(theta),
    z = sqrt(max(0.,1.-x*x-y*y));

    return orient(vec3(x,y,z),N);
}

