void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv.y += (iTime/2.0,iTime/3.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec2 t2 = vec2(0.);
    bool b = true;
    vec2 uv1 = uv;
    //uv.x = mod(uv.x,.5)+.25;
    uv.x = mod(uv.x,.5);
    uv.y = mod(uv.y,1.)+1.;
    for(int k = 0;k<100; k++){
        uv =
            abs(-.5-uv-t2*sign(uv.y-uv.x))/1.5-1.
        ;
        b = uv.y<uv.x;
        if(b) uv = uv.yx;
        t2 =
            triangle_wave(uv-.5).yx
        ;
        uv =
            t2+triangle_wave(uv.yx).yx
        ;
        if(uv.y>uv.x||!b)
        //{t2 = t2.yx; uv=uv.yx;}
        {
        col.x =
            abs((uv.x)+col.x)
        ;
        col.xyz =
            abs(col.yzx-float(b)/2.)
        ;
        }
        else break;
    }
    if (!b) col = 1.-col;
    fragColor = vec4(min(col,vec3(1.)),1.0);
}

