#define fmod(x,y) floor(max(mod(floor((x/2.))*floor(x/2.),y),mod(floor(x/2.),y)))
#define fmod1(x,y) floor(min(mod(floor((x/2.))*floor(x/2.),y),mod(floor(x/2.),y)))
vec2 mainSound(int samp, float time){  
  float tempo = .5;
  time /= tempo;
  float s2 = pow(2.,1.+fmod1(time/4.,3.)),
  s1 = pow(2.,fmod(time*s2*2.,3.)),
  m1 = s1*(1.+fmod(time/s2/s1*2.+1.,2.)),
  t = time/s1/pow(2.,fmod1(time/5./8.,5.))*2.,
  t1 = 1.+fmod(t/s1,2.),
  m4 = fmod1(t*s1/t1,s2),
  m3 = fmod(t*s1/m1,s2);
  t *= s1/t1;
  float m2 = 1. + fmod(t,s1),
  a = 8.*fract(-t),
  nb = time*tempo*pow(2.,(m3+m2)/5.+7.5);
  return abs(abs(1./s1-1./s2)/8.-abs(vec2(sin(nb),cos(nb))))*a;
}

