const float shapeSize = 0.10;
const float shapeCount = 3.00;
const float colorSpeed = 0.90;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    float aspectRatio = iResolution.x / iResolution.y;
    uv.x *= aspectRatio;
    uv = 2.0 * (uv - 0.5);

    float time = iTime * colorSpeed;
    float shapeDist = 0.0;
    for (float i = 0.0; i < shapeCount; i++) {
        float angle = 2.0 * 3.14159265 * (i / shapeCount);
        vec2 shapePos = vec2(cos(angle), sin(angle));
        shapeDist += shapeSize / length(uv - shapePos);
    }

    vec3 color = vec3(sin(time), sin(time + 2.0 * 3.14159265 / 3.0), sin(time + 4.0 * 3.14159265 / 3.0));
    fragColor = vec4(shapeDist * color, 1.0);
}

const float speed = 0.42;
const float thickness = 1.00;
const float radius = 1.00;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    float angle = atan(uv.y, uv.x);
    float dist = length(uv);
    float time = iTime * speed;

    float spinner = smoothstep(thickness, -thickness, abs(mod(angle + time, 2.0 * 3.14159 / 8.0) - 3.14159 / 8.0));
    float circle = smoothstep(radius + thickness, radius - thickness, dist);

