}

// ---------------------------------------------------------------------------------------
//	Created by fenix in 2023
//	License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
//  Rigid body dice colliding with the ground (not each other [yet!]). An ode to games
//  and chance. Featuring code from:
//
//  The inspiration for this shader, fizzer's excellent
//
//      Simple and Cheap Dice SDF:      https://shadertoy.com/view/csySDG
//
//  wnu's beautiful
//
//      sparkle commented:              https://shadertoy.com/view/Ndlczs
// 
//  Card suit SDFs were made using my own tool:
//
//      Polygon SDF Modelling Tool:     https://shadertoy.com/view/dtBSDw
//
//  Dipping my toes into rigid body physics simulation. Contact constraints are resolved
//  via an iterative accumulation solver, and feature warm starting, restitution,
//  friction, and position correction.
//
//  Dont's miss: mouse can grab and drag/throw dice!
//
//  The rendering is a fairly typical voronoi system, from both the camera and light
//  source perspectives (the latter, for shadows). The shadows were somewhat tricky to
//  get lined up correctly, and once I did I found a perspective projection was causing
//  large shadows to be cast by dice passing close to the light source. So I switched to
//  an orthographic projection and had to line the shadows up again. :P
//
//  Continuing with the theme of "chance", I figured there was a chance users might not
//  notice, at least right away, that the dice don't collide with each other. :)
//
//  I do intend to keep going on research in this direction, and getting the bodies to
//  collide against each other is a top priority. I think I just need to figure out some
//  clever way to address the contact impulses for body pairs. If anyone know of any
//  papers, shaders, or other prior art I would be very interested.
//
//  I have attempted to write clear code and detailed comments. Please let me know if I
//  should explain anything in more detail.
//
// ---------------------------------------------------------------------------------------

#define MSAA 1 // 1 to disable (for more speed), 2 for 2x2, 3 for 3x3, etc.

// from fizzer's Simple and Cheap Dice SDF: https://shadertoy.com/view/csySDG
// Distance to hemisphere-shaped hole in a plane.
float hole(vec3 p, float r)
{
    float c = min(0., length(p.xz) - r);
    return (p.y > 0.) ? length(vec2(c, p.y)) : r - length(p);
}

// Die SDF.
float sdDie(vec3 p)
{
    // Get index of closest face on die, and local point b.
    vec3 q = abs(p);

    bvec3 dots = sign(p.z) > 0. ? bvec3(false, true, true) : bvec3(true, true, false);
    vec3 b = p.xzy * sign(p.z);

    if(q.x > q.y && q.x > q.z)
    {
        b = p.zxy * sign(p.x);
        b.z = -b.z;
        dots = sign(p.x) > 0. ? bvec3(false, true, false) : bvec3(true, true, true);
    }
    else if(q.y > q.z)
    {
        b = p.zyx * sign(p.y);
        dots = sign(p.y) > 0. ? bvec3(false, false, true) : bvec3(false, false, false);
    }

    // Some parameteric constants.
    float r = .02, r2 = .04 ,r3 = .13;

    b.y -= .5 - r;

    float d;

    if(all(not(dots)))
    {
        // Special case for side 6.
        b.x = abs(b.x);
        b.z = mod(b.z + .3 / 2., .3) - .3 / 2.;
        d = hole(b - vec3(.2, 0, 0), r3);
    }
    else
    {
        // The sides other than 6 have reflective symmetry in two lines.
        if(b.x > -b.z)
            b.xz = -b.zx;

        if(b.x > b.z)
            b.xz = b.zx;

        // Thanks to this symmetry, the presence of only three dots account
        // for all of the sides. So the effective LUT contains 3 bits per face.
        if(b.z - b.x > .25)
            d = dots.y ? hole(b - vec3(-1, 0, +1) / 4., r3) : b.y;
        else if(-b.x - b.z > .25)
            d = dots.x ? hole(b - vec3(-1, 0, -1) / 4., r3) : b.y;
        else
            d = dots.z ? hole(b, r3): b.y;
    }

    // Use sdRoundBox to bevel the outer edges of the die.
    return max(d - r, sdRoundBox(p, vec3(.5) - r2, r2));
}

// transform point from world space to body space
vec3 bodyTrans(fxRigidBody b, vec3 p)
{
    return rotateVectorByQuat(p - b.pos, b.rot);
}

float sdBody(fxRigidBody b, vec3 p)
{
    p = bodyTrans(b, p);
    return sdDie(p);
}

vec3 normBody(fxRigidBody b, vec3 p)
{
    float d = sdBody(b, p);
    vec3 n = vec3(0.0);
    for( int i=min(iFrame,0); i<4; i++ )
    {
        float d2d;
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*sdBody(b,p+0.000001*e);
    }
    return normalize(n);
}

vec3 dieColor(fxRigidBody b, vec3 p)
{
    p = bodyTrans(b, p);
    return mix(vec3(0), vec3(1), smoothstep(.01, .02, sdRoundBox(p, vec3(.38), .1)));
}

float shadow(mat4 invMat, vec3 pos, vec3 lightPos, vec3 lightLeft, vec3 lightUp, int exception)
{
    vec3 sp = (invMat * vec4(pos, 1)).xyz;
    sp.xy /= SHADOW_RES;
    lightPos -= (lightLeft * sp.x + lightUp * sp.y) * SHADOW_RES;

    vec2 r = iResolution.xy;
    ivec2 coord = ivec2((sp.xy * vec2(r.y / r.x, 1) + .5) * r.xy);
    if (coord.x < 0 || coord.y < 0 || coord.x >= int(iResolution.x) || coord.y >= int(iResolution.y)) return 1.;

    ivec4 old = fxGetClosestLight(coord);
    float maxS = 0.;
    for (int i = 0; i < 4; ++i)
    {
        int id = old[i];
        if (id < 0) break;
        if (id == exception) continue;

        vec3 bodyPos = fxGetBodyData(id, POS).xyz;
        if (dot(bodyPos - pos, LIGHT_DIR) < 0.) continue;

        vec4 bodyRot = fxGetBodyData(id, ROT);
        vec3 localDir = rotateVectorByQuat(-LIGHT_DIR, bodyRot);
        vec3 localOrigin = rotateVectorByQuat(lightPos - bodyPos, bodyRot);
        float t = boxIntersection(localOrigin, localDir, vec3(.5));

        if (t >= 0.)
        {
            maxS = max(maxS, smoothstep(8., 4., bodyPos.y)); // blend in shadows only near the ground
        }
    }

    return 1. - maxS;
}

void renderDie(int id, vec3 ro, vec3 rd, vec3 lightPos, vec3 lightLeft, vec3 lightUp, inout vec3 col, inout float maxT, mat4 invMat)
{
    fxRigidBody b = fxGetBody(id);
    float t = 0.;
    for(int i = 0; i < 100; ++i)
    {
        float d = sdBody(b, ro + rd * t);
        if(abs(d) < 1e-4 || t > FAR_CLIP)
            break;
        t += d;
    }

    vec3 rp = ro + rd * t;

    if(t < maxT)
    {
        maxT = t;

        vec3 n = normBody(b, rp);
        vec3 baseColor = dieColor(b, rp);
        vec3 rr = reflect(rd, n);
        vec3 spec = vec3(pow(dot(rr, LIGHT_DIR), 8.));
        vec3 diff = vec3(0.5 + 0.5 * dot(n, LIGHT_DIR)) + vec3(0.5 + 0.5 * -n.y) * vec3(.1, .1, .2) * 1.5;
        float fresnel = mix(0.01, .4, pow(clamp(1. + dot(rd, n), 0., 1.), 2.));

        col = mix(baseColor * diff, spec, fresnel);

        col *= shadow(invMat, rp, lightPos, lightLeft, lightUp, id) * .6 + .4;
    }
}

