// https://iquilezles.org/articles/distfunctions2d/
#define DEFINE_POLYGON(X) \
float sdPolygon(int num, vec2 verts[X], vec2 p) \
{ \
    float d = dot(p-verts[0],p-verts[0]); \
    float s = 1.0; \
    for( int i=0, j=num-1; i<num; j=i, i++ ) \
    { \
        vec2 e = verts[j] - verts[i]; \
        vec2 w =    p - verts[i]; \
        vec2 b = w - e*clamp( dot(w,e)/dot(e,e), 0.0, 1.0 ); \
        d = min( d, dot(b,b) ); \
        bvec3 cond = bvec3( p.y>=verts[i].y, \
                            p.y <verts[j].y, \
                            e.x*w.y>e.y*w.x ); \
        if( all(cond) || all(not(cond)) ) s=-s; \
    } \
    return s*sqrt(d); \
}

DEFINE_POLYGON(NUM_SPADE_VERTS)
DEFINE_POLYGON(NUM_TAIL_VERTS)
DEFINE_POLYGON(NUM_DIAMOND_VERTS)
DEFINE_POLYGON(NUM_HEART_VERTS)

float sdSpade(vec2 p)
{
    p.x = abs(p.x);
    return min(sdPolygon(NUM_SPADE_VERTS, SPADE_VERTS, p), sdPolygon(NUM_TAIL_VERTS, TAIL_VERTS, p));
}

float sdClub(vec2 p)
{
    p.x = abs(p.x);
    float d = sdPolygon(NUM_TAIL_VERTS, TAIL_VERTS, p);
    d = min(d, distance(p, vec2(0, .28)) - .18);
    d = min(d, distance(p, vec2(.15, 0)) - .18);
    d = min(d, distance(p, vec2(-.15, 0)) - .18);
    return d;
}

float sdDiamond(vec2 p)
{
    p.x = abs(p.x);
    p.y *= .9;
    return sdPolygon(NUM_DIAMOND_VERTS, DIAMOND_VERTS, p);
}

float sdHeart(vec2 p)
{
    p.x = abs(p.x) * 1.5;
    return sdPolygon(NUM_HEART_VERTS, HEART_VERTS, p);
}

float sdGround(vec2 p)
{
    ivec2 id = ivec2(mod(p, 4.));
    p = (fract(p) - .5) * vec2(1, 1);

    if (id.y == 0)
    {
        if (id.x == 0) return sdHeart(p);
        if (id.x == 2) return sdClub(p);
        return sdDiamond(p.yx * vec2(6, 1));
    }
    else if (id.y == 2)
    {
        if (id.x == 0) return sdSpade(p);
        if (id.x == 2) return sdDiamond(p);
        return sdDiamond(p.yx * vec2(6, 1));
    }
    if (id.x == 0 || id.x == 2) return sdDiamond(p * vec2(6, 1));;
    return 1e6;
}

vec2 noise( in vec3 x )
{
    vec3 ip = floor(x);
    vec3 fp = fract(x);
	fp = fp*fp*(3.0-2.0*fp);
	vec2 tap = (ip.xy+vec2(37.0,17.0)*ip.z) + fp.xy;
	vec4 rz = textureLod( iChannel3, (tap+0.5)/256.0, 0.0 );
	return mix( rz.yw, rz.xz, fp.z );
}

// from sparkle commented by wnu: https://www.shadertoy.com/view/Ndlczs
vec3 glitter(vec3 rayHit, vec3 normal, vec3 camDir, float sh)
{
    float t = iTime;
    vec3 n = normal;

    /////BASE COLOR

    //FRESNEL 

    float fre = clamp(1. + dot(camDir,normal),-1.,1.);
    float edge = sqrt(1.2-pow(fre,1./4.2));

    //PICK YOUR COLOR

    vec3 colorPick = vec3(1);

    //COLOR FROM NORMALS AND FRESNEL

    vec3 c = 2.25*-sin(sin(t*0. + n.x*1.+ 1.57 + sin(n.z*1. + 0.76)) + n.z + t*0.+  colorPick);
    float ss = min(.5 - edge, smoothstep(.45 - edge, 1., edge));
    c *= ss;

    ///////GLINTS/GLITTER COMPUTATION

    #define PRIMARY_INTENSITY 1.3
    #define PRIMARY_CONCENTRATION 2.
    #define SECONDARY_INTENSITY 8.
    #define SECONDARY_CONCENTRATION 1.

    // HOW BIG THE GLITTER SPECKS ARE ; BIGGER -> SMALLER SPECKS
    // (7 is probably the smallest I would go)

    float scale = 20.;
    rayHit = floor(rayHit*scale)/scale;

    vec3 h = normalize(LIGHT_DIR-camDir);
    float nl = dot(n,LIGHT_DIR);

    vec3 coord = rayHit*.5;
    coord.xy = coord.xy*.7071+coord.yx*.7071*vec2(1,-1);
    coord.xz = coord.xz*.7071+coord.zx*.7071*vec2(1,-1);
    vec3 coord2 = coord;

    //first layer (inner glints)
    float pw = .5*((iResolution.x));
    vec3 aniso = vec3( noise((coord*pw)), noise((coord.yzx*pw)) )*2.0-1.0;
    aniso -= n*dot(aniso,n);
    float anisotropy = min(1.,length(aniso));
    aniso /= anisotropy;
    anisotropy = .55;
    float ah = abs(dot(h,aniso));
    float nh = abs(dot(n,h));
    float qa = exp2((1.1-anisotropy)*1.5);
    nh = pow( nh, qa*PRIMARY_CONCENTRATION );
    nh *= pow( 1.-ah*anisotropy, 10.0 );
    vec3 glints = c*nh*exp2((1.2-anisotropy)*PRIMARY_INTENSITY);

    //second layer (outer glints)
    pw = .145*((iResolution.x));
    vec3 aniso2 = vec3( noise(coord2*pw), noise(coord2.yzx*pw).x )*2.0-1.0;
    anisotropy = .6;
    float ah2 = abs(dot(h,aniso2));
    float nh2 = abs(dot(n,h));
    float q2 = exp2((.1-anisotropy)*3.5);
    nh2 = pow( nh, q2*SECONDARY_CONCENTRATION );
    nh2 *= pow( 1.-ah2*anisotropy, 150.0 );
    vec3 glints2 = c*nh2*((1.-anisotropy)*SECONDARY_INTENSITY);

    ///// ADD BASE COLOR AND GLINTS/GLITTER LAYERS   

    vec3 col = vec3(c)/4.;
    col += (glints+glints2) * .5 * sh + .3;

    return col*col;
}

// From https://knarkowicz.wordpress.com/2016/01/06/aces-filmic-tone-mapping-curve/
vec3 ACESFilm(vec3 x)
{
    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    return clamp((x*(a*x+b))/(x*(c*x+d)+e), 0., 1.);
}

vec3 renderScene(vec2 fragCoord)
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;
    fxState state = fxGetState();

    vec3 cameraLookAt, cameraPos, cameraFwd, cameraLeft, cameraUp;
    fxCameraPath(cameraPos, cameraLookAt, iTime);
    fxCalcCamera(cameraPos, cameraLookAt, cameraFwd, cameraLeft, cameraUp);
    vec3 rayDir = fxCalcRay(fragCoord, iResolution, cameraFwd, cameraUp, cameraLeft);

    vec3 lightFwd, lightLeft, lightUp, lightPos, lightLookAt;
    fxLightPath(cameraLookAt, cameraFwd, lightPos, lightLookAt);
    fxCalcCamera(lightPos, lightLookAt, lightFwd, lightLeft, lightUp);

    mat4 lightMat = fxCalcCameraMat(iResolution, lightLeft, lightUp, lightFwd, lightPos);
    mat4 invMat = inverse(lightMat);

    float maxT = FAR_CLIP;
    vec3 col = vec3(0);

    // render dice
#if 1 // disable to skip voronoi and render all bodies (slow, ground truth)
    ivec2 ifc = ivec2(fragCoord);
    ivec4 old = fxGetClosestCamera(ifc);      

    for (int i = 0; i < 4; ++i)
    {
        int id = old[i];
        if (id < 0) break;
#else
    for (int id = 0; id < NUM_BODIES; ++id)
    {
#endif
        renderDie(id, cameraPos, rayDir, lightPos, lightLeft, lightUp, col, maxT, invMat); 
    }

    // render ground if we missed all the dice
    if (maxT == FAR_CLIP)
    {
        float t = -cameraPos.y / rayDir.y;
        vec3 hit = cameraPos + rayDir * t;
        float d = sdGround(hit.xz * .5);
        const vec3 GREEN = vec3(.05, .2, .05);
        float sh = shadow(invMat, hit, lightPos, lightLeft, lightUp, -1);
        const vec3 GOLD = vec3(1, .9, .1);
        if (d <= .01)
        {
            vec3 g = glitter(hit, vec3(0, 1, 0), rayDir, sh) * GOLD;
            col = mix(g, GREEN, smoothstep(0., .01, d));
        }
        else
        {
            col = GREEN;
        }

        col *= sh * .6 + .4;
    }

    // render small blue sphere at grab point, if any
    if (state.grabId >= 0)
    {
        fxRigidBody b = fxGetBody(state.grabId);

        vec3 grabPoint = rotateVectorByQuat(state.localGrabPos, quatInverse(b.rot)) + b.pos;
        float i1 = sphIntersect(cameraPos, rayDir, vec4(grabPoint, .1));
        if (i1 > 0. && i1 < maxT)
        {
            col = vec3(0, 0, 1);
        }
    }

    return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord)
{
#if MSAA <= 1
    vec3 col = renderScene(fragCoord);
#else
    vec3 col = vec3(0);

    for (int x = 0; x < MSAA; ++x)
    for (int y = 0; y < MSAA; ++y)
    {
        vec2 offset = (vec2(x, y) / float(MSAA - 1)) - .5;
        col += renderScene(fragCoord + offset);
    }

    col /= float(MSAA * MSAA);
#endif

    fragColor.xyz = pow(ACESFilm(col), vec3(1./2.2));
    fragColor.a = 1.;
}

