const float FAR_CLIP = 100.;                       // don't render beyond this t value
const vec3 LIGHT_DIR = normalize(vec3(1, 1, 1.5)); // fixed light direction (sunlight)
const float SHADOW_RES = 40.;                      // resolution/coverage of shadow buffer

// PERSISTENT STATE

struct fxState
{
    float resolution;  // res.x * res.y to detect changes, negative for one frame when change is detected
    int grabId;        // which body we're grabbing with the mouse
    float grabDist;    // how far away the grab point is from the camera
    vec3 localGrabPos; // where on the grabbed body we're grabbing
};

// retrieve persistent state information
fxState fxGetStateInternal(sampler2D sampler)
{
    vec4 data0 = texelFetch(sampler, ivec2(0), 0);
    vec4 data1 = texelFetch(sampler, ivec2(1, 0), 0);
    fxState state;
    state.resolution = data0.x;
    state.localGrabPos = data1.xyz;
    state.grabId = int(data0.y);
    state.grabDist = data1.w;
    return state;
}

#define fxGetState() fxGetStateInternal(iChannel0)

bool fxIsStatePixel(ivec2 ifc)
{
    return ifc == ivec2(0, 0) || ifc == ivec2(1, 0);
}

const int NUM_STATE_PIXELS = 2;

// store persistent state information
vec4 fxPutState(fxState state, ivec2 ifc)
{
    if (ifc == ivec2(0, 0))
        return vec4(state.resolution, state.grabId, 0, 0);
    else
        return vec4(state.localGrabPos, state.grabDist);
}

// RIGID BODIES

const int NUM_BODIES = 300; // only uses a small part of the buffer for this shader

struct fxRigidBody
{
    vec3 pos;         // world space CG position
    vec4 rot;         // rotation expressed as quaternion
    vec3 vel;         // linear velocity
    vec3 angVel;      // angular velocity
    vec3 impulses[8]; // retained impulses per contact (one per box corner); x - normal impulse, yz - friction impulse
};

#define POS 0
#define ROT 1
#define VEL 2
#define ANG_VEL 3
#define IMPULSES00 5
#define IMPULSES01 6
#define IMPULSES02 7
#define IMPULSES03 8
#define IMPULSES04 9
#define IMPULSES05 10
#define IMPULSES06 11
#define IMPULSES07 12
#define NUM_DATA_TYPES 13

//returns the location of the body within the body buffer corresponding with the input id 
ivec2 fxLocFromIDImpl(int width, int id, int dataType)
{
    int index = id * NUM_DATA_TYPES + dataType + NUM_STATE_PIXELS;
    return ivec2(index % width, index / width);
}

#define fxLocFromID(X, Y) fxLocFromIDImpl(int(iResolution.x), X, Y)

//get the body corresponding to the input id
fxRigidBody fxGetBodyImpl(sampler2D sampler, int resolutionWidth, int id)
{
    vec4 bodyData0 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, POS), 0);
    vec4 bodyData1 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, ROT), 0);
    vec4 bodyData2 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, VEL), 0);
    vec4 bodyData3 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, ANG_VEL), 0);
    vec4 bodyData4 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, IMPULSES00), 0);
    vec4 bodyData5 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, IMPULSES01), 0);
    vec4 bodyData6 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, IMPULSES02), 0);
    vec4 bodyData7 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, IMPULSES03), 0);
    vec4 bodyData8 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, IMPULSES04), 0);
    vec4 bodyData9 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, IMPULSES05), 0);
    vec4 bodyData10 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, IMPULSES06), 0);
    vec4 bodyData11 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, IMPULSES07), 0);

    fxRigidBody body;
    body.pos = bodyData0.xyz;
    body.rot = bodyData1;
    body.vel = bodyData2.xyz;
    body.angVel = bodyData3.xyz;
    body.impulses[0] = bodyData4.xyz;
    body.impulses[1] = bodyData5.xyz;
    body.impulses[2] = bodyData6.xyz;
    body.impulses[3] = bodyData7.xyz;
    body.impulses[4] = bodyData8.xyz;
    body.impulses[5] = bodyData9.xyz;
    body.impulses[6] = bodyData10.xyz;
    body.impulses[7] = bodyData11.xyz;

    return body;
}

#define fxGetBody(X) fxGetBodyImpl(iChannel0, int(iResolution.x), X)

// write out the correct data member to fragColor
vec4 fxSaveBody(fxRigidBody b, int dataType)
{    
    switch(dataType)
    {
    case POS:
        return vec4(b.pos, 0);
    case ROT:
        return vec4(b.rot);
    case VEL:
        return vec4(b.vel, 0);
    case ANG_VEL:
        return vec4(b.angVel, 0);
    case IMPULSES00:
        return vec4(b.impulses[0], 0);
    case IMPULSES01:
        return vec4(b.impulses[1], 0);
    case IMPULSES02:
        return vec4(b.impulses[2], 0);
    case IMPULSES03:
        return vec4(b.impulses[3], 0);
    case IMPULSES04:
        return vec4(b.impulses[4], 0);
    case IMPULSES05:
        return vec4(b.impulses[5], 0);
    case IMPULSES06:
        return vec4(b.impulses[6], 0);
    case IMPULSES07:
        return vec4(b.impulses[7], 0);
    }
}

// get just one body member (untyped)
vec4 fxGetBodyDataImpl(sampler2D sampler, int resolutionWidth, int id, int dataType)
{
    return texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, dataType), 0);
}

#define fxGetBodyData(X, Y) fxGetBodyDataImpl(iChannel0, int(iResolution.x), X, Y)

// returns the ids of the four closest bodies from the input coords
ivec4 fxGetClosestImpl(sampler2D sampler, ivec2 xy)
{
    return ivec4(texelFetch(sampler, xy, 0));
}

#define fxGetClosestCamera(X) fxGetClosestImpl(iChannel1, X)
#define fxGetClosestLight(X) fxGetClosestImpl(iChannel2, X)

// UTILITY

const float PI = 3.141592653589793;
const vec4 QUAT_IDENT = vec4(0, 0, 0, 1);

float length2(vec2 v) { return dot(v, v); }

// rotate vector by quaternion
vec3 rotateVectorByQuat(vec3 v, vec4 q)
{
    vec3 u = q.xyz;
    float s = q.w;

    return 2. * dot(u, v) * u
          + (s*s - dot(u, u)) * v
          + 2. * s * cross(u, v);
}

// multiply quaternions
vec4 quatMult(vec4 Q1, vec4 Q2)
{
   return vec4( 
       Q2.x*Q1.w + Q2.y*Q1.z - Q2.z*Q1.y + Q2.w*Q1.x,
      -Q2.x*Q1.z + Q2.y*Q1.w + Q2.z*Q1.x + Q2.w*Q1.y,
       Q2.x*Q1.y - Q2.y*Q1.x + Q2.z*Q1.w + Q2.w*Q1.z,
      -Q2.x*Q1.x - Q2.y*Q1.y - Q2.z*Q1.z + Q2.w*Q1.w
   );
}

// construct quaternion from euler angles
vec4 quatFromRot(vec3 rot)
{
    float cr = cos(rot.x * 0.5);
    float sr = sin(rot.x * 0.5);
    float cp = cos(rot.y * 0.5);
    float sp = sin(rot.y * 0.5);
    float cy = cos(rot.z * 0.5);
    float sy = sin(rot.z * 0.5);

    vec4 q;
    q.w = cr * cp * cy + sr * sp * sy;
    q.x = sr * cp * cy - cr * sp * sy;
    q.y = cr * sp * cy + sr * cp * sy;
    q.z = cr * cp * sy - sr * sp * cy;

    return q;
}

// quaternion inverse
vec4 quatInverse(vec4 q)
{
    return vec4(-q.xyz, q.w);
}

// compute matrix from quaternion
mat3 matFromQuat(vec4 Q)
{
    float q0 = Q[0];
    float q1 = Q[1];
    float q2 = Q[2];
    float q3 = Q[3];

    float r00 = 2. * (q0 * q0 + q1 * q1) - 1.;
    float r01 = 2. * (q1 * q2 - q0 * q3);
    float r02 = 2. * (q1 * q3 + q0 * q2);

    float r10 = 2. * (q1 * q2 + q0 * q3);
    float r11 = 2. * (q0 * q0 + q2 * q2) - 1.;
    float r12 = 2. * (q2 * q3 - q0 * q1);

    float r20 = 2. * (q1 * q3 - q0 * q2);
    float r21 = 2. * (q2 * q3 + q0 * q1);
    float r22 = 2. * (q0 * q0 + q3 * q3) - 1.;

    return mat3(r00, r01, r02,
                r10, r11, r12,
                r20, r21, r22);
}

// https://iquilezles.org/articles/spherefunctions/
float sphIntersect( in vec3 ro, in vec3 rd, in vec4 sph )
{
	vec3 oc = ro - sph.xyz;
	float b = dot( oc, rd );
	float c = dot( oc, oc ) - sph.w*sph.w;
	float h = b*b - c;
	if( h<0.0 ) return -1.0;
	return -b - sqrt( h );
}

// https://iquilezles.org/articles/boxfunctions/
float boxIntersection( in vec3 ro, in vec3 rd, in vec3 rad) 
{
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*rad;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );

    if( tN>tF || tF<0.0) return -1.; // no intersection

    return tN;
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

uvec4 hash(uvec4 x){
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    //x = (x >> 16u) ^ x;
    return x;
}

vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

// CAMERA

// camera animation based on time
void fxCameraPath(out vec3 cameraPos, out vec3 cameraLookAt, float t)
{
    t += 200.;
    cameraPos = vec3(200. * sin(t * .02), 16, 200. * cos(t * .02));
    cameraLookAt = vec3(14. * cos(t * .02), -16, -14. * sin(t * .02)) + cameraPos;
}

// light animation based on time (light position determines the center of focus for shadow voronoi buffer)
void fxLightPath(vec3 cameraLookAt, vec3 cameraFwd, out vec3 lightPos, out vec3 lightLookAt)
{
    lightLookAt = cameraLookAt + vec3(cameraFwd.x, 0, cameraFwd.z) * 20.;
    lightPos = lightLookAt + LIGHT_DIR * 50.;
}

// compute lookat basis vectors
void fxCalcCamera(vec3 cameraPos, vec3 cameraLookAt, out vec3 cameraFwd, out vec3 cameraLeft, out vec3 cameraUp)
{
    cameraFwd  = normalize(cameraLookAt - cameraPos);
    cameraLeft = -normalize(vec3(-cameraFwd.z, 0, cameraFwd.x));
    cameraUp   = normalize(cross(cameraLeft, cameraFwd));
}

// compute matrix from basis vectors
mat4 fxCalcCameraMat(vec3 resolution, vec3 cameraLeft, vec3 cameraUp, vec3 cameraFwd, vec3 cameraPos)
{
    return mat4(vec4(-cameraLeft, 0.0),
        vec4(-cameraUp, 0.0),
        vec4(cameraFwd, 0.0),
        vec4(cameraPos, 1.0));
}

// compute ray from camera basis
vec3 fxCalcRay(in vec2 fragCoord, in vec3 iResolution, in vec3 cameraFwd, in vec3 cameraUp, in vec3 cameraLeft)
{
	vec2 screenPos = (fragCoord - .5 * iResolution.xy) / iResolution.y;
	return normalize(cameraFwd - screenPos.x * cameraLeft - screenPos.y * cameraUp);
}

// VORONOI

float distance2Body(sampler2D bodyData, int resolutionWidth, int id, vec3 ro, vec3 rd)
{
    if(id < 0) return FAR_CLIP;

    vec3 worldPos = fxGetBodyDataImpl(bodyData, resolutionWidth, id, POS).xyz;
    vec4 worldRot = fxGetBodyDataImpl(bodyData, resolutionWidth, id, ROT);

    vec3 localDir = rotateVectorByQuat(rd, worldRot);
    vec3 localOrigin = rotateVectorByQuat(ro - worldPos, worldRot);

    float t = boxIntersection(localOrigin, localDir, vec3(.6)); // n.b.: slightly bigger than box

    if (t < 0.) return 1e6; // boxIntersection gives us a -1 if we miss, but we need something that sorts to the end

    return t;
}

void insertion_sort(inout ivec4 i, inout vec4 d, int i_, float d_)
{	
    if(any(equal(ivec4(i_),i))) return;
    if     (d_ < d[0])             
        i = ivec4(i_,i.xyz),    d = vec4(d_,d.xyz);
    else if(d_ < d[1])             
        i = ivec4(i.x,i_,i.yz), d = vec4(d.x,d_,d.yz);
    else if(d_ < d[2])            
        i = ivec4(i.xy,i_,i.z), d = vec4(d.xy,d_,d.z);
    else if(d_ < d[3])           
        i = ivec4(i.xyz,i_),    d = vec4(d.xyz,d_);
}

// online algorithm to compute the closest bodies to each raster position
ivec4 voronoi(sampler2D bodyData, int resolutionWidth, sampler2D cameraClosest, vec2 fragCoord, vec3 cameraPos, vec3 rayDir, int iFrame, uint searchRange, uint searchCount)
{
    ivec4 new = ivec4(-1);
    if (iFrame == 0) return new;

    vec4 dis = vec4(1e6);
    ivec2 ifc = ivec2(fragCoord);

    // reinsert existing closest to compute distances
    ivec4 old = fxGetClosestImpl(cameraClosest, ifc);      
    for (int j = 0; j < 4; j++)
    {
        int id = old[j];
        if (id < 0) break;
        float dis2 = distance2Body(bodyData, resolutionWidth, id, cameraPos, rayDir);
        insertion_sort( new, dis, id, dis2 );
    }

    // search nearby voronoi cells for bodies that should move into our cell
    for(uint i=0u; i<searchCount; ++i)
    {
        uvec4 h0 = hash(uvec4(fragCoord, iFrame, i) * i);
        ivec4 old = fxGetClosestImpl(cameraClosest, ifc + ( ivec2(h0.xy % searchRange) - ivec2(searchRange / 2u) ));      

        for (int j = 0; j < 1; j++)
        {
            int id = old[j];
            if (id < 0) break;
            float dis2 = distance2Body(bodyData, resolutionWidth, id, cameraPos, rayDir);
            insertion_sort( new, dis, id, dis2 );
        }        
    }

    // random searching to kick start the process
    int searchIterations = iFrame < 5 ? 20 : 1;
    for(int k = 0; k < searchIterations; k++)
    {
        int id = int(hash(uvec4(ifc.x, ifc.y * 3, iFrame, k)).x) % NUM_BODIES;
        insertion_sort(new, dis, id, distance2Body(bodyData, resolutionWidth, id, cameraPos, rayDir));
    }

    return new;
}

