// -------------------------------------------------------------------------------------------
// Rigid body simulation and persistent state...an array of fxRigidBodies following an fxState
// -------------------------------------------------------------------------------------------

const vec3 GRAVITY = vec3(0, -.01, 0);            // velocity change per frame caused by gravity
const float FRIC_COEF = .03;                      // Coulomb friction constant
const float ALLOWED_PENETRATION = .001;           // object floats up to this high above surface to maintain contact coherence
const float RESTITUTION_COEF = .5;                // how much to bounce (elasticity); 1. == perfect elasticity 0. == perfect inelasticity
const float INVERSE_MASS = 80.;                   // one over the mass
const mat3 INV_INERTA_LOCAL = mat3(INVERSE_MASS); // box inertia, used for impulse calculations

// maintains the status of each contact point during iterations
struct fxContact
{
    vec3 relPos;       // world space position of contact relative to body CG
    vec3 norm;         // contact normal
    float depth;       // collision depth, positive needs position correction
    float targetVel;   // used by restitution to aim us towards to bigger velocity than zero
    vec3 jacDiagABInv; // the relationship between impulse and velocity for x (normal) and yz (friction) directions
};

// get the linear velocity of a point on the body
vec3 getLocalVelocity(fxRigidBody b, vec3 relPos)
{
    return b.vel + cross(relPos, b.angVel);
}

// rotate the inertia tensor into world space
mat3 getInvInertiaTensorWorld(fxRigidBody b)
{
    mat3 basis = matFromQuat(b.rot);
    return basis * INV_INERTA_LOCAL * transpose(basis);
}

// make arbitrary (but consistent) basis vectors given a direction
void makeOrthonormals(vec3 v, out vec3 v1, out vec3 v2)
{
    v1 = cross(v, vec3(1, 0, 0));
    if (length(v1) < 1e-6)
    {
        v1 = cross(v, vec3(0, 1, 0));
    }

    v1 = normalize(v1);
    v2 = normalize(cross(v, v1));
}

// how much impulse is needed to achieve a change in velocity in each direction (x - normal, yz - friction)
vec3 computeImpulseDenominator(fxRigidBody b, vec3 relPos, vec3 norm)
{
    vec3 tangent, binormal; makeOrthonormals(norm, tangent, binormal);
    mat3 invInertiaTensorWorld = getInvInertiaTensorWorld(b);

    vec3 c0 = cross(relPos, norm);
    vec3 v0 = cross(c0 * invInertiaTensorWorld, relPos);

    vec3 c1 = cross(relPos, tangent);
    vec3 v1 = cross(c1 * invInertiaTensorWorld, relPos);

    vec3 c2 = cross(relPos, binormal);
    vec3 v2 = cross(c2 * invInertiaTensorWorld, relPos);

    return INVERSE_MASS + vec3(dot(norm, v0), dot(tangent, v1), dot(binormal, v2));
}

// change the velocity of a body via an impulse at an offset from CG in world space
void applyImpulse(inout fxRigidBody b, vec3 impulse, vec3 relPos)
{
    b.vel += impulse * INVERSE_MASS;
    b.angVel += getInvInertiaTensorWorld(b) * cross(impulse, relPos);
}

void initRigidBody(inout fxRigidBody b, int id, vec3 cameraPos)
{
    // setup random initial position, rotation, velocity, angVel
    vec4 h = hash44(vec4(iFrame, id, id, id));
    b.pos = (h.xyz - vec3(.5, 0, .5)) * 16. * vec3(5, 30, 5) + vec3(0, 20, 0) + cameraPos;
    vec4 h2 = hash44(vec4(iFrame, h.w, iDate.z, id));
    b.rot = quatFromRot(vec3(h2.xyz * 3. * PI));

    vec4 h3 = hash44(vec4(iFrame, h2.w, iDate.z, id));
    b.vel = vec3(h3.x - .5, 0, h3.y - .5) * .3;
    b.angVel = vec3((h2.xyz - .5) * 2. * PI) * .01;
}

// constraint math adapted from Erwin Coumans' Bullet:
// https://github.com/bulletphysics/bullet3/blob/master/src/BulletDynamics/ConstraintSolver/btContactConstraint.cpp
void prepareContact(inout fxRigidBody b, float warmStartImpulse, inout fxContact c)
{
    // compute the component of the jacobian in three directions (normal, tangent, binormal)
	vec3 denom = computeImpulseDenominator(b, c.relPos, c.norm);
	c.jacDiagABInv = 1. / denom;

    // precompute the target velocity necessary to achieve the desired restitution
  	vec3 vel = getLocalVelocity(b, c.relPos);
	float relVel = dot(c.norm, vel);
    c.targetVel = min(0., relVel * RESTITUTION_COEF + .01);

    // compute and apply the Baumgarte impulse (position correction) to prevent sinking
    float positionalError = c.depth * .01;
    float penetrationImpulse = positionalError / denom.x;

    // combine the Baumgarte with warm start impulse and apply
    float prepareImpulse = penetrationImpulse + warmStartImpulse;
    applyImpulse(b, c.norm * prepareImpulse, c.relPos);
}

// perform one iteration, computing impulses for one contact to resolve velocity error
void resolveContact(inout fxRigidBody b, inout vec3 accumImpulse, inout fxContact c)
{
	vec3 vel = getLocalVelocity(b, c.relPos); // the velocity at the contact point (world space)
	float relVel = dot(c.norm, vel); // the component of the local velocity in the normal direction

	float velocityError = -relVel - c.targetVel; // how much velocity error we need to correct
	float normalImpulse = velocityError * c.jacDiagABInv.x; // the impulse needed to correct the error

    float totalImpulse = accumImpulse.x + normalImpulse; // the full normal impulse applied so far
	totalImpulse = max(0., totalImpulse); // complementarity condition (can't apply negative total impulse)
    float normalImpulseAdjustment = totalImpulse - accumImpulse.x; // how much correction to apply to the impulse
    accumImpulse.x = totalImpulse; // save the normal impulse for next time (warm start)

	applyImpulse(b, c.norm * normalImpulseAdjustment, c.relPos); // apply normal impulse

	vel = getLocalVelocity(b, c.relPos); // update the velocity after applying normal impulse

    vec3 tangent, binormal; makeOrthonormals(c.norm, tangent, binormal); // compute friction directions

    vec2 frictionVel = vec2(dot(vel, tangent), dot(vel, binormal)); // compute the velocity in the friction directions
	vec2 frictionImpulse = -frictionVel * c.jacDiagABInv.yz; // compute the impulse necessary to stop all sliding

    vec2 totalFrictionImpulse = accumImpulse.yz + frictionImpulse; // compute the total friction impulse applied

    // clamp total friction based on friction constant times normal force to allow dynamic friction
	totalFrictionImpulse = clamp(totalFrictionImpulse, -FRIC_COEF * totalImpulse, FRIC_COEF * totalImpulse);
    vec2 frictionImpulseAdjustment = totalFrictionImpulse - accumImpulse.yz; // how much correction to apply to friction impulse
    accumImpulse.yz = totalFrictionImpulse; // save the friction impulse for next time (warm start)

    vec3 worldFrictionImpulse = totalFrictionImpulse.x * tangent + totalFrictionImpulse.y * binormal; // combine friction impulses
	applyImpulse(b, worldFrictionImpulse, c.relPos); // apply friction impulses
}

// advance one body's state by one frame
void updateRigidBody(int id, inout fxRigidBody b, fxState state)
{
    // integrate velocity
    b.vel += GRAVITY;
    const float MAX_Y_SPEED = .2;
    if (abs(b.vel.y) > MAX_Y_SPEED)
    {
        b.vel.y = MAX_Y_SPEED * sign(b.vel.y);
    }

    // collision detection (very simple, just corners vs. plane)
    const int MAX_NUM_CONTACTS = 8;
    fxContact contacts[MAX_NUM_CONTACTS];
    int numContacts = 0;

    for (int x = 0; x < 2; x++)
    for (int y = 0; y < 2; y++)
    for (int z = 0; z < 2; z++)
    {
        int index = x * 4 + y * 2 + z;
        vec3 localPos = (vec3(x, y, z) * 2. - 1.)  * .5;
        vec3 relPos = rotateVectorByQuat(localPos, quatInverse(b.rot));

        vec3 worldPos = relPos + b.pos;
        if (worldPos.y < ALLOWED_PENETRATION)
        {
            // prepare contact found below plane
            contacts[index].relPos = relPos;
            contacts[index].norm = vec3(0, 1, 0);
            contacts[index].depth = ALLOWED_PENETRATION - worldPos.y;
            prepareContact(b, b.impulses[index].x, contacts[index]);
        }
        else
        {
            // not touching, reset contact
            contacts[index].depth = 0.;
            b.impulses[index] = vec3(0);
        }
    }

    // mouse grab impulse
    if (iMouse.z > 0. && iMouse.w < 0. && state.grabId == id)
    {
        vec3 cameraLookAt, cameraPos, cameraFwd, cameraLeft, cameraUp;
        fxCameraPath(cameraPos, cameraLookAt, iTime);
        fxCalcCamera(cameraPos, cameraLookAt, cameraFwd, cameraLeft, cameraUp);
        vec3 rayDir = fxCalcRay(iMouse.xy, iResolution, cameraFwd, cameraUp, cameraLeft);
        vec3 mouseGrabPos = cameraPos + rayDir * state.grabDist;
        vec3 localPos = rotateVectorByQuat(state.localGrabPos, quatInverse(b.rot));
        vec3 boxGrabPos = localPos + b.pos;
        const float SPRING_K = .0003;
        const float SPRING_DAMP = .001;
        vec3 localVel = getLocalVelocity(b, localPos);
        applyImpulse(b, SPRING_K * (mouseGrabPos - boxGrabPos) - localVel * SPRING_DAMP, boxGrabPos - b.pos);
    }

    // constraint solve
    for (int iter = 0; iter < 3; ++iter)
    for (int index = 0; index < 8; ++index)
    {
        if (contacts[index].depth > 0.)
        {
            resolveContact(b, b.impulses[index], contacts[index]);
        }
    }

    // integrate position
    b.pos += b.vel;

    // integrate rotation
    vec4 rq = quatFromRot(b.angVel);
    b.rot = quatMult(rq, b.rot);
}

// handle persistent state info
void updateState(inout fxState state)
{
    if (iFrame == 0 || iResolution.x * iResolution.y != abs(state.resolution))
    {
        state.resolution = -iResolution.x * iResolution.y;
    }
    else
    {
        state.resolution = abs(state.resolution);
    }

    if (iMouse.z > 0.)
    {
        if (iMouse.w > 0.)
        {
            state.grabId = -1;
            float minT = 1e6;
            for (int i = 0; i < NUM_BODIES; ++i) // seems like this should be slow, and could use voronoi buffer here, but \_()_/
            {
                fxRigidBody b = fxGetBody(i);
                vec3 cameraLookAt, cameraPos, cameraFwd, cameraLeft, cameraUp;
                fxCameraPath(cameraPos, cameraLookAt, iTime);
                fxCalcCamera(cameraPos, cameraLookAt, cameraFwd, cameraLeft, cameraUp);
                vec3 rayDir = fxCalcRay(iMouse.xy, iResolution, cameraFwd, cameraUp, cameraLeft);
                vec3 localDir = rotateVectorByQuat(rayDir, b.rot);
                vec3 localOrigin = rotateVectorByQuat(cameraPos - b.pos, b.rot);

                float t = boxIntersection(localOrigin, localDir, vec3(.5));
                if (t > 0. && t < minT)
                {
                    minT = t;
                    state.grabId = i;
                    state.grabDist = t;
                    state.localGrabPos = localOrigin + localDir * t;
                }
            }
        }
    }
    else
    {
        state.grabId = -1;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 ifc = ivec2(fragCoord);
    fxState state = fxGetState();

    // handle state pixels
    if (fxIsStatePixel(ifc))
    {
        updateState(state);
        fragColor = fxPutState(state, ifc);
        return;
    }

    // locate which body we're in, and determine the data member we're computing
    int index = ifc.x + ifc.y*int(iResolution.x) - NUM_STATE_PIXELS;
    int id = index / NUM_DATA_TYPES;
    int dataType = index - id * NUM_DATA_TYPES;
    if(id>=NUM_BODIES) return;

    fxRigidBody b = fxGetBody(id);

    vec3 cameraLookAt, cameraPos, cameraFwd, cameraLeft, cameraUp;
    fxCameraPath(cameraPos, cameraLookAt, iTime);
    fxCalcCamera(cameraPos, cameraLookAt, cameraFwd, cameraLeft, cameraUp);

    if (iFrame == 0 || // reset on frame zero...
        state.resolution < 0. || // ...or if the resolution changed...
        distance(b.pos.xz, cameraPos.xz) > 40. || // ...or if too far from the camera...
        dot(b.pos - cameraPos, cameraFwd) < 0.) // ...or if behind the camera
    {
        initRigidBody(b, id, cameraPos);
    }
    else
    {
        updateRigidBody(id, b, state);
    }

    fragColor = fxSaveBody(b, dataType);
}

