// ---------------------------------------------------------------------------------------
// Voronoi particle tracking buffer from camera position
// ---------------------------------------------------------------------------------------

void mainImage( out vec4 fragColor, vec2 fragCoord)
{
    vec3 cameraLookAt, cameraPos, cameraFwd, cameraLeft, cameraUp;
    fxCameraPath(cameraPos, cameraLookAt, iTime);
    fxCalcCamera(cameraPos, cameraLookAt, cameraFwd, cameraLeft, cameraUp);

    vec3 rayDir = fxCalcRay(fragCoord, iResolution, cameraFwd, cameraUp, cameraLeft);

    const uint searchRange = 128u; // larger ranges allow faster movement, but require larger counts
    const uint searchCount = 12u; // larger counts find more correct ids, but cost more
    ivec4 new = voronoi(iChannel0, int(iResolution.x), iChannel1, fragCoord, cameraPos, rayDir, iFrame, searchRange, searchCount);

    fragColor = vec4(new);
}

