// ---------------------------------------------------------------------------------------
// Voronoi particle tracking buffer from light position (orthographic)
// ---------------------------------------------------------------------------------------

void mainImage( out vec4 fragColor, vec2 fragCoord)
{
    vec3 cameraLookAt, cameraPos;
    fxCameraPath(cameraPos, cameraLookAt, iTime);

    vec3 lightPos, lightLookAt, lightFwd, lightLeft, lightUp;
    fxLightPath(cameraLookAt, normalize(cameraLookAt - cameraPos), lightPos, lightLookAt);
    fxCalcCamera(lightPos, lightLookAt, lightFwd, lightLeft, lightUp);
	vec2 screenPos = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    lightPos -= (lightLeft * screenPos.x + lightUp * screenPos.y) * SHADOW_RES; // move light pos intead of ray dir (orthographic projection)

    vec3 rayDir = -LIGHT_DIR;
    const uint searchRange = 64u; // larger ranges allow faster movement, but require larger counts
    const uint searchCount = 12u; // larger counts find more correct ids, but cost more
    ivec4 new = voronoi(iChannel0, int(iResolution.x), iChannel2, fragCoord, lightPos, rayDir, iFrame, searchRange, searchCount);

    fragColor = vec4(new);
}

