	vec2 uv = frag/iResolution.xy;

	float time = iTime-.50;

    vec3 col = texture(iChannel0, uv).xyz;

    // Bloom computation
    vec3 cumul = vec3(0);
	for(float i=-10.; i<=0.5; ++i) {
