//GLOBAL VARIABLES
float time;
//PRIMITIVE FUNCTIONS
float box(vec3 p,vec3 r){p=abs(p)-r;return max(max(p.x,p.y),p.z);}
float sphere(vec3 p,float r){return length(p)-r;}
float torus(vec3 p,vec2 t ){vec2 q=vec2(length(p.xz)-t.x,p.y);return length(q)-t.y;}
// Pseudo random number generator. 
float hash( vec2 a )
{

    return fract( sin( a.x * 3433.8 + a.y * 3843.98 ) * 45933.8 );

}

// Value noise courtesy of BigWingz 
// check his youtube channel he has
// a video of this one.
// Succint version by FabriceNeyret
float noise( vec2 U )
{
    vec2 id = floor( U );
          U = fract( U );
    U *= U * ( 3. - 2. * U );  

    vec2 A = vec2( hash(id)            , hash(id + vec2(0,1)) ),  
         B = vec2( hash(id + vec2(1,0)), hash(id + vec2(1,1)) ),  
         C = mix( A, B, U.x);

    return mix( C.x, C.y, U.y );
}
//MAP / SCENE FUNCTION
float map(vec3 p)
{ 
  float mainBox=box(p,vec3(.1));
  float scene = mainBox;
  float t = clamp(0., 24.0, iTime * 0.2);
  for (float i=0.; i<(t); i+=0.222){
      float fx = sin(noise(vec2(i, i*i)))-0.5;
      float fy = cos(noise(vec2(i*i, i)))-0.5;
      float fr = cos(noise(vec2(i)))-0.5;
      float newBox = box(p+vec3(i*fx*fx*cos(i)*i, i * fy * fy * sin(iTime*0.2), i*fr*fr*tan(i*i))*2., vec3(0.2));
      scene=min(scene,newBox);
      }
  //scene=max(scene,rightTorus);
  return scene;
}
//RAY CAST / TRACE LOOP FUNCTION
float raycast( vec3 ro, vec3 rd )
{
  float dist,res=0.;
  for(int i=0;i<128;i++){ 
    dist=map(ro+rd*res); 
    if(dist<.0001||res>128.) break;
    res+=dist;
  }
  if(res>120.) res=0.;
  return res;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv=(fragCoord.xy/iResolution.xy-0.5)/vec2(iResolution.y/iResolution.x,1); //UV.y from -.5 to .5 and UV.x from -.88 to .88 (aspect ratio broooh)
  time=mod(iTime,62.82); //Modulo time to avoid sin glitch noise due to floating point precision
  // CAMERA  
  vec3 rayOrigin=vec3(cos(time*.2)*5.,2,sin(time*.2)*5.);
  vec3 cameraForward=normalize(vec3(0)-rayOrigin);
  vec3 cameraLeft=normalize(cross(cameraForward,vec3(0,1,0)));
  vec3 cameraUp=normalize(cross(cameraLeft,cameraForward));
  vec3 rayDirection=mat3(cameraLeft,cameraUp,cameraForward)*normalize(vec3(uv,.5)); 
  // LIGHT DIRECTION & BACKGROUND SETUP   
  vec3 lightDirection=normalize(vec3(-.1,.4,-.3));  
  vec3 backgroundColor=vec3(.1,.1,.1)-length(uv)*.1;
  vec3 color=backgroundColor;
  // TRACE AND GET RESULT FOR THIS PIXEL
  float result=raycast(rayOrigin,rayDirection);
  // IF WE HIT SOMETHING THEN DO SOME LIGHTING  
  if(result>0.){ 
    vec3 hitPos=rayOrigin+rayDirection*result; // GET POSITION OF WHERE WE HIT
    vec2 e=vec2(.00035,-.00035); // e=ESPILON = OFFSET NUMBER FOR NORMALS CALCULATION
    vec3 normals=normalize(e.xyy*map(hitPos+e.xyy)+e.yyx*map(hitPos+e.yyx)+e.yxy*map(hitPos+e.yxy)+e.xxx*map(hitPos+e.xxx)); //CALCULATE NORMALS FOR WHERE WE HIT
    vec3 albedo=vec3(.0,.2,.4); // SETUP BASE COLOUR
    float diffuse=max(0.,dot(normals,lightDirection)); // SIMPLE DIFFUSE LIGHTING
    float fresnel=pow(1.+dot(normals,rayDirection),4.); // FRESNEL = BACKGROUND REFLECTIONS ADDED TO EDGES OF GEOMETRY TO COMPOSITE IT BETTER IN THE SCENE
    float specular=pow(max(dot(reflect(-lightDirection,normals),-rayDirection),0.),30.);// SPECULAR LIGHTING = Bright highlights, the shiny white flashy bit especially visible on metallic objects
    float ao=clamp(map(hitPos+normals*.05)/.05,0.,1.); // AO = AMBIENT OCCLUSION = Small shadows where geometries touch due to ambient lighting
    float sss=smoothstep(0.,1.,map(hitPos+lightDirection*.4)/.4);// SSS = Sub surface scattering = Light penetrating through edges of a translucent object, especially visible on candle wax or skin. Can be used as backlight too.
    color=mix(specular+albedo*(ao+.2)*(diffuse+sss*.1),backgroundColor,fresnel); // Final lighting result made of all the above and then mixed with fresnel
    color=mix(backgroundColor,color,exp(-.002*result*result*result)); // Add fog right at the end
  }
  fragColor = vec4(pow(color,vec3(.4545)),1);// RETURN FINAL COLOR WITH GAMMA CORRECTION (pow(color,vec3(.4545)))
}

// Solum Object 0.52.230509 by QuantumSuper
// auto-vj with circles & triangles, shattered particles, and visual tweaks
// 
// - use with music in iChannel0 -


#define PI 3.14159265359 
#define aTime 128./60.*iTime
vec4 fft, ffts; //compressed frequency amplitudes


void compressFft(){ //v1.2, compress sound in iChannel0 to simplified amplitude estimations by frequency-range
    fft = vec4(0), ffts = vec4(0);

	// Sound (assume sound texture with 44.1kHz in 512 texels, cf. https://www.shadertoy.com/view/Xds3Rr)
    for (int n=0;n<3;n++) fft.x  += texelFetch( iChannel0, ivec2(n,0), 0 ).x; //bass, 0-517Hz, reduced to 0-258Hz
    for (int n=6;n<8;n++) ffts.x  += texelFetch( iChannel0, ivec2(n,0), 0 ).x; //speech I, 517-689Hz
    for (int n=8;n<14;n+=2) ffts.y  += texelFetch( iChannel0, ivec2(n,0), 0 ).x; //speech II, 689-1206Hz
    for (int n=14;n<24;n+=4) ffts.z  += texelFetch( iChannel0, ivec2(n,0), 0 ).x; //speech III, 1206-2067Hz
    for (int n=24;n<95;n+=10) fft.z  += texelFetch( iChannel0, ivec2(n,0), 0 ).x; //presence, 2067-8183Hz, tenth sample
    for (int n=95;n<512;n+=100) fft.w  += texelFetch( iChannel0, ivec2(n,0), 0 ).x; //brilliance, 8183-44100Hz, tenth2 sample
    fft.y = dot(ffts.xyz,vec3(1)); //speech I-III, 517-2067Hz
    ffts.w = dot(fft.xyzw,vec4(1)); //overall loudness
    fft /= vec4(3,8,8,5); ffts /= vec4(2,3,3,23); //normalize
    //for (int n=0;n++<4;) fft[n] *= 1. + .3*pow(fft[n],5.); fft = clamp(fft,.0,1.); //workaround for VirtualDJ, ?any hints for reverting audio limiters appreciated 
}

