#define FLAT_COLORS 1
#define USE_VIDEO 0

const vec4 BACKGROUND_COLOR = vec4(0.8, 0.8, 0.8, -1.0);
const float N = 2.0;

struct Torus
{
    float r;
    float s;

    vec3 color;
};

struct Light
{
    vec3 pos;
    vec3 color;
    float ambientIntensity;
};

Torus t1 = Torus(0.5, 0.25, vec3(0.80, 0.45, 0.38));
Light l1 = Light(vec3(2.0, -5.0, 3.0), vec3(1.0), 0.2);


mat4 translate(vec3 v) {
    return mat4(
        1, 0, 0, -v.x, 
        0, 1, 0, -v.y, 
        0, 0, 1, -v.z, 
        0, 0, 0, 1
    );
}

mat4 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat4(
        1, 0, 0, 0,
        0, c, -s, 0,
        0, s, c, 0,
        0, 0, 0, 1
    );
}

mat4 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat4(
        c, 0, s, 0, 
        0, 1, 0, 0, 
        -s, 0, c, 0, 
        0, 0, 0, 1
    );
}

float torusSDF(in vec3 p)
{
    float h = length(p.xz);
    return length(vec2(h-t1.r,p.y))-t1.s;
}

vec3 calculateNormal(in vec3 pos)
{
    const vec3 smallStep = vec3(0.001, 0.0, 0.0);

    float gradientX = torusSDF(pos + smallStep.xyy) - torusSDF(pos - smallStep.xyy);
    float gradientY = torusSDF(pos + smallStep.yxy) - torusSDF(pos - smallStep.yxy);
    float gradientZ = torusSDF(pos + smallStep.yyx) - torusSDF(pos - smallStep.yyx);

    vec3 normal = vec3(gradientX, gradientY, gradientZ);

    return normalize(normal);
}

vec4 rayMarch(in vec3 rayOrigin, in vec3 rayDir)
{
    float totalDistTraveled = 0.0;
    const int NUMBER_OF_STEPS = 256;
    const float MINIMUM_HIT_DIST = 0.001;
    const float MAXIMUM_TRACE_DIST = 5.0;

    for (int i = 0; i < NUMBER_OF_STEPS; ++i)
    {        
        if (totalDistTraveled >= MAXIMUM_TRACE_DIST)
            break;

        vec4 currentPos = vec4(rayOrigin + totalDistTraveled * rayDir, 1.0);
        currentPos *= translate(vec3(0.0, 0.0, 0.0)) * rotateY(iTime) * rotateX(1.0);

        float smallestDist = torusSDF(currentPos.xyz);

        if (smallestDist <= MINIMUM_HIT_DIST)
        {
            vec3 normal = calculateNormal(currentPos.xyz);
            l1.pos = vec3(vec4(l1.pos, 0.0) * rotateY(iTime));
            vec3 lightDir = normalize(currentPos.xyz - l1.pos);

            float diffuseIntensity = max(l1.ambientIntensity, dot(normal, lightDir));

            return vec4(t1.color * l1.color * diffuseIntensity, 0.0);
        }

        totalDistTraveled += smallestDist;
    }

    return BACKGROUND_COLOR;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    // convert from (0 to 1) to (-1 to 1)
    uv = uv * 2.0 - 1.0;
    // rescale
    uv.x *= iResolution.x / iResolution.y;


    vec3 rOrigin = vec3(0.0, 0.0, -1.2);
    vec3 rDir = vec3(uv, 1.0);
#if USE_VIDEO
    vec4 color = texture(iChannel1, fragCoord / iResolution.xy).rgba;
#else
    vec4 color = rayMarch(rOrigin, rDir);
#endif

    float greyScale = dot(color.xyz, vec3(1.0));
    float inverseN = 1.0 / N;
    float difference = mod(greyScale, inverseN);
    greyScale -= difference - inverseN;
#if FLAT_COLORS
    color *= vec4(vec3(greyScale), 1.0);
    //color = vec4(t1.color, 1.0) * vec4(vec3(greyScale), 1.0); // arguably better
#endif

    if (color.a == -1.0)
    {
        color /= vec4(vec3(greyScale), 1.0);
        greyScale = 0.0;
    }

    // Output to buffer A
    fragColor = vec4(color.xyz, greyScale);
}

