const float STRONG_THRESHOLD = 0.7;
const float WEAK_THRESHOLD = 0.2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = floor(fragCoord.x), y = floor(fragCoord.y);
    float prevIndex = x + iResolution.x * y - 1.0;
    ivec2 prevFragCoord = ivec2(mod(prevIndex, iResolution.x), floor(prevIndex / iResolution.x));

    float nextIndex = x + iResolution.x * y + 1.0;
    ivec2 nextFragCoord = ivec2(mod(nextIndex, iResolution.x), floor(nextIndex / iResolution.x));

    vec3 color = texelFetch(iChannel0, ivec2(fragCoord), 0).rgb;
    float colorIntensity = dot(color, vec3(1.0));
    float prevColorIntensity = dot(texelFetch(iChannel0, prevFragCoord, 0).rgb, vec3(1.0));
    float nextColorIntensity = dot(texelFetch(iChannel0, nextFragCoord, 0).rgb, vec3(1.0));

    fragColor = vec4(0.0);
    if (nextFragCoord.y == int(fragCoord.y) && floor(fragCoord.y) != iResolution.y - 1.0)
    {
        if (colorIntensity >= WEAK_THRESHOLD && colorIntensity >= prevColorIntensity && colorIntensity >= nextColorIntensity)
        {
            fragColor = vec4(texelFetch(iChannel0, ivec2(fragCoord), 0).rgb, 0.1);
            if (colorIntensity >= STRONG_THRESHOLD)
            {
                fragColor = vec4(texelFetch(iChannel0, ivec2(fragCoord), 0).rgb, 1.0);
            }
        }
    }
}

