void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color = texelFetch(iChannel0, ivec2(fragCoord), 0).rgba;
    float sum = 0.0;
    for (float y = -1.0; y <= 1.0; y++)
    {
        for (float x = -1.0; x <= 1.0; x++)
        {
            if (x != 0.0 && y != 0.0)
            {
                ivec2 newFragCoord = ivec2(fragCoord + vec2(x, y));
                vec4 neighbour = texelFetch(iChannel0, newFragCoord, 0).rgba;
                sum += neighbour.a;
            }
        }
    }

    fragColor = vec4(color.rgb, 0.0);
    if (sum >= 1.0)
        fragColor = color;
}

