// Rectangular Pillar LOD Traversal - gelami
// https://www.shadertoy.com/view/mttGWX

/*
 * Fast traversal of a dense grid of rectangular pillars
 * using an implicit quadtree acceleration structure
 * 
 * Mouse drag to look around
 * Defines in Common
 * 
 * The idea is to have the height on each level be multiplied
 * with all of the heights of the levels above it
 * This ensures the height is smaller than than ones above,
 * and allowing us to traverse it as a quadtree structure
 * 
 * Though the bounds for each level isn't tightly fit,
 * it works as long as it is conservative
 * 
 * Another way to do this with proper bounds is using interval arithmetic,
 * which allows us to query the height over a range to have proper bounds
 * 
 * No AA this time unfortunately,
 * the very dense grid didn't bode well with TAA or FXAA v_v
 * 
 * My previous shader that also did minmax traversal
 * which precomputes the minmax mipmap in a buffer
 * 
 * Fast Minmax Terrain Traversal - gelami
 * https://www.shadertoy.com/view/msj3Dh
 * 
 */

// Fork of "Gelami Raymarching Template" by gelami. https://shadertoy.com/view/mslGRs
// 2023-05-05 04:42:46

vec2 ce;
float map(vec2 p, float lod)
{
    #ifdef ENABLE_LOD

    float st = (sin(iTime * TAU * 0.05)*0.5+0.5)*0.5 + 0.4;

    float h = 1.0;
    for (float i = MAX_LOD; i >= lod; i--)
    {
        #if 0
        float f = hash12(floor(p * exp2(MAX_LOD-i)));

        f = sin((f + iTime * 0.25) * TAU)*0.5+0.5;
        f = mix(1.0, f, 0.4);

        #else
        float s = exp2(MAX_LOD-i);
        vec2 o = floor(p * s) / exp2(MAX_LOD-i);
        vec2 o2 = (o - ce) * st + ce;

        float f = (sin(o2.x)*0.5+0.5 + cos(o2.y)*0.5+0.5)*0.5;

        float g = texture(iChannel0, 0.2 * o2 / iChannelResolution[0].xy).r;

        float of = texture(iChannel0, o * 0.0004).x;
        f = sin((f + g + iTime * 0.25 + of) * TAU)*0.5+0.5;

        float f2 = hash12(-floor(p * exp2(MAX_LOD-i)));
        f -= f2 * 0.1;
        f = mix(1.0, f, 0.15);
        #endif

        h *= f;
    }
    #else
    float st = (sin(iTime*0.08)*0.5+0.5) + 0.2;
    st *= 0.25;
    float f = (sin(p.x * st)*0.5+0.5 + cos(p.y * st)*0.5+0.5)*0.5;
    f = sin((f + iTime * 0.25) * TAU)*0.5+0.5;
    float h = f;
    #endif

    return clamp(h * 0.9999 * MAX_HEIGHT, MIN_HEIGHT, MAX_HEIGHT);
}

struct HitInfo
{
    float t;
    vec3 n;
    vec2 tp;
    float lod;
    int i;
};

bool trace(vec3 ro, vec3 rd, out HitInfo hit)
{
    if ((ro.y > MAX_HEIGHT && rd.y > 0.0) || (ro.y < MIN_HEIGHT && rd.y > 0.0))
        return false;

    vec2 tp = floor(ro.xz);
    vec2 ltp = tp;

    float lod = MAX_LOD;
    float bs = 1.0;

    vec3 ird = 1.0 / rd;
    vec3 srd = sign(rd);
    vec3 ard = abs(ird);

    vec3 iro = ro * ird;

    hit.t = MAX_DIST;
    hit.n = vec3(-1);

    int i = 0;
    for (; i < STEPS; i++)
    {
        float d = map(tp * bs, lod) * 0.5;

        vec3 p = vec3((tp.x + 0.5) * bs, d, (tp.y + 0.5) * bs);
        vec3 s = vec3(bs * 0.5, d, bs * 0.5);
        vec3 n = iro - p * ird;
        vec3 k = ard * s;

        vec3 t1 = -n - k;
        vec3 t2 = -n + k;

        float tN = max(max(t1.x, t1.y), t1.z);
        float tF = min(min(t2.x, t2.y), t2.z);

        vec3 pos = ro + rd * tN;

        if (pos.y > MAX_HEIGHT && rd.y > 0.0)
            return false;

        if (tN < tF && tN >= 0.0)
        {
            if (tN >= MAX_DIST)
                return false;

            #ifndef ENABLE_LOD
            hit.t = tN;
            hit.n = -srd * step(t1.yzx, t1) * step(t1.zxy, t1);
            hit.tp = tp;
            hit.i = i;
            return true;

            #else

            float maxLod = log2(tN);

            if (lod > 0.0)
            {
                tp *= 2.0;
                tp += step(vec2(0), pos.xz - p.xz);
                ltp = tp;

                bs *= 0.5;
                lod--;
                continue;
            } else
            {
                #ifndef ROUNDED
                hit.t = tN;
                hit.n = -srd * step(t1.yzx, t1) * step(t1.zxy, t1);
                hit.tp = tp * bs;
                hit.lod = lod;
                hit.i = i;
                return true;

                #else
                float rad = RADIUS;
                vec3 o = p;
                vec3 rp = ro - o;
                vec3 rs = max(s-rad, vec3(0));
                float rt = roundedboxIntersect(pos - o, rp, rd, rs, rad);

                if (rt != MAX_DIST)
                {
                    hit.t = rt == 0.0 ? tN : rt;
                    vec3 pos = rp + rd * hit.t;
                    hit.n = normalize(roundedboxNormal(pos, rs, rad));
                    hit.tp = tp * bs;
                    hit.lod = lod;
                    hit.i = i;
                    return true;
                }
                #endif
            }
            #endif
        }

        ltp = tp;
        tp += step(t2.xz, t2.zx) * srd.xz;

        #ifdef ENABLE_LOD
        if (floor(ltp*0.5) != floor(tp*0.5) && lod < MAX_LOD)
        {
            tp = floor(tp*0.5);
            ltp = tp;
            bs *= 2.0;
            lod++;
        }
        #endif
    }
    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pv = (2. * (fragCoord) - iResolution.xy) / iResolution.y;
    vec2 uv = fragCoord / iResolution.xy;

    #ifdef ENABLE_LOD
    vec3 ro = vec3(0, 0, 16);
    #else
    vec3 ro = vec3(0, 0, 60);
    #endif
    vec3 lo = vec3(0, MAX_HEIGHT*0.7, 0);

    lo.xz += vec2(1, 0.3) * iTime;
    ce = lo.xz;

    vec2 m = iMouse.xy / iResolution.xy;

    float ax = iMouse.z < 0. || iMouse.x == 0. ? -PI * .7 + iTime * .2 : -m.x * TAU + PI;
    float ay = iMouse.z < 0. || iMouse.y == 0. ? PI * 0.23 : -m.y * PI + PI * 0.5;

    ro.yz *= rot2D(ay);
    ro.xz *= rot2D(ax);
    ro += lo;

    mat3 cmat = getCameraMatrix(ro, lo);

    vec3 rd = normalize(cmat * vec3(pv, 2));

    vec3 col = vec3(0);

    vec3 pl = ro;
    float pt = 0.0;
    if (pl.y > MAX_HEIGHT)
    {
        pt = -(pl.y - MAX_HEIGHT) / rd.y;
        pl = pl + rd * pt;
    }

    HitInfo hit;
    bool isHit = trace(pl, rd, hit);

    vec3 pos = pl + rd * hit.t;

    float t = hit.t + pt;

    vec3 alb = palette2(map(hit.tp, 0.0) / MAX_HEIGHT * 2.0 + (hash12(hit.tp)-0.5)*0.15);

    col = alb;

    col *= dot(abs(hit.n), vec3(0.85, 1, 0.7));
    col *= smoothstep(MAX_HEIGHT*0.3, MAX_HEIGHT*0.8, pos.y)*0.8+0.2;

    vec3 ldir = normalize(vec3(1, 2.5, 0.6));
    vec3 ref = reflect(rd, hit.n);

    HitInfo hitL;
    bool isHitL = trace(pos + hit.n * EPS, ldir, hitL);

    float diff = max(dot(ldir, hit.n), 0.0) * float(!isHitL);
    float bou = max(dot(ref, -ldir), 0.0);

    col *= diff*0.8+0.25;
    col += alb * bou * 0.03;

    vec3 refCol = sRGBToLinear(textureLod(iChannel1, ref, 2.0).rgb) * palette2(0.0);

    float r0 = 0.08;
    float fre = r0 + (1.0 - r0) * pow(1.0 - dot(-rd, hit.n), 5.0);

    col = mix(col, refCol, fre * 0.6);

    #if 0
    float a = 0.03;
    float b = 0.1;
    float fog = (a / b) * exp(-ro.y * b) * (1.0 - exp(-max(t - 20.0, 0.0) * rd.y * b)) / rd.y;
    #else
    float fog = 1.0 - exp(-max(t - 20.0, 0.0) * 0.01);
    #endif

    #ifdef FOG
    col = mix(col, palette2(0.0), saturate(fog));
    #endif

    if (!isHit)
        col = palette2(0.0);

    //float f = exp2(float(hit.i) / float(STEPS) * 4.0);
    //f = float(hit.i) / float(STEPS) * 2.0;
    //col = palette(f);

    col = max(col, vec3(0));
    col = col / (1. + col);

    col = mix(col, smoothstep(vec3(0), vec3(1), col), 0.2);
    //col = ACESFilm(col);
    fragColor = vec4(linearTosRGB(col), 1);
    fragColor += (dot(hash23(vec3(fragCoord, iTime)), vec2(1)) - 0.5) / 255.;
}

