    col.r = abs(sin(p.x * 162.0 + time * 20.0));
    col.g = abs(sin(p.y * 1.0 + time * 4.0));
    col.b = abs(sin((p.x + p.y) * 8.0 + time * 50.0));

    // Add VHS-style distortion
    col += 0.09 * vec3(sin(fragCoord.y * 0.11 + time), cos(fragCoord.x * 0.01 + time * 1.1), sin(fragCoord.x * 0.60 + time * 0.7));

    // Output final color
    fragColor = vec4(col, 1.0);
}

