float _seed;
// Thanks to Dave_Hoskin for the random numbers
float rand(){
    float v = fract(_seed*.1031);
    v *= _seed + 33.33;
    v *= _seed + _seed;
    v += 34.35;
    float a = fract(v);
    _seed++;
    _seed *= 2.*a;
    return a;
}

vec2 rand2(){
    vec3 p = fract(_seed*vec3(.1031,.103,.0973));
    p += dot(p,p.yzx+33.33);
    vec2 a = fract((p.xx+p.yz)*p.zy);
    _seed++;
    _seed *= a.x+a.y;
    return a;
}

void irand(vec2 i,float t){
    vec3 p = vec3(i.y,t,i.x);
    p = fract(p*.1031);
    p += dot(p,p.zyx+32.31);
    _seed = fract((p.x+p.y)*p.z);
}

mat3 rot3(vec3 a){
    vec3 c = cos(a);
    vec3 s = sin(a);

    mat3 xm = mat3(1., 0., 0.,
                   0., c.x, -s.x,
                   0., s.x, c.x );

    mat3 ym = mat3(c.y, 0.,-s.y,
                   0.,1.,0.,
                   s.y,0.,c.y);

    mat3 zm = mat3(c.z,s.z,0.,
                   -s.z,c.z,0.,
                   0.,0.,1.);


    return xm*ym*zm;
}


