#define DefaultSph SphBody(vec3(0.),0.,false)
#define DefaultBox BoxBody(vec3(0.),vec3(0.),vec3(0.),false,false)
#define DefaultQuad QuadBody(vec3(0.),vec3(0.),vec3(0.),vec3(0.),false,false)

#define objC 9
struct NewWorld {
    Obj objs[objC];
    Viewer camera;
};

Obj Obj_Sphere(vec3 pos,float rad,Mat m){
    AABB bound = AABB(pos-rad-.0001,pos+rad+.0001);
    return Obj(SphBody(pos,rad,true),DefaultBox,DefaultQuad,m,bound);
}

void createObj_HollowSphere(vec3 pos,float rad,float thickness,int idx0,int idx1,Mat m,inout Obj objlst[objC]){
    objlst[idx0] = Obj_Sphere(pos,rad,m);
    objlst[idx1] = Obj_Sphere(pos,thickness-rad,m);
}

Obj Obj_Box(vec3 pos,vec3 size,vec3 rot,Mat m){
    AABB bound = AABB(pos-2.*size-.001,pos+2.*size+.0001);
    return Obj(DefaultSph,BoxBody(pos,size,rot,false,true),DefaultQuad,m,bound);
}

void createObj_HollowBox(vec3 pos,vec3 size,vec3 rot,float thickness,int idx0,int idx1,Mat m,inout Obj objlst[objC]){
    objlst[idx0] = Obj_Box(pos,size,rot,m);
    AABB bound = AABB(pos-2.*abs(size)-.001+thickness,pos+2.*abs(size)+.0001-thickness);
    objlst[idx1] = Obj(DefaultSph,BoxBody(pos,size-thickness,rot,true,true),DefaultQuad,m,bound);
}

Obj Obj_Quad(vec3 v0,vec3 v1,vec3 v2,vec3 v3,bool fn,Mat m){
    vec3 minv = min(min(v0,v1),min(v2,v3));
    vec3 maxv = max(max(v0,v1),max(v2,v3));
    AABB bound = AABB(minv,maxv);
    return Obj(DefaultSph,DefaultBox,QuadBody(v0,v1,v2,v3,fn,true),m,bound);
}

IntRes newSphereInt( in vec3 ro, vec3 rd, SphBody sph) {
    ro -= sph.c;
    float b = dot( ro, rd );
    float c = dot( ro, ro ) - sph.rad*sph.rad;
    float h = b*b - c;
    if( h<0. ) return IntRes(-1.,false);
    h = sqrt( h );
    if(length(ro)<=sph.rad){
        return IntRes(-b+h,false);
    }else{
        return IntRes(-b-h,true);
    }
}

bool insideAabb(vec3 p,vec3 s){
    return (p.x<=s.x&&p.x>=-s.x)&&(p.y<=s.y&&p.y>=-s.y)&&(p.z<=s.z&&p.z>=-s.z);
}

IntRes newBoxInt( in vec3 ro, in vec3 rd, BoxBody box) {
    mat3 r = rot3(-box.rot);

    ro, rd *= r;
    ro -= box.pos;
    vec3 m = 1./rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*abs(box.size);
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );
    if( tN>tF || tF<0.) return IntRes(-1.,false);
    if(insideAabb(ro,abs(box.size))){
        return IntRes(tF,box.inverted);
    }else{
        return IntRes(tN,!box.inverted);
    }
}

float newQuadInt(vec3 ro,vec3 rd,QuadBody q){
    vec3 n = cross(q.v1-q.v0,q.v2-q.v0);
    float d = dot(n, q.v0);

    float nd = dot(n, rd);
    if (abs(nd) < 1e-6) return -1.;

    float t = (d - dot(n, ro)) / nd;
    if (t < 0.) return -1.;
    vec3 p = ro + t * rd;

    vec3 e0 = q.v1 - q.v0;
    vec3 e1 = q.v2 - q.v1;
    vec3 e2 = q.v3 - q.v2;
    vec3 e3 = q.v0 - q.v3;
    vec3 c0 = p - q.v0;
    vec3 c1 = p - q.v1;
    vec3 c2 = p - q.v2;
    vec3 c3 = p - q.v3;
    if (dot(n, cross(e0, c0)) > 0. &&
        dot(n, cross(e1, c1)) > 0. &&
        dot(n, cross(e2, c2)) > 0. &&
        dot(n, cross(e3, c3)) > 0.) {
        return t;
    }

    return -1.;
}

bool aabbInt(in vec3 ro,vec3 rd,AABB box){
    vec3 a = .5*(box.maxv+box.minv);
    vec3 d = .5*(box.maxv-box.minv);
    vec3 m = 1./rd;
    vec3 n = m*(ro-a); 
    vec3 k = abs(m)*d;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );
    if( tN>tF || tF<0.) return false;
    return true;
}



const float bias = 1.0001;
vec3 newBoxNor(vec3 p,BoxBody b){
    vec3 q = p-b.pos;
    vec3 d = b.size;
    return normalize(vec3(ivec3(q/d*bias)));
}

vec3 cosDir(vec3 nor)
{
    float u = rand();
    float v = rand();
    float a = 2.*PI*v; float b = 2.*u-1.;
    vec3 dir = vec3(sqrt(1.-b*b)*vec2(cos(a),sin(a)),b);
    return normalize(nor+dir);
}
vec3 uniformVec(void)
{
    float phi = rand()*2.*PI;
    float z = rand()*2.-1.;
    float r = sqrt(1.-z*z);
    float x = r*cos(phi), y = r*sin(phi);
	return vec3(x,y,z);
}
mat2 rot(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2( c,-s,
                 s, c);
}
float reflectance(float cosv,float refidx){
    float r0 = (1.-refidx)/(1.+refidx);
    r0 *= r0;
    return r0+(1.-r0)*pow(1.-cosv,5.);
}
vec3 hemi(vec3 h){
    return reflect(uniformVec(),h);
}


vec3 LessThan(vec3 f, float val) {
    return vec3(
        (f.x < val) ? 1. : 0.,
        (f.y < val) ? 1. : 0.,
        (f.z < val) ? 1. : 0.);
}

vec3 linearToSRGB(vec3 rgb) {
    rgb = clamp(rgb, 0., 1.);

    return mix(
        pow(rgb, vec3(1. / 2.4)) * 1.055 - .055,
        rgb * 12.92,
        LessThan(rgb, .0031308)
    );
}

vec3 SRGBToLinear(vec3 rgb) {
    rgb = clamp(rgb, 0., 1.);

    return mix(
        pow(((rgb + .055) / 1.055), vec3(2.4)),
        rgb / 12.92,
        LessThan(rgb, 0.04045)
    );
}

vec3 ACESFilm(vec3 x) {
    float a = 2.51;
    float b = .03;
    float c = 2.43;
    float d = .59;
    float e = .14;
    return clamp((x*(a*x + b)) / (x*(c*x + d) + e), 0., 1.);
}

float fresnel(float n1, float n2, vec3 nor, vec3 I, float f0, float f90) {
        float r0 = (n1-n2) / (n1+n2);
        r0 *= r0;
        float cosX = -dot(nor, I);
        if (n1 > n2) {
            float n = n1/n2;
            float sinT2 = n*n*(1.-cosX*cosX);
            // Total internal reflection
            if (sinT2 > 1.) return f90;
            cosX = sqrt(1.-sinT2);
        }
        float x = 1.-cosX;
        float ret = r0+(1.-r0)*x*x*x*x*x;

        // adjust reflect multiplier for object reflectivity
        return mix(f0, f90, ret);
}

