NewHit newInt(vec3 ro,vec3 rd,Obj objs[objC]){
    NewHit h;
    h.len = -1.;

    for(int i = 0; i < objC; i++) {
        Obj o = objs[i];
        //if(!aabbInt(ro,rd,o.bound)) continue;
        if(o.sph.isActive) {IntRes res = newSphereInt(ro,rd,o.sph);if(res.len>=.0001&&(res.len<h.len||h.len<0.)){h.o=o;h.ff=res.ff;h.len=res.len;}}
        else if(o.box.isActive) {IntRes res = newBoxInt(ro,rd,o.box);if(res.len>=.0001&&(res.len<h.len||h.len<0.)){h.o=o;h.ff=res.ff;h.len=res.len;}}
        else if(o.quad.isActive) {float res = newQuadInt(ro,rd,o.quad);if(res>=.0001&&(res<h.len||h.len<0.)){h.o=o;h.ff=true;h.len=res;}}
    }

    Obj o = h.o;
    vec3 p = ro+rd*h.len;

    if(o.sph.isActive) h.nor = normalize(p-o.sph.c);//*sign(o.sph.rad);
    else if(o.box.isActive) {BoxBody b = o.box;mat3 r = rot3(b.rot);mat3 rn = rot3(-b.rot);h.nor = newBoxNor(b.pos+(p-b.pos)*rn,b)*r;}
    else if(o.quad.isActive) {QuadBody q = o.quad;h.nor = normalize(cross(q.v1-q.v0,q.v2-q.v0))*(q.fn?-1.:1.);}

    //h.nor *= h.ff?1.:-1.;

    return h;
}

const vec3 sunDir = normalize(vec3(.5,1.,.75));
//Sebastian Lagues's sky function
vec3 getSky(float t,vec3 rd) {
    #if SkyMode == 1
    float skyGradT = pow(smoothstep(0.,.4,rd.y),.35);
    vec3 skyGrad = mix(vec3(.05),vec3(.3,.6,.9),skyGradT);

    vec3 sun = pow(max(0.,dot(rd,sunDir)),40.)*10.*vec3(1.,.85,.7);

    float groundT = smoothstep(-.01,0.,rd.y);

    return mix(vec3(.25),skyGrad,groundT)+sun;
    #elif SkyMode == 0
    return vec3(0.);
    #elif SkyMode == 2
    return SRGBToLinear(texture(iChannel1,rd).xyz);
    #endif
}

vec3 newRay(inout vec3 ro,inout vec3 rd,vec3 p,in vec3 n,Mat m,bool ff) {
    vec3 oro = ro; vec3 ord = rd;
    ro = p;
    #if 0
    vec3 mn = normalize(mix(n,hemi(n),m.r));
    rd = reflect(rd,mn);
    return m.col;
    #else
    if(!ff) n *= -1.;

    float specChance = m.spcCh;
    float refraChance = m.rfaCh;

    float rayProb = 1.;
    if(specChance > 0.) {
        specChance = fresnel(ff?1.:m.IOR,ff?m.IOR:1.,rd,n,m.spcCh,1.);

        float chanceMultiplier = (1.-specChance) / (1.-m.spcCh);
        refraChance *= chanceMultiplier;
    }

    float doRefra = 0., doSpec = 0., roll = rand();
    if(specChance > 0. && roll < specChance) {
        doSpec = 1.;
        rayProb = specChance;
    } else if(refraChance > 0. && roll < specChance + refraChance) {
        doRefra = 1.;
        rayProb = refraChance;
    } else {
        rayProb = 1.-(specChance+refraChance);
    }

    rayProb  = max(rayProb, .0001);

    float r2 = m.r*m.r;

    vec3 difDir = normalize(n+uniformVec());
    vec3 refDir = reflect(rd,n);
    vec3 specDir = normalize(mix(refDir,difDir,r2));

    vec3 refraDir = refract(rd,n,ff?1./m.IOR:m.IOR);
    refraDir = normalize(mix(refraDir,normalize(uniformVec()-n),r2));

    rd = mix(difDir,specDir,doSpec);
    rd = mix(rd,refraDir,doRefra);

    vec3 col = vec3(1.);

    if(doRefra==0.) col *= mix(m.col,m.spcCl,doSpec);

    return col / rayProb;
    #endif

}

void mainImage( out vec4 o, in vec2 i ){
    #ifdef maxFrame
    if(iFrame>=maxFrame) {o=texelFetch(iChannel0,ivec2(i),0);return;}
    #endif
    irand(i,float(iFrame));

    float ftime;
    if(mode==0.){
        o = texelFetch(iChannel0,ivec2(i),0);
        ftime = 0.;
    } else if(mode==1.) {
        o = vec4(0.);
        ftime = iTime;
    }else{
        if(iTimeDelta*mode>1.) {
        o = vec4(0.);
        }else{
        o = texelFetch(iChannel0,ivec2(i),0);
        }
        ftime = floor(iTime*mode)/mode;
    }
    if(texture(iChannel3, vec2(32.5/256.0,0.25)).x > 0.1) o = vec4(0.);

    #if fovInDeg==0
    float dv = 1./tan(FOV/2.);
    #else
    float dv = 1./tan(PI*FOV/360.);
    #endif

    vec3 ttcol = vec3(0.);
    for(float _i = 0.; _i<spp;_i++) {
        vec2 p1 = (-R+ 2.*(i + rand2())) / R.y;

        vec3 er = normalize(vec3(p1,dv));
        float ctime = ftime - rand()*shutAp/24.;

        NewWorld world = getNewWorld(ctime);
        Viewer cam = world.camera;

        vec3 rd = er.x*cam.uu + er.y*cam.vv+er.z*cam.ww;

        vec3 go = blurAmt*vec3(-1.+2.*rand2(),0.);
        vec3 gd = normalize( er*fcsDist-go );
        cam.ro += go.x*cam.uu + go.y*cam.vv;
        rd += gd.x*cam.uu + gd.y*cam.vv;
        rd = normalize(rd);

        vec3 tcol = vec3(0.);
        vec3 fcol = vec3(1.);
        for(int _j = 0; _j<numLvls;_j++) {
            NewHit hit = newInt(cam.ro,rd,world.objs); 
            if(hit.len==-1.) {tcol+=getSky(ctime,rd)*fcol;break;}                             

            if(!hit.ff) fcol *= exp(-hit.o.mat.absorb*hit.len);

            vec3 p = hit.len*rd+cam.ro;

            Mat mat = hit.o.mat;


            tcol += mat.ems*fcol;
            fcol *= newRay(cam.ro,rd,p,hit.nor,mat,hit.ff);

            #if 1
            {
                float r_p = max(.0001,max(fcol.x,max(fcol.y,fcol.z)));
                if(rand()>r_p) break;
                fcol /= r_p;
            }
            #endif
        }

        ttcol += tcol;
    }
    if(ttcol.x!=ttcol.x) ttcol.x = 0.;
    if(ttcol.y!=ttcol.y) ttcol.y = 0.;
    if(ttcol.z!=ttcol.z) ttcol.z = 0.;
    o += vec4(ttcol / spp,1.);
    //o = vec4(i*.001,.5,1.);
}

