    Year of Truchets #019
    05/08/2023  @byt3 - m3chanic

    Just a mix of things laying around on the shader floor scrapped together 
    with some reflections and love.. 

    All year long I'm going to just focus on truchet tiles and the likes!
    Truchet Core \M/->.<-\M/ 2023 

*/

#define R           iResolution
#define T           iTime
#define M           iMouse
#define PI          3.14159265
#define PI2         6.28318530

#define MAX_DIST    50.
#define MIN_DIST    .001

mat2 rot(float a) { return mat2(cos(a),sin(a),-sin(a),cos(a)); }
float hash21(vec2 p) { return fract(sin(dot(p,vec2(26.37,45.93)))*4374.23); }

// https://www.shadertoy.com/view/wsjfRD 
// simple fbm noise generation for clouds
float rand(vec3 p) {
    return fract(sin(dot(p, vec3(12.345, 67.89, 412.12))) * 42123.45) * 2. - 1.;
}
// A perlin noise function. Since we are not using textures, we am gonna sample 8 corners of a cube.
float perlin(vec3 p) {
    vec3 u = floor(p);
    vec3 v = fract(p);
    vec3 s = smoothstep(0., 1., v);

    float a = rand(u);
    float b = rand(u + vec3(1, 0, 0));
    float c = rand(u + vec3(0, 1, 0));
    float d = rand(u + vec3(1, 1, 0));
    float e = rand(u + vec3(0, 0, 1));
    float f = rand(u + vec3(1, 0, 1));
    float g = rand(u + vec3(0, 1, 1));
    float h = rand(u + vec3(1, 1, 1));

    return mix(mix(mix(a, b, s.x), mix(c, d, s.x), s.y),
               mix(mix(e, f, s.x), mix(g, h, s.x), s.y),
               s.z);
}
// The fbm function.
float fbm(vec3 p) {
    vec3 off = vec3(.175,.4,.0) * T;
    vec3 q = p + off;
    // fbm
    float f = .5 * perlin(q); q *= 2.;
    f += .125 * perlin(q); q *= 2.;
    f += .225 * perlin(q); q *= 2.;
    return clamp(f - p.y, .0, 1.);
}

//@iq shapes
float box( vec3 p, vec3 s ) {
    p = abs(p)-s;
    return length(max(p,0.))+min(max(p.x, max(p.y, p.z)),0.)-.025;
}

float trs( vec3 p,vec2 t){
    vec2 q = vec2(length(p.zx)-t.x,p.y);
    return length(q)-t.y;
}

vec3 hit=vec3(0),hitPoint=vec3(0),gid=vec3(0),sid=vec3(0),speed=vec3(0);
mat2 r90,r35,r25;

const float size = 2.125;
const float hlf = size/2.;

vec2 map(in vec3 p) {
    vec2 res = vec2(1e5,0.);
    p += speed;

    vec2 id = floor((p.xz+hlf)/size);
    vec2 q = mod(p.xz+hlf,size)-hlf;

    float chk = mod(id.y + id.x,2.) * 2. - 1.;

    float hs = hash21(id);
    float hss = hs;
    if(hs>.5) q*=r90;

    hs = perlin(vec3(id,.1)*2.2);

    vec2 d2 = vec2(length(q-hlf), length(q+hlf));
    vec2 gx = d2.x<d2.y ? vec2(q-hlf) : vec2(q+hlf);

    float ff = .15+perlin(vec3(p.xz,T*.7)*.45)*.75;  
    float tk = .35+.25*sin(ff+(p.x*.33+p.z*.21)-T);

    vec3 r = vec3(gx.x,p.y-ff,gx.y);
    vec3 t = vec3(q.x,p.y-ff,q.y);

    float d4 = trs(r,vec2(hlf,tk));

    if(hs>.75) {
        d4 = min(length(t.yz)-tk,length(t.yx)-tk);
    } else if (hs>.5) {
        d4 = min(length(vec3(abs(t.x)-hlf,t.yz))-tk,length(vec3(t.yx,abs(t.z)-hlf))-tk);
    } else if (hs>.25) {
        d4 = min(length(t.yx)-tk, length(vec3(abs(t.x)-hlf,t.yz))-tk);
    }

    if(d4<res.x){
        float rs=fract(hss*432.32);
        res = vec2(d4,rs<.465?3.:4.);
        hit = p; 
        gid = vec3(id,1);
    }

    float fl = p.y+2.;
    if(fl<res.x){
        res = vec2(fl,2.);
        hit = p;
    }
    return res;
}

vec3 normal(vec3 p, float t) {
    float e = MIN_DIST*t;
    vec2 h = vec2(1,-1)*.5773;
    vec3 n = h.xyy * map(p+h.xyy*e).x+
             h.yyx * map(p+h.yyx*e).x+
             h.yxy * map(p+h.yxy*e).x+
             h.xxx * map(p+h.xxx*e).x;
    return normalize(n);
}

vec4 sumColor;
vec2 marcher(vec3 ro, vec3 rd, int steps,  float bnc) {
    float t = .0;
    float m = .0;
    for( int i=0; i<steps; i++ ) {
        vec3 p = ro + rd * t;
        vec2 d = map(p);
        //@42yeah https://www.shadertoy.com/view/wsjfRD 
        // simple volume trace in my march
        float density = fbm(p);
        if (density > 1e-2) {
            vec4 color = vec4(mix(vec3(0), vec3(1), density), density);
            color.w *= .5;
            color.rgb *= color.w;
            sumColor += color * (1.1 - sumColor.a);
        }
        //
        m = d.y;
        if(d.x<MIN_DIST*t||t>MAX_DIST) break;
        t += i<32? d.x*.4 : d.x;
    }
    return vec2(t,m);
}

vec3 hue(float t) {
    return .45 + .35*cos(PI2*t*(vec3(.12,.47,.92)+vec3(.88,.97,.85))); 
}

vec4 FC = vec4(.65,.75,.75,0);
vec4 render(inout vec3 ro, inout vec3 rd, inout vec3 ref, float bnc, inout float d, vec2 uv) {

    vec3 C = vec3(0);
    vec2 ray = marcher(ro,rd,100,bnc);
    hitPoint = hit;  
    sid = gid;
    d = ray.x;
    float m = ray.y;
    float alpha = 0.;

    if(d<MAX_DIST)
    {
        vec3 p = ro + rd * d;
        vec3 n = normal(p,d);
        vec3 lpos =vec3(-5,20,-15);
        vec3 l = normalize(lpos-p);

        vec3 h = vec3(.1);
        vec3 hp = hitPoint*vec3(-1,1,1);

        float diff = clamp(dot(n,l),.1,.95);
        float spec = .75 * pow(max(dot(normalize(p-ro),reflect(normalize(lpos),n)),.1),45.);

        if(m==2.){
            h = vec3(.1);
            vec2 f = fract(hp.xz)-.5;
            if(f.x*f.y>0.) {h = vec3(0); ref = vec3(0);}
        }
        if(m==3.){
            h = hue(((121.-hp.z)*.02)+((321.-hp.x)*.015));
            ref = h*.35;
        }
        if(m==4.){
            h = vec3(clamp(.1+perlin(hp*.12),0.,1.));
            ref = vec3(.1);
        }

        C = (diff*h)+spec;

        ro = p+n*MIN_DIST;
        rd = reflect(rd,n);
    } else {
        C = vec3(1);
    }
    return vec4(C,alpha);
}

void mainImage( out vec4 O, in vec2 F )
{   
    r35=rot(-.685);
    r25=rot(-.685+.2*sin(T*.125));
    r90=rot(1.5707);
    speed = vec3(0,0,T*.55);

    // zoom level 
    float zoom = 10.;
    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);
    vec3 ro = vec3(uv*zoom,-(zoom+5.));
    vec3 rd = vec3(0,0,1);

    ro.yz*=r35,ro.xz*=r25;
    rd.yz*=r35,rd.xz*=r25;

    // reflection loop (@BigWings)
    vec3 C = vec3(0),ref=vec3(0), fil=vec3(1);
    float d = 0.;

    for(float i=0.; i<2.; i++) {
        vec4 pass = render(ro, rd, ref, i, d, uv);
        C += pass.rgb*fil;
        fil*=ref;
    }

    C += sumColor.rgb;
    C = mix(C,clamp(C*.75,vec3(0),vec3(1)),hash21(uv+2.));
    C = pow(C, vec3(.4545));
    O = vec4(C,1);
}

//
// SDFs
//
float dot2( in vec2 v ) {
  return dot(v,v);
}

float inverseLerp(float v, float minValue, float maxValue) {
  return (v - minValue) / (maxValue - minValue);
}

float remap(float v, float inMin, float inMax, float outMin, float outMax) {
  float t = inverseLerp(v, inMin, inMax);
  return mix(outMin, outMax, t);
}

float ndot(vec2 a, vec2 b ) { return a.x*b.x - a.y*b.y; }

mat2 rotate2D(float angle) {
  float s = sin(angle);
  float c = cos(angle);
  return mat2(c, -s, s, c);
}


// All SDF code from the amazing Inigo Quilez
// https://iquilezles.org/articles/distfunctions2d/
float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdRhombus( in vec2 p, in vec2 b ) 
{
    p = abs(p);
    float h = clamp( ndot(b-2.0*p,b)/dot(b,b), -1.0, 1.0 );
    float d = length( p-0.5*b*vec2(1.0-h,1.0+h) );
    return d * sign( p.x*b.y + p.y*b.x - b.x*b.y );
}


float sdEquilateralTriangle( in vec2 p, in float size ) {
  const float k = sqrt(3.0);
  p.x = abs(p.x) - size;
  p.y = p.y + 1.0/k;
  if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
  p.x -= clamp( p.x, -2.0, 0.0 );
  return -length(p)*sign(p.y);
}

float sdPentagon( in vec2 p, in float r )
{
    const vec3 k = vec3(0.809016994,0.587785252,0.726542528);
    p.x = abs(p.x);
    p -= 2.0*min(dot(vec2(-k.x,k.y),p),0.0)*vec2(-k.x,k.y);
    p -= 2.0*min(dot(vec2( k.x,k.y),p),0.0)*vec2( k.x,k.y);
    p -= vec2(clamp(p.x,-r*k.z,r*k.z),r);    
    return length(p)*sign(p.y);
}

float sdHexagon( in vec2 p, in float r )
{
    const vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}

float sdStar5(in vec2 p, in float r, in float rf)
{
    const vec2 k1 = vec2(0.809016994375, -0.587785252292);
    const vec2 k2 = vec2(-k1.x,k1.y);
    p.x = abs(p.x);
    p -= 2.0*max(dot(k1,p),0.0)*k1;
    p -= 2.0*max(dot(k2,p),0.0)*k2;
    p.x = abs(p.x);
    p.y -= r;
    vec2 ba = rf*vec2(-k1.y,k1.x) - vec2(0,1);
    float h = clamp( dot(p,ba)/dot(ba,ba), 0.0, r );
    return length(p-ba*h) * sign(p.y*ba.x-p.x*ba.y);
}

float sdHeart( in vec2 p )
{
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
        return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

float sdRoundedX( in vec2 p, in float w, in float r )
{
    p = abs(p);
    return length(p-min(p.x+p.y,w)*0.5) - r;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

  // Position
  // Move
  //vec2 pos = p - vec2(cos(iTime * 0.5) * 0.9, sin(iTime * 0.5) * 0.5);

  // Rotate
  //pos *= rotate2D(iTime * 0.25);  

  // scale
  //float d = sdEquilateralTriangle(pos, remap(sin(iTime * 0.5), -1.0, 1.0, 0.2, 0.9));

  // SDFs
  float d = 0.0;
  int shape = int(mod(iTime, 9.0));
  switch (shape) {
    case 0:
      d += sdCircle(p, 0.5);
      break;

    case 1:
      d += sdBox(p, vec2(1.0, 0.5));
      break;

    case 2:
      d += sdRhombus(p, vec2(1.0, 0.5));
      break;

    case 3:
      d += sdEquilateralTriangle(p, 0.5);
      break;

    case 4:
      d += sdPentagon(p, 0.6);
      break;

    case 5:
      d += sdHexagon(p, 0.6);
      break;

    case 6:
      d += sdStar5(p, 0.75, 0.5);
      break;

    case 7:
      d += sdHeart(vec2(p.x, p.y + 0.5));
      break;

    case 8:
      d += sdRoundedX(p, 0.75, 0.2);
      break;
  }

  // coloring
  vec3 col;
  if (d>0.0) {
    col = vec3(0.15);
    col *= 0.8 + 0.937 * cos(50.0 * d + iTime * -10.0);
  } else {
    col = vec3(0.937);
  }

  col *= 2.0 - exp(-6.0*abs(d));

  fragColor = vec4(col,1.0);
}

// "Reflecting Dynamic Block Grid" by dr2 - 2023
// License: Creative Commons Attribution-NonCommercial-ShareAlike 4.0

// [Based on "Dynamic Block Grid" (sdsBDH)]

#define AA  0   // (= 0/1) optional antialiasing

#define VAR_ZERO min (nFrame, 0)

float PrRound4BoxDf (vec3 p, vec3 b, float r);
float PrRound4Box2Df (vec2 p, vec2 b, float r);
float Maxv3 (vec3 p);
float Minv2 (vec2 p);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec3 HsvToRgb (vec3 c);
float Hashfv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 qHit, bsHit, ltDir;
vec2 pMid[4], pEdge[4], qcMin, ip;
float tCur, dstFar, eFac, eRound;
int nFrame;
const float pi = 3.1415927;

#define H(z) (0.5 + 0.5 * cos (tCur * (0.1 + 0.9 * Hashfv2 (ip + z))))

void CellParms ()
{
  vec4 hm, hc;
  vec3 e;
  vec2 ee[4];
  float hp;
  e = vec3 (-1., 0., 1.);
  ee[0] = e.xz;
  ee[1] = e.zz;
  ee[2] = e.zx;
  ee[3] = e.xx;
  hp = H(0.);
  hm = vec4 (H(e.zy), H(e.xy), H(e.yz), H(e.yx));
  hc = vec4 (H(e.zz), H(e.xx), H(e.xz), H(e.zx));
  if (mod (ip.x + ip.y, 2.) < 0.5) {
    pEdge[0] = vec2 (hm.z - hm.y, hc.z - hp);
    pEdge[1] = vec2 (hm.x - hm.z, hc.x - hp);
    pEdge[2] = vec2 (hm.x - hm.w, hp - hc.w);
    pEdge[3] = vec2 (hm.w - hm.y, hp - hc.y);
    pMid[0] = vec2 (hm.z, hp);
    pMid[1] = pMid[0];
    pMid[2] = vec2 (hm.w, hp);
    pMid[3] = pMid[2];
  } else {
    pEdge[0] = vec2 (hp - hc.z, hm.z - hm.y);
    pEdge[1] = vec2 (hc.x - hp, hm.z - hm.x);
    pEdge[2] = vec2 (hc.w - hp, hm.x - hm.w);
    pEdge[3] = vec2 (hp - hc.y, hm.y - hm.w);
    pMid[0] = vec2 (hp, hm.y);
    pMid[1] = vec2 (hp, hm.x);
    pMid[2] = pMid[1];
    pMid[3] = pMid[0];
  }
  for (int k = 0; k < 4; k ++) {
    pEdge[k] = eFac * pEdge[k] + 0.5;
    pMid[k] = 2. * eFac * (pMid[k] - 0.5) + pEdge[k] * ee[k];
  }
}

float ObjDf (vec3 p)
{
  vec3 q, bs;
  vec2 qc;
  float dMin, d;
  dMin = dstFar;
  for (int k = VAR_ZERO; k < 4; k ++) {
    qc = ip + pMid[k];
    q.xz = p.xz - qc;
    qc = floor (qc);
    bs.xz = pEdge[k] - eFac + 0.05;
    bs.y = 0.8 * (bs.x + bs.z) + 0.2 * Hashfv2 (qc);
    q.y = p.y - bs.y;
    d = PrRound4BoxDf (q, bs - eRound, eRound);
    if (d < dMin) {
      dMin = d;
      qcMin = qc;
      qHit = q;
      bsHit = bs;
    }
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p, rdi;
  vec2 fp, ipP;
  float dHit, d, eps;
  eps = 0.0005;
  if (rd.x == 0.) rd.x = 0.0001;
  if (rd.z == 0.) rd.z = 0.0001;
  rdi.xz = 1. / rd.xz;
  dHit = eps;
  ipP = vec2 (0.5);
  for (int j = VAR_ZERO; j < 120; j ++) {
    p = ro + dHit * rd;
    fp = p.xz + 0.5;
    ip = floor (fp);
    if (ip != ipP) {
      ipP = ip;
      CellParms ();
    }
    d = ObjDf (p);
    dHit += min (d, eps + max (0., Minv2 ((ip + step (0., rd.xz) - fp) * rdi.xz)));
    if (d < eps || dHit > dstFar || p.y < 0.) break;
  }
  if (d > eps || p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0001, -0.0001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 ipP;
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  ipP = vec2 (0.5);
  for (int j = VAR_ZERO; j < 32; j ++) {
    p = ro + d * rd;
    ip = floor (p.xz + 0.5);
    if (ip != ipP) {
      ipP = ip;
      CellParms ();
    }
    h = ObjDf (p);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += max (h, 0.02);
    if (h < 0.001 || d > dstFar) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 SkyCol (vec3 rd)
{
  rd.y = abs (rd.y);
  return mix (vec3 (0.3, 0.35, 0.7), vec3 (0.8, 0.8, 0.8),
     clamp (2. * (Fbm2 (8. * rd.xz / rd.y + 0.1 * tCur) - 0.1) * rd.y, 0., 1.));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  vec2 q, qc, b;
  float dstObj, dstGrnd, sh, s, ss, reflFac;
  const int nRefl = 9;
  eFac = 0.2;
  eRound = 0.2;
  reflFac = 1.;
  for (int k = VAR_ZERO; k < nRefl; k ++) {
    dstObj = ObjRay (ro, rd);
    if (k < nRefl - 1 && dstObj < dstFar && Maxv3 (abs (qHit) - bsHit) > -0.005) {
      ro += dstObj * rd;
      rd = reflect (rd, ObjNf (ro));
      ro += 0.01 * rd;
      reflFac *= 0.85;
    } else break;
  }
  dstGrnd = dstFar;
  if (dstObj < dstFar || rd.y < 0.) {
    if (dstObj < dstFar) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      col4 = vec4 (HsvToRgb (vec3 (Hashfv2 (qcMin), 0.8, 1.)), 0.1);
      s = 1.;
    } else {
      dstGrnd = - ro.y / rd.y;
      ro += dstGrnd * rd;
      vn = VaryNf (16. * ro, vec3 (0., 1., 0.), 0.5);
      ip = floor (ro.xz + 0.5);
      CellParms ();
      s = 1.;
      for (int k = VAR_ZERO; k < 4; k ++) {
        q = ro.xz - (ip + pMid[k]);
        b = pEdge[k] - eFac + 0.1;
        ss = PrRound4Box2Df (q, b - eRound, eRound);
        if (ss < s) {
          s = ss;
          qc = floor (ip + pMid[k]);
        }
      }
      col4 = vec4 (mix (HsvToRgb (vec3 (Hashfv2 (qc), 0.8, 1.)), vec3 (0.6),
         smoothstep (0.02, 0.03, abs (s))), 0.1);
      s = 0.5 + 0.5 * smoothstep (-0.03, -0.01, s);
    }
    sh = (dstGrnd < min (dstObj, dstFar)) ? min (s, ObjSShadow (ro + 0.01 * vn, ltDir)) : 1.;
    col = col4.rgb * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.)) +
       col4.a * step (0.95, sh) * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
  } else {
    col = SkyCol (rd);
  }
  col *= reflFac;
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  nFrame = iFrame;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  tCur += 1.;
  az = 0.;
  el = -0.25 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.6 * pi * mPtr.y;
  }
  el = clamp (el, -0.45 * pi, -0.15 * pi);
  ltDir = normalize (vec3 (0.7, 2., -1.));
  vuMat = StdVuMat (el, az);
  ro.xz = - vec2 (0.06, 0.15) * tCur;
  ro.y = 5.;
  zmFac = 4.;
  dstFar = 80.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  col = vec3 (0.);
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrRound4BoxDf (vec3 p, vec3 b, float r)
{
  p = max (abs (p) - b, 0.);
  return sqrt (length (p * p)) - r;
}

float PrRound4Box2Df (vec2 p, vec2 b, float r)
{
  p = max (abs (p) - b, 0.);
  return sqrt (length (p * p)) - r;
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

