#define CS(a)  vec2(cos(a),sin(a))
//#define rnd(x) ( 2.* fract(1456.68*sin(1e4*(x+fract(iDate.w)))) -1.) // NB: mod(t,1.) for less packed pattern
#define rnd(x) ( 2.* fract(1456.68*sin(dot(vec2(x,fract(iDate.w)),R+71.))) -1.) // NB: mod(t,1.) for less packed pattern

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    float r = 1.5, N = min(500.,R.x); // width , number of worms

    //if (T(R).x==0.) { U = abs(U/R*2.-1.); O  = vec4(max(U.x,U.y)>1.-r/R.y); O.w=0.; return; }

    if (U.y==.5 && T(U).w==0.) {                           // initialize heads state: P, a, t
        vec4 P = T(vec2(.5+(.5+.5*rnd(U.x+.1))*N,.5)); // parallel to an existing one
        if (P.w < 3.) {
            if(rnd(U.x+.3)<.1) { O = vec4( R/2. + R/2.4* vec2(rnd(U.x),rnd(U.x+.1)) , 3.14 * rnd(U.x+.2), 1); if (T(O.xy).w>0.) O.w = 0.; return; }
            return;
        }
        float a = P.z;
        O = vec4( P.xy+(r+2.)*CS(a+1.57*sign(rnd(U.x))), a, 1);
        if (T(O.xy).w>0.) O.w = 0.;                        // invalid start position
        return;
    }

    O = T(U);
//  vec2 M = iMouse.xy; if (length(M)>0.) O += smoothstep(r,0., length(M-U));

    for (float x=.5; x<N; x++) {                           // draw heads
        vec4 P = T(vec2(x,.5));                            // head state: P, a, t
        if (P.w>0.) O += smoothstep(r,0., length(P.xy-U))  // draw head if active
                         * .2*vec4(.01*P.w,1,0,1);          // coloring scheme
                     // *.5*(.5+.5*sin(6.3*x/N+vec4(0,-2.1,2.1,1)));   // coloring scheme
    }

    if (U.y==.5) {                                         // head programms: worm strategy
        vec4 P = T(U);                                     // head state: P, a, t
        if (P.w>0.) {                                      // if active
            vec2 A = abs(P.xy-R/2.)-R/2.; if ( max(A.x,A.y) > 0. ) { O.w = 0.; return; }  // out: stop head
            float a = P.z;
            if ( T(P.xy+(r+2.)*CS(a)).w > 0. ) { O.w = 0.; return; }  // hit: stop head
            if ( T(P.xy+(r+1.)*CS(a)).w > 0. ) { O.w = 0.; return; }  // hit: stop head
            for (float x=U.x+1.; x<N; x++) 
                if( length(P.xy-T(vec2(x,.5)).xy)<1.)  { O.w = 0.; return; }  // hit: stop head

            a += .08*2.*rnd(U.x);// .08*rnd(P.x) + .02;
            O = vec4(P.xy+1.*CS(a),mod(a,6.2832),P.w+1.);     // move head
        }
    }
}

