#define res iResolution.xy
#define pi 3.14159
#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define ths(a,b) tanh(a*sin(b))/tanh(a)
#define Dir(a) vec2(cos(a),sin(a))
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define ss(a) smoothstep(k, -k, a)
#define eps 1e-4

// https://iquilezles.org/articles/distfunctions2d/
float seg(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 O, in vec2 px )
{
    float k = 1. / res.y;
    vec2 uv = (px - 0.5 * res) * k;
    vec2 ouv = uv;

    float t = 1.8 * iTime;   
    uv *= rot(0.06*pi*cos(t));

    float r = 0.2 - 0.02 * cos(0.5*t);
    uv.y += r;

    vec2 pHead = vec2(0, mix(1.2, 1.5, .5-.5*cos(2.*t)) * r);

    float a = acos(pHead.y / r - 1.);  
    float theta = (a-eps)*thc(2., 0.5 * uv.x + t) - pi/2.;
    vec2 pHip = pHead + r * Dir(theta);
    pHip.y += (r-pHip.y) * sin(2.*t);
    vec2 pLegL = vec2(pHip.x + sqrt(r*r-pHip.y*pHip.y), 0);
    vec2 pLegR = vec2(pHip.x - sqrt(r*r-pHip.y*pHip.y), 0);

    vec2 dir = normalize(pHead-pHip);
    float a2 = atan(dir.y, dir.x);
    pHead = pHip + r * dir;
    vec2 pBody = mix(pHead, pHip, 0.3);
    vec2 pArmL = pBody - 0.65 * r * Dir(a2 - pi/2. - sin(1.5*t) + .1*cos(2.*t));
    vec2 pArmR = pBody + 0.65 * r * Dir(a2 - pi/2. - sin(1.5*t) - .1*cos(2.*t));
    vec2 pFloorL = vec2(-2. * r, -0.02);
    vec2 pFloorR = vec2( 2. * r, -0.02);

    float dHead     = length(uv - pHead)        - 0.03;
    float dHeadHip  = seg(uv, pHead, pHip)      - 0.01;
    float dHipLegL  = seg(uv, pHip, pLegL)      - 0.01;
    float dHipLegR  = seg(uv, pHip, pLegR)      - 0.01;
    float dBodyArmL = seg(uv, pBody, pArmL)     - 0.01;
    float dBodyArmR = seg(uv, pBody, pArmR)     - 0.01;
    float dFloor    = seg(uv, pFloorL, pFloorR) - 0.01;
    float d = min(min(dHead, dHeadHip), min(dHipLegL, dHipLegR));    
    d = min(min(d, dFloor), min(dBodyArmL, dBodyArmR));

    float dCircle = length(ouv) - (0.23 - 0.231 * thc(5., 0.5 * t));
    float sCircle = ss(-dCircle);

    float xp = exp(-20.*sqrt(abs(d)));
    float s = max(ss(d), xp);
    s = 1.- abs(s - sCircle);
    vec3 col = vec3(1.-s);
    vec3 tx1 = texture(iChannel1, ouv).rgb;
    vec3 tx2 = texture(iChannel2, vec3(ouv, exp(-0.5*s*s))).rgb;

    col = mix(2.*(1.-tx2)*sqrt(s)*tx1, col, 1.-s*s);
    O = vec4(col, 1.0);
}

/*
Wythoff reflective polyhedra, code adapted from

    https://www.shadertoy.com/view/tlX3WH

I basically rewrote the polyhera construction and raytracing part there,
and kept the rendering part untouched.

The code should look simpler now?

To play with:

1. Change pqr and truncation_type to see other polyhedron.
2. use 'INSIDE_VIEW' to view from the inside.
3. use 'DUAL' to view the dual polyhera.
   pqr=(2, 3, 4) and truncation_type=(0, 0, 1) will give the
   rhombic dodecahedron that tiles the whole inner space.
*/
#define PI                3.141592654
#define L2(x)             dot(x, x)
#define EDGE_THICKNESS    0.05
#define MAX_TRACE_STEPS   128
#define MAX_RAY_BOUNCES   12
#define EPSILON           1e-4
#define FAR               20.
#define min3(x, y, z)     (min(x, min(y, z)))
#define max3(x, y, z)     (max(x, max(y, z)))


//#define INSIDE_VIEW

//#define DUAL

const vec3 pqr = vec3(2, 3, 5);   // (2, 3, 3), (2, 3, 4)
const vec3 truncation_type = vec3(1, 1, 1);

const float size = 1.35;  // polyhera size

mat3 M;  // normals of reflection mirrors
mat3 T;  // three vertices of the fundamental triangle  
vec3 v0; // initial vertex

mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

void init() {
    vec3 c = cos(PI / pqr);
    float sp = sin(PI / pqr.x);
    vec3 m1 = vec3(1, 0, 0);
    vec3 m2 = vec3(-c.x, sp, 0);
    float x3 = -c.z;
    float y3 = -(c.y + c.x*c.z)/sp;
    float z3 = sqrt(1.0 - x3*x3 - y3*y3);
    vec3 m3 = vec3(x3, y3, z3);
    M = mat3(m1, m2, m3);
    T[0] = normalize(cross(m2, m3));
    T[1] = normalize(cross(m3, m1));
    T[2] = normalize(cross(m1, m2));
    v0 = normalize(truncation_type * inverse(M)) * size;
#ifdef DUAL
    #define proj(p, n)  (length(p - dot(p, n) * n))
    float scale = min3(proj(v0, m1), proj(v0, m2), proj(v0, m3));
    v0 /= scale;
#endif
}

vec3 fold(vec3 p) {
    for (int i = 0; i < 5; i++)
    for (int j = 0; j < 3; j++) {
        p -= 2. * min(dot(p, M[j]), 0.) * M[j];
    }
    return p;
}

float dSegment(vec3 p, vec3 a, vec3 b) {
  vec3 pa = p - a;
  vec3 ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
  float d = length(pa - ba * h);
  return d;
}

vec3 distEdges(vec3 p) {
    p = fold(p);
#ifdef DUAL
	vec3 ed = vec3(1e5);
    vec3 a = T[0] / dot(T[0], v0) * size;
    vec3 b = T[1] / dot(T[1], v0) * size;
    vec3 c = T[2] / dot(T[2], v0) * size;
	if(bool(truncation_type.x))
        ed.x = dSegment(p, b, c);
	if(bool(truncation_type.y))
        ed.y = dSegment(p, c, a) ;
	if(bool(truncation_type.z))
        ed.z = dSegment(p, a, b);
	return ed;
#else
    p -= v0;
    vec3 ed;
    for (int i = 0; i < 3; i++) {
        ed[i] = L2(p - min(0., dot(p, M[i])) * M[i]);
    }
	return sqrt(ed);
#endif
}

float map(vec3 p) {
    p = fold(p);
#ifdef DUAL
    return dot(p, v0) - size;
#else
    p -= v0;
    return max(dot(p, T[0]), max(dot(p, T[1]), dot(p, T[2])));
#endif
}

float trace(vec3 pos, vec3 rd, bool outside) {
    float t = 0.0;
    float sgn = outside ? 1.0 : -1.0;
    for (int i = 0; i < MAX_TRACE_STEPS; i++) {
        float d = map(pos + t * rd);
        if (abs(d) < EPSILON)
            return t;
        if (t > FAR)
            break;
        t += sgn * d  * 0.9;
    }
    return FAR;
}

vec4 wallColor(vec3 dir, vec3 nor, vec3 eds) {
    float d = min3(eds.x, eds.y, eds.z);

    // Texturing of walls
    vec3 albedo = texture(iChannel1, vec2(eds.xy * 2.0)).rgb;
    albedo = pow(albedo, vec3(2.2)) * 0.5;

    // Simple diffuse lighting
    float lighting = 0.2 + max(dot(nor, vec3(0.8, 0.5, 0.0)), 0.0);

    if (dot(dir, nor) < 0.0) {
        // Outer walls, just add a black line to hide seams
        float f = clamp(d * 1000.0 - 3.0, 0.0, 1.0);
        albedo = mix(vec3(0.01), albedo, f);
        return vec4(albedo * lighting, f);
    } else {
        // Inner walls, add fancy lights
        float m = max3(eds.x, eds.y, eds.z);
        vec2 a = fract(vec2(d, m) * 40.6) - 0.5;
        float b = 1.0 - sqrt(dot(a, a));
        b = 0.2 / (dot(a, a) + 0.2);

        float lightShape = 1.0 - clamp(d * 100.0 - 2.0, 0.0, 1.0);
        lightShape *= b;

        vec3 emissive = vec3(3.5, 1.8, 1.0);
        return vec4(mix(albedo * lighting, emissive, lightShape), 0.0);
    }
	return vec4(1.0);
}


mat3 camera_matrix(vec3 eye, vec3 lookat, vec3 up) {
    vec3 forward = normalize(lookat - eye);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));
    return mat3(right, up, -forward);
}

vec3 get_normal( in vec3 pos ) {
    vec3 eps = vec3(0.001,0.0,0.0);
	return normalize( vec3(
           map(pos+eps.xyy) - map(pos-eps.xyy),
           map(pos+eps.yxy) - map(pos-eps.yxy),
           map(pos+eps.yyx) - map(pos-eps.yyx) ) );
}

vec3 background(vec3 dir) {
	vec3 col = texture(iChannel0, dir).rgb;
    col = pow(col, vec3(2.2));
    float origLuma = dot(col, vec3(0.2126, 0.7152, 0.0722)) * 0.7;
    return 2.5 * col / (1.0 - origLuma);
}

vec3 drawRay(vec3 pos, vec3 rd) {
    vec3 color = vec3(0.0);
#ifndef INSIDE_VIEW
    float t = trace(pos, rd, true);
    if (t == FAR) {
    	return background(rd);
    }
    pos = pos + t * rd;
    vec3 nor = get_normal(pos);
    vec3 reflDir = reflect(rd, nor);
    vec3 bgColor = pow(background(reflDir), vec3(1.0));
    float fresnel = 0.04 + 0.96 * pow(1.0 - max(dot(rd, -nor), 0.0), 5.0);
    color += bgColor * fresnel;
    vec3 eds = distEdges(pos);
    float d = min3(eds.x, eds.y, eds.z);
    if (d < EDGE_THICKNESS) {
        vec4 wc = wallColor(rd, nor, eds);
    	return color * wc.a + wc.rgb;
    }
#endif
    vec3 transmittance = vec3(1.0);
    for (int i = 0; i < MAX_RAY_BOUNCES; i++) {
        float t = trace(pos, rd, false);
        pos = pos + t * rd;
        vec3 eds = distEdges(pos);
        vec3 nor = get_normal(pos);
    	float d = min3(eds.x, eds.y, eds.z);
        if (d < EDGE_THICKNESS) {
        	return color + transmittance * wallColor(rd, nor, eds).rgb;
        }
        rd = reflect(rd, nor);
        pos += rd * 0.005;
        transmittance *= vec3(0.4, 0.7, 0.7);
    }

    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    init();
    vec2 uv = (fragCoord.xy - iResolution.xy*0.5) / iResolution.y;
    vec2 move = vec2(iTime * 0.2, sin(iTime * 0.2) * 0.5);

#ifdef INSIDE_VIEW
    float radius = 0.1;
#else
    float radius = 2.0;
#endif
    vec3 eye = radius * vec3(
        cos(move.x)*cos(move.y),
        sin(move.y),
        sin(move.x)*cos(move.y)
    );

    vec2 mouse = vec2(0);
    if (iMouse.x > 0.)
        mouse = 2. * iMouse.xy / iResolution.y - 1.;
    eye = rotateY(mouse.x) * rotateX(-mouse.y) * eye;

    vec3 lookat = vec3(0);
    vec3 up = vec3(0, 1, 0);
    vec3 forward = normalize(lookat - eye);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));
    vec3 ray = normalize(uv.x*right + uv.y*up + forward * 1.0);
    vec3 color = drawRay(eye, ray);
    color = color / (color * 0.5 + 0.5);
    color = pow(color, vec3(1.0 / 2.2));

    fragColor = vec4(color,1.0);
}

mat2 rot(float angle){
    float c= cos(angle);
    float s = sin(angle);
    return mat2(c,-s,s,c);
}
float map(vec3 p){
    return length(p)-1.0;
}

float Raymarch(vec3 ro,vec3 rd){
    float d;
    for(int i =0;i<255;i++){
        vec3 p =ro+rd*d;
        float d0 = map(p);
        if(d0<=0.001||d>=40.) break;
        d+=d0;
    }
    return d;
}
vec3 GetNormal(vec3 p){
    float d = map(p);
    vec2 e = vec2(0.001,0.0);
    float dx = d-map(p-e.xyy);
    float dy = d-map(p-e.yxy);
    float dz = d-map(p-e.yyx);
    return normalize(vec3(dx,dy,dz));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;    
    uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;
    vec3 col = vec3(0.0);

    vec3 ro = vec3(0.0,0.0,-3);
    vec3 rd  = normalize(vec3(uv,1.0));

    float d= Raymarch(ro,rd);
    if(d<40.0){
        vec3 p = ro+rd*d;
        vec3 n  = GetNormal(p);
        vec3 lightPos = vec3(0,5,-5.0); 
        lightPos.xz*=rot(iTime);    
        vec3 lightdir = normalize(lightPos-p);
        float diffuse = dot(n,lightdir);
        diffuse =diffuse*.5+.5;
        vec3 color = vec3(1.0,1.0,1.0);
        col+=diffuse*color;
    }


    fragColor = vec4(col,1.0);
}

// Apollonian Britney, mla, 2023
//
// Modification of @fizzer's original https://www.shadertoy.com/view/WtdSDf
// See mainImage around line 210 for limit circle uv calculation.
// Also made a few mods to the code, eg. only invert combined transformation
// at the end & a different way of handling a division by zero.

const float pi = acos(-1.);
const int numCircles = 4; // Set this to 4, for the classic gasket.

// For more on Mbius transformations see: 
// https://en.wikipedia.org/wiki/M%C3%B6bius_transformation
// https://en.wikipedia.org/wiki/Indra%27s_Pearls_(book)

// Transformations are represented by 2x2 complex matrices, mat4x2(a,b,c,d):
// with z -> (az+b)/(cz+d), so multiplying a matrix by complex w represents
// the same transformation. Inverse transformation is just (any multiple of)
// the inverse matrix.
const mat4x2 midentity = mat4x2(vec2(1, 0),
                                vec2(0, 0),
                                vec2(0, 0),
                                vec2(1, 0));

// Apply transformation m to point x
vec2 cMobius(mat4x2 m, vec2 x)
{
    return cDiv(cMul(x, m[0]) + m[1], cMul(x, m[2]) + m[3]);
}

// Matrix multiplication, in fact.
mat4x2 cMobiusConcat(mat4x2 ma, mat4x2 mb)
{
    return mat4x2(cMul(ma[0], mb[0]) + cMul(ma[1], mb[2]),
                  cMul(ma[0], mb[1]) + cMul(ma[1], mb[3]),
                  cMul(ma[2], mb[0]) + cMul(ma[3], mb[2]),
                  cMul(ma[2], mb[1]) + cMul(ma[3], mb[3]));
}

// Matrix inverse, but no need to divide by determinant.
mat4x2 cMobiusInverse(mat4x2 m)
{
    return mat4x2(m[3], -m[1], -m[2], m[0]);
}

// Apply transformation T to circle C
vec3 cMobiusOnCircle(mat4x2 T, vec3 C)
{
    vec3 D;
    vec2 z = C.xy;
    // Handle zero division.
    if (T[2] != vec2(0)) z -= cDiv(vec2(C.z * C.z, 0), cConj(cDiv(T[3], T[2]) + C.xy));
    D.xy = cMobius(T,z).xy;
    D.z = length(D.xy - cMobius(T,C.xy + vec2(C.z, 0)));
    return D;
}

// These are the N inversion circles necessary to produce the fractal which is the limit
// set of a Kleinian group. The Mbius transformations which represent inversions in these circles
// are the generators of the Kleinian group.
vec3 getInversionCircle(int i)
{
    float theta = pi / float(numCircles - 1);
    float r0 = tan(theta);
    float r1 = 1. / cos(theta);
    if(i == numCircles - 1) return vec3(0, 0, r1 - r0); // Central circle
    return vec3(cos(float(i) * theta * 2.) * r1, sin(float(i) * theta * 2.) * r1, r0);
}

// These are the N circles which are the minimum set of circles which all of the other circles
// in the limit set are images of. There are N-1 circles in a circular chain of tangency, and
// one circle surrounding the gasket with radius 1.
vec3 getLimitCircle(int i)
{
    float theta = pi / float(numCircles - 1);
    float r0 = tan(theta);
    float r1 = 1. / cos(theta);
    float r = (r1 - r0) * tan(theta);
    float r2 = (r1 - r0) / cos(theta);
    if(i == numCircles - 1) return vec3(0, 0, 1);
    return vec3(-cos((float(i)) * theta * 2.) * r2, sin((float(i)) * theta * 2.) * r2, r);
}

vec3 getInnerLimitCircle()
{
    float theta = pi / float(numCircles - 1);
    float r0 = tan(theta);
    float r1 = 1. / cos(theta);
    float r = (r1 - r0) * tan(theta);
    float r2 = (r1 - r0) / cos(theta);
    return vec3(0., 0., r2 - r);
}

mat4x2 makeMobiusForInversionInCircle(vec3 ic)
{
    mat4x2 ma = mat4x2(vec2(1. / ic.z, 0),
                       vec2(-ic.x / ic.z, ic.y / ic.z),
                       vec2(0, 0),
                       vec2(1, 0));

    mat4x2 mb = mat4x2(vec2(ic.x, ic.y),
                       vec2(ic.z, 0),
                       vec2(1, 0),
                       vec2(0, 0));

    return cMobiusConcat(mb, ma);
}

vec3 getcol(int i) {
  // Colors for limit circles. For consistent colouring
  // we have 1=4 and 2=3.
  if (i == 0) return vec3(0,0,1);
  if (i == 1) return vec3(1,1,0);
  if (i == 2) return vec3(1,0,0);
  if (i == 3) return vec3(0,1,0);
  if (i == 4) return vec3(1,1,0);
  if (i == 5) return vec3(1,0,0);
  return vec3(1);
}

vec3 getcol(int i, int parity) {
  if (i == 0) return vec3(1,0,0);
  if (i == 1 && parity == 0 || i == 2 && parity == 1) return vec3(0,1,0);
  if (i == 1 && parity == 1 || i == 2 && parity == 0) return vec3(0,0,1);
  return vec3(1,1,0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x / iResolution.y;

    float scale = 1.;

    fragColor = vec4(0, 0, 0, 1);


    vec2 z = vec2(p.x, p.y);

    mat4x2 mi = midentity;

    if(iMouse.x > .5)
    {
        // This mouse interaction is based on mla's shader: https://www.shadertoy.com/view/MlVfzy
        // [mla: actually there is a better way to do this]
        mat4x2 m = midentity;
        vec2 mo = (2.0 * iMouse.xy / iResolution.y - vec2(iResolution.x / iResolution.y, 1.0)) * vec2(1, -1);

        if(length(mo) < 0.999)
        {
            vec2 c = mo * (1.0 / dot(mo, mo));
            vec3 ic = vec3(c, sqrt(dot(c, c) - 1.));

            m = makeMobiusForInversionInCircle(ic);
        }


        mi = cMobiusConcat(m,mi);

        z = cMobius( m,z );
    }

    // Rotate the gasket *after* the mouse position inversion.

    float t = iTime * .4;
    mat4x2 mrotation = mat4x2(vec2(cos(t), sin(t)),
                              vec2(0, 0),
                              vec2(0, 0),
                              vec2(1, 0));

    mi = cMobiusConcat(mrotation,mi);
    z = cMobius( mrotation, z );
    int parity = 0;

    for(int i = 0; i < 65; ++i)
    {
    parity = i%2;
        int k = -1;

        mat4x2 m;
        vec3 ic;

        for(int j = 0; j < numCircles; ++j)
        {
            vec3 c = getInversionCircle(j);

            if(distance(z.xy, c.xy * vec2(1, -1)) < c.z)// &&i%2==0)
            {
                k = j;
                ic = c;
                break;
            }
        }

        if(k == -1) break;

        ic.x += 1e-9;

        m = makeMobiusForInversionInCircle(ic);

        mi = cMobiusConcat(m,mi);

        z = cMobius( m, z );
    }

    mi = cMobiusInverse(mi); // Get inverse transformation

    vec3 col = vec3(0);

    float px = fwidth(p.x);
    for(int i = 0; i < numCircles; ++i)
    {
        vec3 C = getLimitCircle(i);
        vec3 D = cMobiusOnCircle(mi, C);

        if(D.z > 1e-9 && D.z < 1. && abs(D.z - 1.) > .01)
        {
            vec2 v = p.xy - D.xy;

            if(length(v) < D.z) {
               // mla part
               vec2 uv = 0.5*v/D.z;
               //col = texture(iChannel0,uv+0.5).rgb;
               //col = pow(col,vec3(2.2)); // I like the washed out effect of not doing this
               col = getcol(i,parity);
               float twidth = 0.02;
               col = mix(col,texture(iChannel0,uv+0.5).rgb,vec3(smoothstep(0.0,px,0.5-length(uv)-twidth)));
               //col = getcol(i,parity);
               col *= smoothstep(0.0,px,D.z-length(v));
               col *= smoothstep(0.0,px,abs(D.z-length(v)-2.0*D.z*twidth));
               break;
            }
        }
    }
    col = pow(col, vec3(1. / 2.2));
    fragColor = vec4(col,1);
}

