// Complex arithmetic
vec2 cMul(vec2 a, vec2 b)
{
    return vec2(a.x * b.x - a.y * b.y, 
                a.x * b.y + a.y * b.x);
}

vec2 cConj(vec2 a)
{
    return vec2(a.x, -a.y);
}

vec2 cInverse(vec2 a)
{
    return cConj(a) / dot(a,a);
}

vec2 cDiv( vec2 a, vec2 b)
{
    return cMul(a, cInverse(b));
}

