vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(fract((a1)*(a2.x+a2.y))-.5)
    ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = .5;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec2 t2 = vec2(0.);
    bool b = true;
    uv.y = mod(uv.y,.5)+.5;
    uv.x = mod(uv.x,2.)+1.;
    for(int k = 0; k < 6; k++){
        uv =
            abs(.5+uv+t2*sign(uv.y-uv.x))/1.5
        ;
        b = uv.y<uv.x;
        if(b) uv = uv.yx;
        //else k += 2;
        t2 =
            -triangle_wave(uv-.5).yx*sign(uv.x-uv.y)
        ;
        uv =
            t2-triangle_wave(uv.yx).yx
        ;
        //{uv=uv*sign(uv.y-uv.x);}

        //remove this line to make it gray
        col.x = min(sign(uv.x-t2.y),col.x);

        col =
            abs(col.yzx-float(b)/2.)
        ;
    }
    if (!b) col = 1.-col;
    fragColor = vec4(col,1.0);
}

