//remix of https://www.shadertoy.com/view/DdyXz3#
float fmod(float a, float b){
    for(float i = 1.; i < mod(floor(a/b),4.); i++){
        a += mod(floor(a/2.),b)*i;
        a *= 1.+mod(floor(a/2.),2.);
    }
    return
        mod(floor(a),b)
    ;
}

vec2 mainSound(int samp, float time){
  float tempo = .125;
  time /= tempo;
  float s1 =
      8.;
  for(float i = 1.; i < mod(floor(time),4.); i++){
      time += mod(floor(time/2.),s1)*i;
      tempo *= 1.+fmod(floor(time/2.),2.);
  }
  float s2 = 8.,
  t=
      time,
  m1 =
      fmod(t/2.,s2)
  ,
  m3 =
      fmod(t/8.,1.+m1)+fmod(-t/8.,2.);

  float m2 =
      1. + fmod(t/s1,s1)
  ;
  float a=
      4.*(.125+fract(-t/s1))
  ,
  nb = time*pow(2.,floor((m3+m2)/2.)/5.+5.);
  return
      abs(abs(vec2(sin(nb),cos(nb))))*a
  ;
}

