#define COLOR(COORD) texture(iChannel0,(COORD))
float hash21(vec2 p) { return fract(sin(dot(p,vec2(27.609, 57.583)))*43758.5453); }
// chroma spread fake thing
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  

	vec2 uv = fragCoord.xy/iResolution.xy;    
    vec3 color = COLOR(uv).rgb;
    // effect sammple
    vec3 fgclr = vec3(0.);
    float f = length(uv  - 1.75);
    fgclr.x = COLOR(uv - vec2(f*0.001,.000)).x;
    fgclr.y = COLOR(uv + vec2(f*0.002,.001)).y;
    fgclr.z = COLOR(uv - vec2(f*0.002,.001)).z;

    // mask for effect and mixdown 
    float dt = length(uv-.5)-.22;
    vec3 C = mix(color,fgclr,smoothstep(0.,.275,dt));
    C = mix(C,clamp(C+.01,vec3(0),vec3(1)),hash21(uv+2.));
    // output -gamma correct
    C = pow(C, vec3(0.4545));
    fragColor = vec4(C,1.);
}

