//  Saw a tweet by @XorDev where he showed the neat trick of time-sin(time)
//  Tried it, tinkered with it and published it
//  https://twitter.com/XorDev/status/1654553771001749504

#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float box(vec2 p, vec2 b) {
  vec2 d = abs(p)-b;
  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  const float per = 4.0;
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  float aa = 4.0/RESOLUTION.y;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec2 p0 = p;
  float a0 = TAU*TIME/per;
  p0 *= ROT(a0-sin(a0));
  vec3 col = vec3(1.0);
  float d0 = box(p0, vec2(0.25));
  float a1 = a0 - TAU*d0/20.0;
  vec2 p1 = p;
  p1 *= ROT(a1-sin(a1));
  float d1 = box(p1, vec2(0.5));
  col = mix(col, vec3(0.0), smoothstep(0.0, -aa, d0));
  float m = TAU*10.0;
  col = mix(col, vec3(0.0), smoothstep(0.0, -aa*m, sin(m*d1-TAU*TIME/per)));
  col = sqrt(col);

  fragColor = vec4(col, 1.0);
}

/////////////////////////////////////////////////////////////////////
//
// Apollonian Circle Packings, mla, 2023
//
// In fact, hyperbolic honeycombs, seen on the boundary of hyperbolic
// space (eg. stereographically projected from the boundary sphere of
// the Poincare ball).
// 
// Generated by repeated reflection and inversion in two lines and two
// circles intersecting at rational angles, representing the faces of
// the generating hyperbolic tetrahedron.
//
// <up>/<down>: zoom in and out
// <left>/<right>: select tetrahedron
// i: invert at origin
// j: invert at mouse position
// m: monochrome
// p: progressive folding
// o: colour by iteration count
// w,z: show generating lines & circles
// (+ various other more esoteric key controls)
//
/////////////////////////////////////////////////////////////////////

// If point on wrong side of line (p.n < 0), reflect in line
int tryinvert(inout vec2 p, vec2 n, inout float scale) {
  if (dot(p,n) < 0.0) {
    p = reflect(p,n);
    return 1;
  }
  return 0;
}

// If point in "interior" of circle, invert to "exterior"
int tryinvert(inout vec2 p, vec3 c, inout float scale) {
  if (sign(c.z)*(distance(p,c.xy)-abs(c.z)) < 0.0) {
    p -= c.xy;
    float k = c.z*c.z/dot(p,p);
    p *= k;
    scale *= k;
    p += c.xy;
    return 1;
  }
  return 0;
}

int solve2(vec2 p, float r, vec2 q, float s, int a, out vec2 k);

// Given dihedral angles (P,Q,R),(S,T,U), find lines A,B and
// circles C,D such that dihedral angles are:
// AB: P; BC: Q; CA: R
// AD: S; BD: T; CD: U
//
// Line through origin, normal n, and circle centre p, radius r,
// have dihedral t where r cos t = c.n (draw a triangle),
// so if (p,r) has dihedral t and u with n and m, we have:
// r cos t = c.n and  r cos u = c.m  =>
// c.n/c.m = cos t/cos u =>
// c.n cos u - c.m cos t = 0 =>
// c.(n cos u - m cos t) = 0
// which is equation of a line & any point on the line is the centre
// of a circle with the correct dihedral (for suitable radius).
// For dihedral = PI/2, the circle centre is on the line itself,
// so if both dihedrals are PI/2, the centre is at the origin.

mat4x3 solve(ivec3 pqr, ivec3 stu) {
  assert(pqr[0] != 0);
  vec2 n = vec2(1,0);
  vec2 m = vec2(-icos(pqr[0]),isin(pqr[0]));
  // Confusing names here:
  // pqr,stu are vectors of angles
  // n,m are line normals
  // p,r,q,s are centre and radius of circles

  // Find point p on line with correct dihedral with n and m
  vec2 p = mat2(0,-1,1,0)*(m*icos(pqr[2])-n*icos(pqr[1])*n);
  //if (pqr[1] != 2 || pqr[2] != 2) p = normalize(p);
  // Make sure cos(PI/2) = 0 is handled properly!
  // If both, then origin is centre and radius can be arbitrary
  float r =
    pqr[1] != 2 ? dot(m,p)/icos(pqr[1]) 
    : pqr[2] != 2 ? dot(n,p)/icos(pqr[2])
    : 1.0;

  // Find point q on line with correct dihedral with n and m
  vec2 q = mat2(0,1,-1,0)*(m*icos(stu[0])-n*icos(stu[1]));
  float s =
    stu[0] != 2 ? dot(n,q)/icos(stu[0])
    : stu[1] != 2 ? dot(m,q)/icos(stu[1])
    : -1.0;
  // Now scale (q,s) to have correct dihedral with (p,r)
  vec2 roots;
  int nroots = solve2(p,r,q,s,stu[2],roots);
  assert(nroots == 2); // Equation is usually solvable
  // Default to larger root, this usually (always?) seems
  // to be the correct thing to do.
  vec3 C = vec3(p,r);
  vec3 D = vec3(q,s);
  D *= key(CHAR_0) ? roots[0] : roots[1]; // Flip roots
  return mat4x3(vec3(n,0),vec3(m,0),C,D);
}

// Solve quadratic to find scaled circle (q,s) that has dihedral
// angle PI/a with circle (p,r).
int solve2(vec2 p, float r, vec2 q, float s, int a, out vec2 roots) {
  roots = vec2(0);
  // Use cosine law:
  // |p-kq| = p.p - 2kp.q + kq.q = r+ks-2krs.cos(a)
  // If A == 0, then have linear solution plus "infinity"
  // If C == 0, then have linear solution plus zero
  // Solve Ak-2Bk+C = 0
  float A = dot(q,q)-s*s;
  float B = dot(p,q)+r*s*icos(a);
  float C = dot(p,p)-r*r;
  if (abs(A) < 1e-3) {
    // Problematic case - circle D passes through origin
    // (which means triangle AB,BD,DA is Euclidean).
    // -2Bx + C = 0
    roots[0] = roots[1] = C/(2.0*B);
    return 2;
  }
  if (abs(C) < 1e-3) {
    // This case seems unproblematic
    // Ax - 2B = 0
    roots[0] = roots[1] = 2.0*B/A;
    return 2;
  }
  float D = B*B-A*C;
  if (D < 0.0) return 0;
  roots[0] = (B + sqrt(D))/A;
  roots[1] = (B - sqrt(D))/A;
  return 2;
}

// We are really dealing with tetrahedral fundamental
// domains in hyperbolic space, but just displaying
// the boundary (as a stereographic projection to the
// Euclidean plane).

// Tetrahedron angles as (AB,BC,CA),(AD,BD,CD)
// So for a standard linear Coxeter diagram:
// AB,BC,CA,AD,BD,CD
// P  Q  2  2  2  R
// Q  R  2  P  2  2
// Q  2  R  2  P  2

const ivec3 tetrahedra[] =
  ivec3[](
          //ivec3(4,4,4), ivec3(4,2,4), // Problem case: A = 0 in quadratic
          ivec3(4,2,4), ivec3(4,4,4),
          ivec3(1,0,0), ivec3(0,0,0),
          ivec3(2,0,0), ivec3(0,0,0),
          ivec3(3,0,0), ivec3(0,0,0),
          ivec3(6,7,2), ivec3(2,2,20),
          ivec3(7,6,2), ivec3(20,2,2),
          ivec3(7,20,2), ivec3(6,2,2),
          ivec3(3,3,3), ivec3(2,2,7),
          ivec3(5,6,2), ivec3(2,2,5),
          ivec3(6,5,2), ivec3(5,2,0),
          ivec3(5,2,3), ivec3(2,2,0), // Nice recursive zoom
          ivec3(3,5,2), ivec3(2,2,0),
          ivec3(3,2,2), ivec3(2,5,4),
          ivec3(5,3,2), ivec3(2,2,0),
          ivec3(2,2,3), ivec3(0,3,2),
          ivec3(5,3,2), ivec3(0,2,0),
          ivec3(7,3,2), ivec3(2,2,0),
          ivec3(3,3,3), ivec3(2,2,0),
          ivec3(2,2,2), ivec3(3,3,0),
          ivec3(2,3,5), ivec3(2,2,0),
          ivec3(5,5,5), ivec3(5,5,0),
          ivec3(4,2,4), ivec3(4,4,4),
          ivec3(3,3,3), ivec3(0,0,0),
          ivec3(6,6,6), ivec3(6,6,0),
          ivec3(8,3,2), ivec3(2,2,0),
          ivec3(3,2,8), ivec3(2,0,2),
          ivec3(3,2,0), ivec3(2,7,0),
          ivec3(3,7,2), ivec3(2,2,0),
          ivec3(7,3,2), ivec3(0,2,2)
          );

vec3 getcol(vec2 p, float scale, vec2 mouse);

vec2 A,B; // Lines
vec3 C,D; // Circles
vec3 O0,O1,O2,O3; // Orthogonal Circles

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 mouse = vec2(0);
  if (iMouse.x > 0.0) mouse = (2.0*iMouse.xy - iResolution.xy)/iResolution.y;

  int ntetrahedra = tetrahedra.length()/2;
  int tindex = keycount(KEY_RIGHT)-keycount(KEY_LEFT);
  tindex %= ntetrahedra+1;
  assert(tindex >= 0);
  if (tindex != 0) tindex--;
  else tindex = int(iTime)%(ntetrahedra);
  ivec3 pqr = tetrahedra[2*tindex], stu = tetrahedra[2*tindex+1];
  //if (stu.z == 0) stu.z = int(iTime);

  mat4x3 ABCD = solve(pqr,stu);
  A = vec2(ABCD[0]);
  B = vec2(ABCD[1]);
  C = ABCD[2];
  D = ABCD[3];
  if (key(CHAR_A) && iMouse.x != 0.0) D *= 2.0*mouse.x; // Set angle D from mouse

  // Find orthogonal circles
  // Any circle with centre (0,0) is orthogonal to A,B
  O0 = vec3(0,0,sqrt(dot(C.xy,C.xy)-C.z*C.z)); // Orthogonal to A,B,C
  O1 = vec3(0,0,sqrt(dot(D.xy,D.xy)-D.z*D.z)); // Orthogonal to A,B,D
  {
    // O2 orthogonal to A,C,D -> centre lies on A
    // O3 orthogonal to B,C,D -> centre lies on B
    vec2 e = (D.xy-C.xy);
    float K = dot(C.xy,C.xy)-dot(D.xy,D.xy)+D.z*D.z-C.z*C.z;
    vec2 p2 = vec2(A.y,-A.x), p3 = vec2(B.y,-B.x);
    p2 *= -K/(2.0*dot(p2,e));
    p3 *= -K/(2.0*dot(p3,e));
    O2 = vec3(p2,sqrt(dot(C.xy-p2,C.xy-p2)-C.z*C.z));
    O3 = vec3(p3,sqrt(dot(C.xy-p3,C.xy-p3)-C.z*C.z));
  }

  float zoom = 2.0;
  // Try to scale so the main circle just fits on screen
  if (O0.z > 0.001 || O1.z > 0.001) {
    zoom = 1.0;
    float k = 1.0/max(O0.z,O1.z);//O1.z;
    C *= k; D *= k;
    O0 *= k; O1 *= k; O2 *= k; O3 *= k;
  }

  zoom *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));

  bool dozoom = false;
  //dozoom = !key(CHAR_I);
  if (dozoom) zoom *= exp(10.0*mouse.y);
  float AA = 2.0;
  vec3 col = vec3(0);
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      vec2 p = zoom*(2.0*(fragCoord.xy+vec2(i,j)/AA) - iResolution.xy)/iResolution.y;
      col += getcol(p,zoom,mouse);
    }
  }
  col /= AA*AA;
  col = pow(col,vec3(0.4545));
  if (alert) col.r = 1.0;
  fragColor = vec4(col,1);
}

vec3 getcol(vec2 p, float scale, vec2 mouse) {
  float maxscale = 200.0;
  int loops = 20;
  if (key(CHAR_P)) loops = min(loops,1+int(iTime)); // Progressive
  if (key(CHAR_I)) {
    // Invert at origin
    float k = 1.0/dot(p,p);
    p *= k;
    scale *= k;
  }
  if (key(CHAR_J)) {
    // Invert at mouse position
    p -= 2.0*mouse;
    float k = 1.0/dot(p,p);
    p *= k;
    scale *= k;
    p += 2.0*mouse;
  }
  // Remember original coordinates & scale
  // for drawing control circles & lines.
  vec2 p0 = p;
  float scale0 = scale;
  vec3 col = vec3(0);
  int i;
  int steps = 0;
  for (i = 0; i < loops; i++) {
    // Apply mirrors first
    for (int j = 0; j < 20; j++) {
      int k = 0;
      k += tryinvert(p,A,scale);
      k += tryinvert(p,B,scale);
      if (k == 0) break;
    }
    int k = 0;
    // Then inversion circles
    k += tryinvert(p,C,scale);
    k += tryinvert(p,D,scale);
    steps += k;
    if (k == 0) break;
  }
  if (key(CHAR_O)) {
    col = h2rgb(float(steps)/12.0); // Color by steps
    col = 0.2+0.8*col;
  } else if (!key(CHAR_M)) {
    if (length(p) < O0.z) col = vec3(0,1,0);
    if (length(p) > O1.z) col = vec3(1,1,0); // NB. O1 is "inverted"
    if (length(p-O2.xy) < O2.z) col = vec3(0,0,1);
    if (length(p-O3.xy) < O3.z) col = vec3(1,0,0);
    col = 0.2+0.8*col;
  }
  if (key(CHAR_T)) col *= 0.6+0.4*texture(iChannel0,p).xyz;
  float kk = 0.5;
  if (length(p) < O0.z) kk /= O0.z;
  else if (length(p) > O1.z) kk /= O1.z; // NB. O1 is "inverted"
  else if (length(p-O2.xy) < O2.z) kk /= O2.z;
  else if (length(p-O3.xy) < O3.z) kk /= O3.z;
  if (key(CHAR_G)) kk *= 2.0;
  if (key(CHAR_H)) kk *= 0.5;
  if (length(p) > O1.z) {
    // An adhoc correction to scale for the "inverted" circle O1.
    if (key(CHAR_S)) assert(false);
    vec2 p1 = p-O1.xy;
    kk *= O1.z*O1.z/dot(p1,p1);
  }
  if (key(CHAR_D)) col *= 0.8;
  float minwidth = 2.0*scale/iResolution.y;
  float cdist = min(min(dist(p,A),dist(p,B)),
                    min(dist(p,C),dist(p,D)));
  if (!key(CHAR_X)) {
    float cwidth = minwidth;
    if (key(CHAR_F)) cwidth = max(0.0,0.01/kk);
    vec3 ccol = vec3(1);
    col = mix(ccol, col, 0.5+0.5*smoothstep(0.5*cwidth,cwidth,cdist));
  }
  if (key(CHAR_Y)) {
    float cwidth = minwidth;
    if (key(CHAR_F)) cwidth = max(0.0,0.005/kk);
    vec3 ccol = vec3(0);
    col = mix(ccol, col, 0.2+0.8*smoothstep(0.5*cwidth,cwidth,cdist));
  }
  if (!key(CHAR_U) && kk*scale > maxscale) col = vec3(0);
  if (key(CHAR_W)){
    vec3 ccol = vec3(1);
    float d = 1e8;
    float cwidth = 2.0*scale0/iResolution.y;
    if (O0.z > 0.0) d = min(d,dist(p0,O0));
    if (O1.z > 0.0) d = min(d,dist(p0,O1));
    if (O2.z > 0.0) d = min(d,dist(p0,O2));
    if (O3.z > 0.0) d = min(d,dist(p0,O3));
    col = mix(ccol, col, smoothstep(0.0,cwidth,d-cwidth));
  }
  if (key(CHAR_Z)) {
    float cwidth = 2.0*scale0/iResolution.y;
    float cdist0 = min(min(dist(p0,A),dist(p0,B)),
                       min(dist(p0,C),dist(p0,D)));
    vec3 ccol = vec3(0);
    col = mix(ccol, col, smoothstep(0.0,cwidth,cdist0-cwidth));
  }
  return col;
}

