////////////////////////////////////////////////////////////////////////////////

const float PI = 3.1415927;

bool alert = false;
void assert(bool b) {
  if (!b) alert = true;
}

// Distance to line (through origin)
float dist(vec2 p, vec2 l) {
  return abs(dot(p,l));
}

// Distance to circle (z coord is radius, if negative then consider
// the "interior" to be points x with |x - c.xy| > c.z
float dist(vec2 p, vec3 c) {
  return abs(distance(p,c.xy)-abs(c.z));
}

float COS[] =
  // I don't trust cos on the GPU to be accurate enough: cos(PI/n) for n = 0..31
  float[](1.0,-0.5,0.0,0.5,0.7071067812,0.8090169944,0.8660254038,
          0.9009688679,0.9238795325,0.9396926208,0.9510565163,0.9594929736,
          0.9659258263,0.9709418174,0.9749279122,0.9781476007,0.9807852804,
          0.9829730997,0.984807753,0.9863613034,0.9876883406,0.9888308262,
          0.9898214419,0.990685946,0.9914448614,0.9921147013,0.9927088741,
          0.9932383577,0.9937122099,0.9941379572,0.9945218954,0.9948693234);

float icos(int p) {
  // cos(PI/p)
  if (p < COS.length()) return COS[p];
  if (p == 0) return 1.0;
  return cos(PI/float(p));
}

float isin(int p) {
  // sin(PI/p)
  float t = icos(p);
  return sqrt(1.0-t*t);
}

vec3 h2rgb(float h) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return rgb;
}

#if !defined(key)
#define key(code) (texelFetch(iChannel3, ivec2((code),2),0).x != 0.0)
#endif
#define store(i,j) (texelFetch(iChannel2, ivec2((i),(j)),0))
#define keycount(key) (int(store((key),0).x))

