buffer_a_code:
#define keystate(k,n) ( texelFetch(iChannel3,ivec2(k,n),0) )

void mainImage(out vec4 t, vec2 uv) {
  int i = int(uv.x); // Column number
  int j = int(uv.y); // Row
  if (iFrame == 0) {
    t = vec4(0);
  } else {
    t = texelFetch(iChannel2,ivec2(i,j),0); // Get current state   
  }  
  if (j == 0) {
    // First row - key count
    // Do key repeat here as well?
    float w1 = keystate(i,0).x;
    if (w1 != t.w) {
      t.w = w1;
      // Don't count key changes when ALT pressed
      if (keystate(KEY_ALT,0).x == 0.0 && w1 != 0.0) {
        t.x++; t.y = iTime;
      }
    }
    // Key repeat - only when shader is running!
    // Should have longer pause after first key press.
    if (t.w != 0.0 && keystate(KEY_ALT,0).x == 0.0 && iTime-t.y > 0.25) {
      t.x++; t.y = iTime;
    }
  }
}

image_code:
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;
    float fade = 0.02;
    float thinness = 0.02;
    vec3 circleColor = vec3(0.7, 0.2, 0.5);
    float dist = 1.0 - sqrt(dot(uv, uv));
    vec3 color  =  vec3(smoothstep(0.0, fade, dist));
         color *= vec3(smoothstep(fade + thinness, thinness, dist));

    // Output to screen
    fragColor = vec4(color, 1.0);
    fragColor.rgb *= circleColor;
}

vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(fract((a1)*(a2.x+a2.y))-.5)
    ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv.y += (iTime/2.0,iTime/3.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec2 t2 = vec2(0.);
    bool b = true;
    vec2 uv1 = uv;
    uv.x = mod(uv.x,.5)+.25;
    //uv.y = mod(uv.y,1.)-.5;
    uv.y = mod(uv.y,.5)+2.;
    for(int k = 0; k < 9; k++){
        uv =
            abs(-.5+uv-t2*sign(uv.y-uv.x))/1.5+1.
        ;
        b = uv.y>uv.x;
        if(b) uv = uv.yx;
        t2 =
            triangle_wave(uv-.5*sign(uv.y-uv.x)).yx
        ;
        uv =
            t2+triangle_wave(uv.yx).yx
        ;
        if(uv.y>uv.x)
        //{t2 = t2.yx; uv=uv.yx;}
        col.x =
            abs((uv.x)-col.x)
        ;
        col.xyz =
            abs(col.yzx-float(b)/2.)
        ;
    }
    if (!b) col = 1.-col;
    fragColor = vec4(min(col,vec3(1.)),1.0);
}

