        for(int j=-1; j<=1; j++){
            vec2 off = vec2(i, j);
            vec2 uv2 = off+vec2((i==0)?uvf.x:1.-uvf.x, (j==0)?uvf.y:1.-uvf.y);


            float weight = (1. - smoothstep(0., overlap, abs(uvf.x - uv2.x)))
                          *(1. - smoothstep(0., overlap, abs(uvf.y - uv2.y)));

            w += weight;
            col += weight*texture(iChannel0, uvf+getOffset(floor(uv)+off)).rgb;
        }
