  return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);
}

// License: MIT, author: Inigo Quilez, found: https://www.iquilezles.org/www/articles/smin/smin.htm
float pmin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

// License: CC0, author: Mrten Rnge, found: https://github.com/mrange/glsl-snippets
float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

// License: CC0, author: Mrten Rnge, found: https://github.com/mrange/glsl-snippets
float pabs(float a, float k) {
  return -pmin(a, -a, k);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float horseshoe(vec2 p, vec2 c, float r, vec2 w) {
  p.x = abs(p.x);
  float l = length(p);
  p = mat2(-c.x, c.y, c.y, c.x)*p;
  p = vec2((p.y>0.0 || p.x>0.0)?p.x:l*sign(-c.x),
           (p.x>0.0)?p.y:l );
  p = vec2(p.x,abs(p.y-r))-w;
  return length(max(p,0.0)) + min(0.0,max(p.x,p.y));
}

float inf(vec2 p) {
  const float sm = 0.125;
  p.y = -pabs(p.y, sm);
  p.x = -pabs(p.x, sm);
  const float a = PI/4.;
  const vec2 csa = CSA(a);
  const float r = 1.5;
  p.y += 2.0*r*sin(a);
  return horseshoe(p, csa, r, vec2(100.0, 0.25));
}

float inf(vec3 p, float h) {
  p = p.yzx;
  float d = inf(p.xy);
  vec2 w = vec2(d, abs(p.z) - h);
  return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float dot2(vec2 p) {
  return dot(p, p);
}

float dot2(vec3 p) {
  return dot(p, p);
}

float df(vec3 p) {
  float d0 = inf(p, 0.125)-0.1;
  vec2 pp = 20.0*p.yz;
  float h = dot2(sin(pp));
  h *= h;
  h *= h;
  h *= h;
  float d1 = p.x-0.05*abs(p.y)+0.00005*h;
  const vec2 nn = normalize(vec2(1.5, 1.));
  const float zz = 0.5;
  float d = d1;
  /*
  float d2 = vorMap(p/zz)*zz-0.025+0.025*length(p.zy);
  d = pmax(d, -d2, 0.05);*/
  d = pmax(d, -(d0-0.125), 0.025);
  d = min(d, d0);

  return d;
}

vec3 normal(vec3 pos) {
  vec2  eps = vec2(NORM_OFF,0.0);
  vec3 nor;
  nor.x = df(pos+eps.xyy) - df(pos-eps.xyy);
  nor.y = df(pos+eps.yxy) - df(pos-eps.yxy);
  nor.z = df(pos+eps.yyx) - df(pos-eps.yyx);
  return normalize(nor);
}

float rayMarch(vec3 ro, vec3 rd) {
  float t = 0.0;
  const float tol = TOLERANCE;
  vec2 dti = vec2(1e10,0.0);
  int i = 0;
  for (i = 0; i < MAX_RAY_MARCHES; ++i) {
    float d = df(ro + rd*t);
    if (d<dti.x) { dti=vec2(d,t); }
    if (d < TOLERANCE || t > MAX_RAY_LENGTH) {
      break;
    }
    t += d;
  }
  if(i==MAX_RAY_MARCHES) { t=dti.y; };
  return t;
}


float softShadow(vec3 ps, vec3 ld, float mint, float k) {
  float res = 0.0;
  float t = mint*7.0;
  for (int i=0; i<MAX_SHADOW_MARCHES; ++i) {
    vec3 p = ps + ld*t;
    float d = df(p);
    if (d < 0.0) return 0.0;
    res += 0.05*inversesqrt((d));
    if (res > 1.0) {
      return 0.0;
    };
    t += max(d, mint);
  }
  return clamp(1.0-res, 0.0, 1.0);
}

vec3 render(vec3 ro, vec3 rd) {
  const vec3 lightPos1 = vec3(5.0, 3.0, 1.0)*2.;
  const vec3 lightPos2 = vec3(5.0, -3.0, -2.0)*2.;

  float t = rayMarch(ro, rd);
  vec3 col = vec3(0.0);

  vec3 p = ro+rd*t;
  vec3 n = normal(p);
  vec3 r = reflect(rd, n);

  vec3 ld1 = normalize(lightPos1 - p);
  float ll21 = dot2(lightPos1 - p);
  float dif1 = max(dot(ld1, n), 0.0);
  float spe1 = pow(max(dot(ld1, r), 0.0), 10.0);

  float sd = softShadow(p, ld1, 0.025, 2.0);

  vec3 ld2 = normalize(lightPos2 - p);
  float ll22 = dot2(lightPos2 - p);
//  float sd1 = softShadow(p, ld1, 0.025, 2.0);
  float dif2 = max(dot(ld2, n), 0.0);
  dif2 *= dif2;
  float spe2 = pow(max(dot(ld2, r), 0.0), 20.0);

  const float i = 100.0; 
//  float h = 0.7 + 0.125*p.z*p.x-0.25*p.x+0.0125*p.y*p.z;
  float h1 = 0.6;
  float h2 = 0.025;

  vec3 dcol1 = hsv2rgb(vec3(h1, 0.85, i));
  vec3 scol1 = hsv2rgb(vec3(h1, 0.33, i));

  vec3 dcol2 = hsv2rgb(vec3(h2, 0.90, 0.33*i));
  vec3 scol2 = hsv2rgb(vec3(h2, 0.66, 0.33*i));
  if (t < MAX_RAY_LENGTH) {
    vec3 col1 = dcol1;
    col1 *= mix(0.1, 1.0, dif1);
    col1 *= mix(0.2, 1.0, sd);
    col1 += spe1*sd*scol1;
    col1 /= ll21;
    col += col1;

    vec3 col2 = dcol2;
    col2 *= mix(0.1, 1.0, dif2);
    col2 *= mix(0.2, 1.0, sd);
    col2 += spe2*sd*scol2;
    col2 /= ll22;
    col += col2;
  }

  col -= 0.08*vec3(1.0, 2.0, 1.0);
  return 1.5*col;
}

vec3 effect(vec2 p) {
  const vec3 ro = vec3(5.0, 0.0, 0.01);
  const vec3 la = vec3(0.0, 0.0, 0.0);
  const vec3 up = normalize(vec3(0.0, 1.0, 0.0));

  vec3 ww = normalize(la - ro);
  vec3 uu = normalize(cross(up, ww ));
  vec3 vv = (cross(ww,uu));
  const float fov = tan(TAU/6.);
  vec3 rd = normalize(-p.x*uu + p.y*vv + fov*ww);

  vec3 col = render(ro, rd);

  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  vec2 pp = p;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = vec3(0.0);
  col = effect(p);
  col *= smoothstep(1.75, 1.0-0.5, length(pp));
  col = aces_approx(col); 
  col = sRGB(col);
  fragColor = vec4(col, 1.0);
}

