// We define an arch as two points, and a height (sagitta [1])
//
// Let A and B be two points in the 2d space.
// Let s be a scalar.
// We can find a circle that passes through both A and B such that:
// - the distance between the base of the arch and the center of the arch is |s|.
// - the direction of the arch is sign(s) (i.e. negative mirrors the arch around AB)
//
// Let n be the normal vector of the arch: i.e. be perpendicular to AB.
// Then:
// The radius of the circle is r = |(AB)^2 / 8s + s/2|
// The center of the circle is C = A + (B-A)/2 + n * (r - |s|)

/*          _.--""T""--._            +
         _-`      |      `-_         |
       ."         |         ".       |
     ."           |           ".     | s 
    /             |             \    |
   o . . . . . . .|. . . . . . . o   +
   A              |              B   
                  | r
                  |
                  o
                  C
*/

// Then the field is divided into two parts:
// 1. Inside the cone CA CB (the circle pie between A and B extended to infinity)
//    the distance acts a circle i.e. | ||C - X|| - r | for some point X
// 2. Outside the cone either point A or B is closest, we take the minimum,
//    i.e. ||A - X|| min ||B - X|| for some point X
// To check if it is inside the cone we use teh dot product of vectors C-X and C-A

// Sources:
// [1] https://en.wikipedia.org/wiki/Sagitta_(geometry)

// ------------------------------------------------------------------------------------

// Visualize the field

// Sawtooth function
float ramp(float x)
{
    x = abs(x);
    float y = 0.;
    for (int i = 1; i < 7; ++i)
        y += (1. / float(i)) * sin(x * float(i));
    return y * .5 + .5;
}

#define BG vec3(.17, .13, .15)
#define FG (vec3(1.) - BG)

void mainImage(out vec4 O, in vec2 I)
{
    float w = min(iResolution.x, iResolution.y);
    I -= iResolution.xy / 2.;

    float t = iTime + 25.;
    float tA = t / -5.;
    float tB = t / -7.;
    float ts = t / 10.;

    vec2 A = vec2(cos(tA), sin(tA)) * .4 * w;
    vec2 B = vec2(cos(tB), sin(tB)) * .2 * w;
    float s = sin(ts) * .5 * w;

    if (iMouse.z >= 0.)
        s = iMouse.y - .5 * w;

    float d = sdfArc(I, A, B, s) - .04 * w;

    vec3 color = d >= 0. ? vec3(.4, .9, .3) : vec3(.9, .3, .4);
    vec3 field = BG + FG * color * ramp(d / 5.);
    vec3 image = mix(field, FG * vec3(.4, .3, .9), smoothstep(2., 0., abs(d) - 1.));
    O = vec4(image, 1.);
}

void mainImage( out vec4 O, in vec2 fragCoord )
{
    vec4 c;

    const float N = 4.0;

    if (fragCoord.x < iResolution.x * 1.0 / N) {
        O = vec4(0.0);
        c = vec4(1.0, 1.0, 1.0, 1.0);
    }
    else if (fragCoord.x < iResolution.x * 2.0 / N) {
        O -= O;
        c = vec4(1.0, 0.0, 0.0, 1.0);
    }
    else if (fragCoord.x < iResolution.x * 3.0 / N) {
        O *= 0.0;
        c = vec4(0.0, 1.0, 0.0, 1.0);
    }
    else {
        O = O - O + 0.0;
        c = vec4(0.0, 0.0, 1.0, 1.0);
    }

    if (fragCoord.y < 20.0) {
        O = vec4(-0.2);
    }

    O += c;    
}

// simplifying "Colourful Apollonian III" by mla. https://shadertoy.com/view/wdsfWX



#define inv   z = (z-t) / dot(z-t,z-t) *s + t


void mainImage(out vec4 O, vec2 u) {

  vec2 R = iResolution.xy,
       z = ( u+u - R ) / R.y,
       t = ( 2.*iMouse.xy - R ) / R.y;

  float N = 3.,                       // Any odd N >= 3 will work
        a = 3.14/N,
        D = 1./cos(a),                // Centre distance of outer circles
        T = tan(a),                   // Radius of outer circles
        K = D - T,                    // Radius squared of centre circle
        n = 50., i = 0.,j, s = 1.;
                                      // --- init setup 
  if ( length(iMouse.xy) > 9. )       // interactive mode:
      t /= s = dot(t,t),              //   set inversion translation & scaling
      s = 1./s - 1.;
   // z = inv( z, t/s, (1.-s)/s );    //   invertto(z,t)
  else                                // demo mode:
      z += t = vec2( 0, 1.618*sin(.5*iTime) ), // set inversion translation; scaling = 1
      z /= dot(z,z);
   // z = inv( z, t, 1.);
  inv;
                                      // --- compute Apollonian
  for (s = T*T ; i < n && length(z) > K ; i++) { // fractal iterations
    z *= - K / dot(z,z);              // Imaginary radius
    for ( j = 0.; j++ < N; )
      t = D * sin( 6.28*j/N + vec2(0,1.57)),                   
      length(z-t) < T ? inv, j=n : j; // getcircle(j)      
  }

  O = sqrt( // i == n ? vec4(0) :     // to sRGB
            ( .6 + .6 * cos( i  + vec4(0,23,21,0)) )  //hue
            * ( 1.-length(z)/K ) );   // pseudo-shading
}

// "Soddy's Mirror" by dr2 - 2023
// License: Creative Commons Attribution-NonCommercial-ShareAlike 4.0

// Based on "Shaded Gasket"

#define AA  1  // (= 0/1)

#if 0
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

float PrRndBoxDf (vec3 p, vec3 b, float r);
float PrSphDf (vec3 p, float s);
float PrCylDf (vec3 p, float r, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
float SmoothBump (float lo, float hi, float w, float x);
vec3 HsvToRgb (vec3 c);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
mat3 StdVuMat (float el, float az);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

#define N_CIRC  3  // odd value (>= 3)

vec3 limCirc[N_CIRC + 1], invCirc[N_CIRC + 1], sunDir, msOrg;
vec2 rAngHCs, rAngACs, rAngShCs;
float tCur, dstFar, msDep, mDskRad, frmWid, mirFac;
int idObj;
bool isRefl, isSh, oneFace;
const int idFrm = 1, idRob = 2, idEye = 3;
const int maxIt = 64;
const float pi = 3.1415927;

#define CosSin(x) (sin ((x) + vec2 (0.5 * pi, 0.)))
#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

struct Mob {
  vec2 a, b, c, d;
};

vec2 CMul (vec2 a1, vec2 a2)
{
  return vec2 (a1.x * a2.x - a1.y * a2.y, a1.x * a2.y + a1.y * a2.x);
}

vec2 CConj (vec2 a)
{
  return vec2 (a.x, - a.y);
}

float CModSq (vec2 a)
{
  return a.x * a.x + a.y * a.y;
}

vec2 CDiv (vec2 a1, vec2 a2)
{
  return CMul (a1, CConj (a2)) / CModSq (a2);
}

vec2 MobOp (Mob m, vec2 z)
{
  return CDiv (CMul (z, m.a) + m.b, CMul (z, m.c) + m.d);
}

Mob MobInv (Mob m)
{
  return Mob (m.d, - m.b, - m.c, m.a);
}

Mob MobProd (Mob m1, Mob m2)
{
  return Mob (CMul (m1.a, m2.a) + CMul (m1.b, m2.c), CMul (m1.a, m2.b) + CMul (m1.b, m2.d),
     CMul (m1.c, m2.a) + CMul (m1.d, m2.c), CMul (m1.c, m2.b) + CMul (m1.d, m2.d));
}

vec3 MobToCirc (Mob m, vec3 c)
{
  vec2 z;
  z = MobOp (m, c.xy - CDiv (vec2 (c.z * c.z, 0.), CConj (c.xy + CDiv (m.d, m.c))));
  return vec3 (z, length (z - MobOp (m, c.xy + vec2 (c.z, 0.))));
}

Mob CircToMob (vec3 c)
{
  Mob m;
  m = Mob (vec2 (c.z, 0.), c.xy, vec2 (0.), vec2 (1., 0.));
  return MobProd (MobProd (m, Mob (vec2 (0.), vec2 (1., 0.), vec2 (1., 0.), vec2 (0.))),
     Mob (m.d, m.b * vec2 (-1., 1.), m.c, m.a));
}

void CircInit ()
{
  float a, r, rs;
  a = pi / float (N_CIRC);
  r = 1. / cos (a);
  rs = sqrt (r * r - 1.);
  for (int j = 0; j < N_CIRC; j ++) {
    limCirc[j] = vec3 (sin (2. * a * float (j) - vec2 (0.5 * pi, 0.)) * r, rs) * (r - rs);
    invCirc[j] = vec3 (sin (2. * a * float (j) + vec2 (0.5 * pi, 0.)) * r, rs);
  }
  limCirc[N_CIRC] = vec3 (0., 0., 1.);
  invCirc[N_CIRC] = vec3 (0., 0., r - rs);
}

#define DDOT(x) dot ((x), (x))

vec4 PCirc (vec2 p, vec2 pm)
{
  Mob mm, m;
  vec3 g, gi, w;
  vec2 z, cm;
  float eps;
  bool done;
  eps = 1e-9;
  CircInit ();
  z = p;
  mm = Mob (vec2 (1., 0.), vec2 (0.), vec2 (0.), vec2 (1., 0.));
  if (DDOT (pm) > 0.0005 && DDOT (pm) < 1.) {
    cm = pm * vec2 (-1., 1.) / DDOT (pm);
    m = CircToMob (vec3 (cm, sqrt (DDOT (cm) - 1.)));
    z = MobOp (m, z);
    mm = MobProd (m, mm);
  }
  for (int it = VAR_ZERO; it < maxIt; it ++) {
    done = true;
    for (int j = 0; j <= N_CIRC; j ++) {
      gi = invCirc[j];
      if (DDOT (z - gi.xy * vec2 (1., -1.)) < gi.z * gi.z) {
        g = gi;
        done = false;
        break;
      }
    }
    if (! done) {
      if (g.x == 0.) g.x = eps;
      m = CircToMob (g);
      z = MobOp (m, z);
      mm = MobProd (m, mm);
    } else break;
  }
  mm = MobInv (mm);
  if (CModSq (mm.c) == 0.) mm.c = vec2 (eps, 0.);
  w.z = 1.;
  for (int j = 0; j <= N_CIRC; j ++) {
    g = MobToCirc (mm, limCirc[j]);
    if (g.z > 0. && g.z < 1.) {
      w.xy = (p - g.xy) / g.z;
      w.z = DDOT (w.xy);
      if (w.z < 1.) break;
    }
  }
  return vec4 (w, g.z);
}

float RobDf (vec3 p, float dMin)
{
  vec3 q;
  float d, szFac;
  p.xz = vec2 (- p.z, p.x);
  szFac = 0.2;
  p /= szFac;
  dMin /= szFac;
  p.yz = Rot2Cs (p.yz, rAngShCs);
  q = p; 
  q.y -= 2.3;
  d = max (PrSphDf (q, 0.85), - q.y);
  q.y -= -0.08;
  d = min (d, PrCylDf (q.xzy, 0.75, 0.1));
  q = p;
  q.y -= 1.55;
  d = min (d, PrRoundCylDf (q.xzy, 0.62, 0.28, 0.42));
  q = p;  
  q.xz = Rot2Cs (q.xz, rAngHCs);
  q.x = abs (q.x) - 0.3;
  q.y -= 3.1;
  q.xy = Rot2Cs (q.xy, CosSin (0.2 * pi));
  q.y -= 0.25;
  d = min (d, PrRoundCylDf (q.xzy, 0.02, 0.04, 0.26));
  q = p;
  q.x = abs (q.x);
  q.xy -= vec2 (1.05, 2.1);
  q.yz = Rot2Cs (q.yz, rAngACs);
  q.y -= -0.5;
  d = min (d, PrRoundCylDf (q.xzy, 0.05, 0.15, 0.45));
  q = p;
  q.x = abs (q.x);
  q.xy -= vec2 (0.4, 0.475);
  d = min (d, PrRoundCylDf (q.xzy, 0.1, 0.15, 0.4));
  DMIN (idRob);
  q = p;
  q.xz = Rot2Cs (q.xz, rAngHCs);
  q.x = abs (q.x);
  q -= vec3 (0.4, 2.7, 0.6);
  d = PrSphDf (q, 0.15);
  DMIN (idEye);
  return szFac * dMin;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, r;
  dMin = dstFar;
  q = p;
  q.y -= msOrg.y;
  r = length (q.xy) - mDskRad;
  if (! isRefl) {
    d = max (abs (r - frmWid) - frmWid, abs (q.z) - msDep);
    DMIN (idFrm);
    q = p;
    q.y -= 0.2 * frmWid;
    d = PrRndBoxDf (q, vec3 (0.5, 0.2 * frmWid, msDep), 0.01);
    DMIN (idFrm);
  }
  if (isSh && ! oneFace) {
    d = max (r, abs (q.z) - 0.1);
    DMIN (idFrm);
  }
  q = p;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (13. * ((length (q.xz) > 0.) ? atan (q.z, - q.x) /
     (2. * pi) : 0.)) + 0.5) / 13.);
  q.x += 1.5;
  dMin = RobDf (q, dMin);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0001, -0.0001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  isSh = true;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.02 * d, h));
    d += max (h, 0.02);
    if (sh < 0.05 || d > dstFar) break;
  }
  isSh = false;
  return 0.6 + 0.4 * sh;
}

float CylHit (vec3 ro, vec3 rd, float cylRad, float cylLen)
{
  vec3 s;
  float dMin, d, a, b, w, srdz;
  dMin = dstFar;
  a = dot (rd.xy, rd.xy);
  b = dot (rd.xy, ro.xy);
  w = b * b - a * (dot (ro.xy, ro.xy) - cylRad * cylRad);
  if (w > 0.) {
    srdz = sign (rd.z);
    if (a > 0.) {
      d = - b - sqrt (w);
      if (d > 0.) {
        d /= a;
        s = ro + d * rd;
      } else s.z = cylLen;
    } else s.z = cylLen;
    if (abs (s.z) < cylLen) {
      dMin = d;
    } else if (srdz * ro.z < - cylLen) {
      d = - (srdz * ro.z + cylLen) / abs (rd.z);
      if (d > 0.) {
        s = ro + d * rd;
        if (length (s.xy) < cylRad) {
          dMin = d;
        }
      }
    }
  }
  return dMin;
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 vn, col;
  vec2 w, e;
  float sd, f;
  e = vec2 (0.01, 0.);
  if (rd.y == 0.) rd.y = 1e-3;
  if (rd.y > 0.) {
    ro.xz += 2. * tCur;
    sd = max (dot (rd, sunDir), 0.);
    col = vec3 (0.1, 0.2, 0.4) + 0.2 * (1. - rd.y) +
       0.1 * pow (sd, 16.) + 0.2 * pow (sd, 256.);
    f = Fbm2 (0.02 * (ro.xz + rd.xz * (100. - ro.y) / rd.y));
    col = mix (col, vec3 (1.), clamp (0.2 + 1.6 * f * rd.y, 0., 1.));
  } else {
    ro -= (ro.y / rd.y) * rd;
    w = 4. * ro.xz;
    f = Fbm2 (w);
    col = mix (vec3 (0.3, 0.4, 0.1), vec3 (0.4, 0.5, 0.2), 2. * f) * (1. - 0.1 * Noisefv2 (w));
    vn = normalize (vec3 (f - vec2 (Fbm2 (w + e.xy), Fbm2 (w + e.yx)), 0.05)).xzy;
    col *= 0.1 + 0.9 * max (dot (vn, sunDir), 0.);
    col = mix (col, vec3 (0.45, 0.55, 0.7), pow (1. + rd.y, 64.));
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 p4;
  vec3 col, vn;
  vec2 p;
  float dstObj, dstMir, sh, nDotL, sz;
  bool needSh;
  dstMir = CylHit (ro - msOrg, rd, mDskRad, 0.9 * msDep);
  isRefl = false;
  isSh = false;
  sz = sign (rd.z);
  for (int k = 0; k < 2; k ++) {
    dstObj = ObjRay (ro, rd);
    if (k == 0 && (dstMir < min (dstObj, dstFar) && (! oneFace || sz > 0.))) {
      ro += dstMir * rd;
      p = (ro.xy - msOrg.xy) / mDskRad;
      p4 = PCirc (vec2 (- p.y, p.x), vec2 (0.));
      p = mirFac * p4.xy;
      vn = sz * vec3 (vec2 (- p.y, p.x), sqrt (1. - dot (p, p)));
      rd = reflect (rd, vn);
      ro += 0.001 * rd;
      isRefl = true;
    } else break;
  }
  needSh = false;
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == idFrm) {
      vn = VaryNf (128. * ro, vn, 1.);
      col = vec3 (0.6, 0.6, 0.7);
    } else if (idObj == idRob) {
      col = HsvToRgb (vec3 (fract (floor (13. * (atan (ro.z, - ro.x) / (2. * pi))) / 13. +
         0.25), 0.8, 1.));
    } else if (idObj == idEye) {
      col = vec3 (1., 1., 0.8);
    }
    nDotL = max (dot (vn, sunDir), 0.);
    needSh = true;
  } else {
    col = BgCol (ro, rd);
    if (rd.y < 0.) {
      ro -= (ro.y / rd.y) * rd;
      isRefl = false;
      vn = vec3 (0., 1., 0.);
      needSh = true;
    }
  }
  sh = (needSh) ? ObjSShadow (ro + 0.01 * vn, sunDir) : 1.;
  if (dstObj < dstFar) col *= (0.2 + 0.2 * max (dot (vn, sunDir * vec3 (-1., 1., -1.)), 0.) +
     0.8 * sh * nDotL * nDotL);
  else if (rd.y < 0.) col *= sh;
  if (isRefl) col = mix (col, HsvToRgb (vec3 (fract (0.75 - 0.9 * log2 (p4.w) /
     log2 (float (maxIt))), 1., 1.)), 0.1 * smoothstep (0., 0.05, abs (mirFac)));
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float az, el, zmFac, sr, t, rAngA;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az -= 2. * pi * mPtr.x;
    el -= 0.5 * pi * mPtr.y;
  } else {
    t = 0.05 * tCur;
    az = 0.15 * pi * SmoothBump (0.25, 0.75, 0.2, fract (t)) * sign (mod (t, 2.) - 1.);
  }
  el = clamp (el, -0.3 * pi, 0.01 * pi);
  vuMat = StdVuMat (el, az);
  oneFace = false;
  t = 0.06 * tCur;
  mirFac = 0.7 * SmoothBump (0.25, 0.75, 0.22, fract (t)) * sign (mod (t, 2.) - 1.);
  t = mod (0.3 * tCur, 1.);
  rAngHCs = CosSin (0.2 * pi * sin (2. * pi * t));
  rAngA = 2. * pi * (0.5 - abs (t - 0.5));
  rAngACs = CosSin (rAngA);
  rAngShCs = CosSin (0.1 * (rAngA - 0.5 * pi));
  mDskRad = 1.;
  frmWid = 0.02;
  msDep = 0.1;
  msOrg = vec3 (0., mDskRad + frmWid, 0.);
  zmFac = 4.;
  ro = vuMat * vec3 (0., 0.6 * msOrg.y, -7.);
  sunDir = normalize (vec3 (-1., 2., -1.));
  dstFar = 50.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrRndBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float s)
{
  return length (p) - s;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1.,
     0., 1.), c.y);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

const vec2 target_resolution = vec2(940, 86);

vec4 correct(vec4 col1, vec4 col2, float p)
{
 	vec4 temp = col1 * col1;
    vec4 temp2 = col2 * col2;
    return sqrt(mix(temp, temp2, p));
}

float cubicPulse( float c, float w, float x ){
    x = abs(x - c);
    if( x>w ) return 0.0;
    x /= w;
    return 1.0 - x*x*(3.0-2.0*x);
}

float plot(vec2 st, float pct){
  return  smoothstep( pct-0.02, pct, st.y) -
          smoothstep( pct, pct+0.02, st.y);
}

mat4 saturationMatrix( float saturation ) {
    vec3 luminance = vec3( 0.3086, 0.6094, 0.0820 );
    float oneMinusSat = 1.0 - saturation;
    vec3 red = vec3( luminance.x * oneMinusSat );
    red.r += saturation;

    vec3 green = vec3( luminance.y * oneMinusSat );
    green.g += saturation;

    vec3 blue = vec3( luminance.z * oneMinusSat );
    blue.b += saturation;

    return mat4( 
        red,     0,
        green,   0,
        blue,    0,
        0, 0, 0, 1 );
}

void brightnessAdjust( inout vec4 color, in float b) {
    color.rgb += b;
}

void contrastAdjust( inout vec4 color, in float c) {
    float t = 0.5 - c * 0.5; 
    color.rgb = color.rgb * c + t;
}

int modi(int x, int y) {
    return x - y * (x / y);
}

int and(int a, int b) {
    int result = 0;
    int n = 1;
	const int BIT_COUNT = 32;

    for(int i = 0; i < BIT_COUNT; i++) {
        if ((modi(a, 2) == 1) && (modi(b, 2) == 1)) {
            result += n;
        }

        a >>= 1;
        b >>= 1;
        n <<= 1;

        if (!(a > 0 && b > 0))
            break;
    }
    return result;
}

// forked from https://www.shadertoy.com/view/llGSzK
// performance optimized by Ruofei
vec4 vibrance(vec4 inCol, float vibrance) //r,g,b 0.0 to 1.0,  vibrance 1.0 no change, 0.0 image B&W.
{
 	vec4 outCol;
    if (vibrance <= 1.0)
    {
        float avg = dot(inCol.rgb, vec3(0.3, 0.6, 0.1));
        outCol.rgb = mix(vec3(avg), inCol.rgb, vibrance); 
    }
    else // vibrance > 1.0
    {
        float hue_a, a, f, p1, p2, p3, i, h, s, v, amt, _max, _min, dlt;
        float br1, br2, br3, br4, br5, br2_or_br1, br3_or_br1, br4_or_br1, br5_or_br1;
        int use;

        _min = min(min(inCol.r, inCol.g), inCol.b);
        _max = max(max(inCol.r, inCol.g), inCol.b);
        dlt = _max - _min + 0.00001 /*Hack to fix divide zero infinities*/;
        h = 0.0;
        v = _max;

		br1 = step(_max, 0.0);
        s = (dlt / _max) * (1.0 - br1);
        h = -1.0 * br1;

		br2 = 1.0 - step(_max - inCol.r, 0.0); 
        br2_or_br1 = max(br2, br1);
        h = ((inCol.g - inCol.b) / dlt) * (1.0 - br2_or_br1) + (h*br2_or_br1);

		br3 = 1.0 - step(_max - inCol.g, 0.0); 

        br3_or_br1 = max(br3, br1);
        h = (2.0 + (inCol.b - inCol.r) / dlt) * (1.0 - br3_or_br1) + (h*br3_or_br1);

        br4 = 1.0 - br2*br3;
        br4_or_br1 = max(br4, br1);
        h = (4.0 + (inCol.r - inCol.g) / dlt) * (1.0 - br4_or_br1) + (h*br4_or_br1);

        h = h*(1.0 - br1);

        hue_a = abs(h); // between h of -1 and 1 are skin tones
        a = dlt;      // Reducing enhancements on small rgb differences

        // Reduce the enhancements on skin tones.    
        a = step(1.0, hue_a) * a * (hue_a * 0.67 + 0.33) + step(hue_a, 1.0) * a;                                    
        a *= (vibrance - 1.0);
        s = (1.0 - a) * s + a * pow(s, 0.25);

        i = floor(h);
        f = h - i;

        p1 = v * (1.0 - s);
        p2 = v * (1.0 - (s * f));
        p3 = v * (1.0 - (s * (1.0 - f)));

        inCol.rgb = vec3(0.0); 
        i += 6.0;
        //use = 1 << ((int)i % 6);
        use = int(pow(2.0,mod(i,6.0)));
        a = float(and(use , 1)); // i == 0;
        use >>= 1;
        inCol.rgb += a * vec3(v, p3, p1);

        a = float(and(use , 1)); // i == 1;
        use >>= 1;
        inCol.rgb += a * vec3(p2, v, p1); 

        a = float( and(use,1)); // i == 2;
        use >>= 1;
        inCol.rgb += a * vec3(p1, v, p3);

        a = float(and(use, 1)); // i == 3;
        use >>= 1;
        inCol.rgb += a * vec3(p1, p2, v);

        a = float(and(use, 1)); // i == 4;
        use >>= 1;
        inCol.rgb += a * vec3(p3, p1, v);

        a = float(and(use, 1)); // i == 5;
        use >>= 1;
        inCol.rgb += a * vec3(v, p1, p2);

        outCol = inCol;
    }
    return outCol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Same texCoord as in Minecraft
    vec2 texCoord = fragCoord/iResolution.xy;
    // Own uv variable
    vec2 uv = texCoord;

    // Center origin around screen center (range -0.5, 0.5)
    uv -= .5;

    // Squish image in excessive dimension
    float r = (iResolution.x/iResolution.y) / (target_resolution.x/target_resolution.y);
    if (r >= 1.) uv.x *= r;
    else uv.y /= r;

    // Recenter origin to start at 0 (range 0.0, 1.0)
    uv += .5;
    uv.y -= 0.5/r - 0.5;    

    float divisionLine;
    //divisionLine = 0.5;
    float d = cubicPulse(divisionLine,0.14,uv.x);
    vec3 maskColor = vec3(d);
    float pct = plot(uv,d);

    //
    //maskColor = (1.0-pct)*maskColor+pct*vec3(0.0,1.0,0.0);
    maskColor = maskColor+pct*vec3(0.0,1.0,0.0);

    //
    float PI = 3.1415926;
    float time = iTime * 0.08; 
    float scale = 1.3;

    vec4 vcolor0 = vec4(0.259,0.482,1.000,1.0);
    vec4 vcolor1 = vec4(0.278,0.471,1.000,1.0);
    vec4 vcolor2 = vec4(0.459,0.604,1.000,1.0);

    float step0 = 0.1 * scale;
    float step1 = 0.4 * scale;
    float step2 = 0.6 * scale;
    float step3 = 0.65  * scale;
    float step4 = 0.85  * scale;

    float offset = uv.x - time;
    float y = mod(offset,scale);

    vec4 darkColor; 
    if (y < step0) {
    float dist0 = smoothstep(0.0, step0, y);
    darkColor = mix(vcolor0, vcolor1, dist0);
    }

    if (y > step0 && y < step1) {
    float dist1 = smoothstep(step0, step1, y);
    darkColor = mix(vcolor1, vcolor2, dist1);
    //darkColor = correct(vcolor1, vcolor2, dist1);
    }

    else if (y > step1) {
    float dist2 = smoothstep(step1, step2, y);
    darkColor = mix(vcolor2, vcolor0,dist2);
    //darkColor = correct(vcolor2, vcolor3, dist2);
    }

    darkColor = sqrt(darkColor);

    //darkColorMask
    float darkMaskd = cubicPulse(divisionLine,0.24,uv.x);
    vec3 darkMaskColor = vec3(darkMaskd);
    float darkMaskpct = plot(uv,darkMaskd);

    vec3 darkColorMask = darkMaskColor+darkMaskpct*vec3(0.0,1.0,0.0);

    darkColor = mix(darkColor,vec4(0.361,0.639,1.000,1.),darkColorMask.r);

    //
    float qscale = .25;
    float qtime = iTime* 0.0;

    float qstep0 = 0.15 * qscale;
    float qstep1 = 0.25 * qscale;
    float qstep2 = 0.45 * qscale;
    float qstep3 = 0.65 * qscale;

    vec4 qcolor0 = vec4(1.000,0.851,0.702,1.0);

    vec4 qcolor1 = vec4(1.000,0.596,0.380,1.0);

    vec4 qcolor2 = vec4(1.000,0.545,0.302,1.0);

    vec4 qcolor3 = vec4(1.000,0.557,0.322,1.0);

    vec4 qcolor4 = vec4(1.000,0.749,0.502,1.0);

    float brightness4 = 0.15;
	float contrast4 = 1.0;
    float saturation4 = 1.;
    qcolor4 = saturationMatrix(saturation4) * qcolor4; 
    brightnessAdjust(qcolor4, brightness4); 
    contrastAdjust(qcolor4, contrast4); 

    float qoffset = uv.x-qtime;
    float qy = mod(qoffset,qscale);

    vec4 lightColor; 
    if (qy < qstep0) {
    float dist0 = smoothstep(0.0, qstep0, qy);
    //lightColor = vec4(dist0);
    //lightColor = mix(qcolor0, qcolor1, dist0);
    lightColor = correct(qcolor0, qcolor1, dist0);
    }

    if (qy > qstep0 && qy < qstep1) {
    float dist1 = smoothstep(qstep0, qstep1, qy);
    //lightColor = vec4(dist1);
    //lightColor = mix(qcolor1, qcolor2, dist1);
    lightColor = correct(qcolor1, qcolor2, dist1);
    }

    //
    else if (qy > qstep1 && qy < qstep2) {
    float dist2 = smoothstep(qstep1, qstep2, qy);
    //lightColor = mix(qcolor2, qcolor3, dist2);
    lightColor = correct(qcolor2, qcolor3, dist2);
    }

    //
    else if (qy > qstep2 && qy < qstep3) {
    float dist02 = smoothstep(qstep2, qstep3, qy);
    //lightColor = vec4(dist02);
    //lightColor = mix(qcolor3, qcolor4, dist02);
    lightColor = correct(qcolor3, qcolor4, dist02);
    }

    //
    else if (qy > qstep3){
    float dist3 = smoothstep(qstep3, 1.0 * qscale, qy);
    //lightColor = vec4(dist3);
    //lightColor = mix(qcolor4, qcolor0, dist3);
    lightColor = correct(qcolor4, qcolor0, dist3);
    }

    lightColor = sqrt(lightColor);
    //lightColor = vec4(1.0);

    //
    fragColor = mix(darkColor, lightColor, maskColor.r);

    //ending mask mask
    float endMask = smoothstep(0.62,1.0,uv.x);
    vec4 endMaskColor = vec4(0.741,0.812,1.000,1.0);
    //fragColor = mix(fragColor,endMaskColor,endMask);

    // Black bars at the sides
    if (uv.x < 0. || uv.x > 1. || uv.y < 0. || uv.y > 1.) fragColor = vec4(0);    

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

int enc(int c) {
    int off = 65;
    if (c > 25) off = 71;
    if (c > 51) off = -4;
    if (c == 62) off = 43 - c;
    if (c == 63) off = 47 - c;
    return c + off;
}

int dec(int c) {
    int off = 65;
    if (c > 96) off = 71;
    if (c < 58) off = -4;
    if (c == 43) off = c - 62;
    if (c == 47) off = c - 63;
    if (c == 61) off = c;
    return c - off;
}

ivec4 base64_encode(ivec3 triplet) {
    return ivec4(
        enc(triplet.x >> 2),
        enc(((triplet.x & 3) << 4) | (triplet.y >> 4)),
        enc(((triplet.y & 15) << 2) | (triplet.z >> 6)),
        enc(triplet.z & 63)
    );
}

ivec4 base64_encode(ivec2 triplet) {
    return ivec4(
        enc(triplet.x >> 2),
        enc(((triplet.x & 3) << 4) | (triplet.y >> 4)),
        enc(((triplet.y & 15) << 2)),
        61
   );
}

ivec4 base64_encode(int triplet) {
    return ivec4(
        enc(triplet >> 2),
        enc((triplet & 3) << 4),
        61,
        61
   );
}


ivec3 base64_decode(ivec4 quad) {
    quad = ivec4(
        dec(quad.x),
        dec(quad.y),
        dec(quad.z),
        dec(quad.w)
    );
    return ivec3(
        (quad.x << 2) | (quad.y >> 6),
        ((quad.y << 4) | (quad.z >> 2)) & 0xff,
        ((quad.z << 6) | quad.w) & 0xff
    );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec3 triplet = ivec3(255.0 * texelFetch(iChannel0, ivec2(fragCoord), 0).rgb);
    ivec4 encoded = base64_encode(triplet);

    // Output to screen
    if (fragCoord.x > iResolution.x * (0.75 + 0.25*sin(iTime))) fragColor = vec4(triplet, 255.0) / 255.0;
    if (fragCoord.x < iResolution.x * (0.75 + 0.25*sin(iTime))) fragColor = vec4(base64_decode(encoded), 255.0) / 255.0;
    if (fragCoord.x < iResolution.x * (0.5 + 0.25*sin(iTime))) fragColor = vec4(encoded) / 255.0;
    if (fragCoord.x < iResolution.x * (0.25 + 0.25*sin(iTime))) fragColor = texelFetch(iChannel1, ivec2(fragCoord), 0) / 255.0;
    if (abs(fragCoord.x - iResolution.x * (0.25 + 0.25*sin(iTime))) < 2.0) fragColor = vec4(1.0, 1.0, 0.0, 1.0);
    if (abs(fragCoord.x - iResolution.x * (0.5 + 0.25*sin(iTime))) < 2.0) fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    if (abs(fragCoord.x - iResolution.x * (0.75 + 0.25*sin(iTime))) < 2.0) fragColor = vec4(0.0, 1.0, 0.0, 1.0);
}

buffer_a_code:
// How fast is it?
// Set this to 500 or something and calculate:
// BENCHMARK_ITERATIONS * width * height * 4 * fps / 1e9 = gigabytes per second
//
// On an M2 Macbook Air, around 30 GB/s encode.
// (YMMV with real data, especially if you need to truck the data over the PCIe bus.)
//
// A proper compute shader implementation should be a bit faster still.
//
// For comparison, Chrome's btoa runs at about 500-600 MB/s.
// I got a JavaScript implementation to run at about 300 MB/s.
//
// I don't know who needs a memory-speed btoa but there you have it.
//
#define BENCHMARK_ITERATIONS 0


int enc(int c) {
    int off = 65;
    if (c > 25) off = 71;
    if (c > 51) off = -4;
    if (c == 62) off = 43 - c;
    if (c == 63) off = 47 - c;
    return c + off;
}

ivec4 base64_encode(ivec3 triplet) {
    return ivec4(
        enc(triplet.x >> 2),
        enc(((triplet.x & 3) << 4) | (triplet.y >> 4)),
        enc(((triplet.y & 15) << 2) | (triplet.z >> 6)),
        enc(triplet.z & 63)
    );
}

ivec2 quadIndexToTexCoord(int quadIndex, int width) {
    return ivec2(quadIndex % width, quadIndex / width);
}

vec4 readVec4(int byteIndex, int width) {
    int quadIndex = byteIndex / 4;
    ivec2 texCoord1 = quadIndexToTexCoord(quadIndex, width);
    ivec2 texCoord2 = quadIndexToTexCoord(quadIndex + 1, width);
    vec4 quad1 = texelFetch(iChannel0, texCoord1, 0);
    vec4 quad2 = texelFetch(iChannel0, texCoord2, 0);
    vec4 result = quad1;
    int byteOffset = byteIndex % 4;
    if (byteOffset == 1) result = vec4(quad1.yzw, quad2.x);
    if (byteOffset == 2) result = vec4(quad1.zw, quad2.xy);
    if (byteOffset == 3) result = vec4(quad1.w, quad2.xyz);
    return result;
}

ivec4 base64EncodeTexture(in vec2 fragCoord) {
    // Fetch the triplet of bytes corresponding to the current pixel and encode it into a quad.
    int w = int(iChannelResolution[0].x);
    int h = int(iChannelResolution[0].y);
    int byteIndex = ((int(fragCoord.y) * int(iResolution.x) + int(fragCoord.x)) * 3) % (w * h * 4);
    ivec3 triplet = ivec3(readVec4(byteIndex, w).xyz * 255.0);
    return base64_encode(triplet);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(base64EncodeTexture(fragCoord));
    for (int i = 0; i < BENCHMARK_ITERATIONS; i++) {
      fragColor += vec4(base64EncodeTexture(fragCoord + vec2(float(i*37), float(i*17))));
    }
}

