// How fast is it?
// Set this to 500 or something and calculate:
// BENCHMARK_ITERATIONS * width * height * 4 * fps / 1e9 = gigabytes per second
//
// On an M2 Macbook Air, around 30 GB/s decode.
// (YMMV with real data, especially if you need to truck the data over the PCIe bus.)
//
// A proper compute shader implementation should be a bit faster still.
//
// For comparison, Chrome's atob runs at about 700 MB/s.
//
// I don't know who needs a memory-speed atob either, but there you have it.
//
#define BENCHMARK_ITERATIONS 0

int dec(int c) {
    int off = 65;
    if (c > 96) off = 71;
    if (c < 58) off = -4;
    if (c == 43) off = c - 62;
    if (c == 47) off = c - 63;
    if (c == 61) off = c;
    return c - off;
}

ivec3 base64_decode(ivec4 quad) {
    quad = ivec4(
        dec(quad.x),
        dec(quad.y),
        dec(quad.z),
        dec(quad.w)
    );
    return ivec3(
        (quad.x << 2) | (quad.y >> 6),
        ((quad.y << 4) | (quad.z >> 2)) & 0xff,
        ((quad.z << 6) | quad.w) & 0xff
    );
}

ivec2 quadIndexToTexCoord(int quadIndex, int width) {
    return ivec2(quadIndex % width, quadIndex / width);
}

vec4 readVec4(int byteIndex, int width) {
    int quadIndex = byteIndex / 4;
    ivec2 texCoord1 = quadIndexToTexCoord(quadIndex, width);
    ivec2 texCoord2 = quadIndexToTexCoord(quadIndex + 1, width);
    vec4 quad1 = texelFetch(iChannel0, texCoord1, 0);
    vec4 quad2 = texelFetch(iChannel0, texCoord2, 0);
    vec4 result = quad1;
    int byteOffset = byteIndex % 4;
    if (byteOffset == 1) result = vec4(quad1.yzw, quad2.x);
    if (byteOffset == 2) result = vec4(quad1.zw, quad2.xy);
    if (byteOffset == 3) result = vec4(quad1.w, quad2.xyz);
    return result;
}

ivec4 base64DecodeTexture(in vec2 fragCoord, int width) {
    // Convert the fragCoord to the byte index in the decoded string.
    // Convert the decoded byte index to the encoded byte index for the triplet.
    //
    // byteIndex 8.
    //            V
    // [AAAB|BBCC|CDDD]
    //
    // encoded 12 . = (byteIndex / 3) * 4 = tripletByteIndex
    //            V
    // [aaaa|bbbb|cccc|dddd]
    //
    // Grab the two triplets from encoded.
    //
    // [cccc|dddd] = tripletByteIndex, tripletByteIndex + 4
    //
    // Decode the encoded triplets.
    //
    // [CCC|DDD]
    //
    // Extract the wanted bytes.
    //     V = byteIndex % 3 = 8 % 3 = 2
    // [CC|CDDD] 

    int byteIndex = (int(fragCoord.y) * width + int(fragCoord.x)) * 4;
    int tripletByteIndex = (byteIndex / 3) * 4;

    int w = int(iChannelResolution[0].x);

    ivec4 encodedTriplet1 = ivec4(readVec4(tripletByteIndex, w));
    ivec4 encodedTriplet2 = ivec4(readVec4(tripletByteIndex + 4, w));

    ivec3 triplet1 = base64_decode(encodedTriplet1);
    ivec3 triplet2 = base64_decode(encodedTriplet2);

    int byteOffset = byteIndex % 3;

    ivec4 quad = ivec4(triplet1.xyz, triplet2.x);
    if (byteOffset == 1) quad = ivec4(triplet1.yz, triplet2.xy);
    if (byteOffset == 2) quad = ivec4(triplet1.z, triplet2.xyz);

    return quad;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(base64DecodeTexture(fragCoord, 512)) / 255.0;
    for (int i = 0; i < BENCHMARK_ITERATIONS; i++) {
      fragColor += vec4(base64DecodeTexture(fragCoord + vec2(float(i*37), float(i*17)), 512));
    }
}

