// CC0: B/W logarithmic circles
//  Inspired by this tweet: https://twitter.com/concinnus/status/1654244526297583616?s=20
//  Not an exact copy but good enough for a quick morning hack

#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

const float ExpBy = log2(2.24);

float forward(float l) {
  return exp2(ExpBy*l);
}

float reverse(float l) {
  return log2(l)/ExpBy;
}

float modPolar(inout vec2 p, float repetitions) {
  float angle = TAU/repetitions;
  float a = atan(p.y, p.x) + angle/2.;
  float r = length(p);
  float c = floor(a/angle);
  a = mod(a,angle) - angle/2.;
  p = vec2(cos(a), sin(a))*r;
  // For an odd number of repetitions, fix cell index of the cell in -x direction
  // (cell index would be e.g. -5 and 5 in the two halves of the cell):
  if (abs(c) >= (repetitions/2.0)) c = abs(c);
  return c;
}

vec3 effect(vec2 p) {
  float aa = 4.0/RESOLUTION.y;
  float ltm = 0.75*TIME;
  mat2 rot0 = ROT(-0.5*ltm); 
  float mtm = fract(ltm);
  float ntm = floor(ltm);
  float zz = forward(mtm);

  vec2 p0 = p;
  p0 *= rot0;
  p0 /= zz;

  float l0 = length(p0);

  float n0 = ceil(reverse(l0));
  float r0 = forward(n0);
  float r1 = forward(n0-1.0);
  float r = (r0+r1)/2.0;
  float w = r0-r1;
  float nn = n0;
  n0 -= ntm;
  vec2 p1 = p0;
  p1 *= ROT(3.0*n0*TAU/16.0);
  float n1 = modPolar(p1, 8.0);
  p1.x -= r;

  float a = 0.5*ltm+n1/8.0;
  a = fract(a);
  float d1 = length(p1)-0.5*w;
  float d2 = length(p1)-0.5*w*smoothstep(0.0, 0.45, mod(a, 0.5));
  d1 *= zz;
  d2 *= zz;
  vec3 col = vec3(0.2*smoothstep(-sqrt(0.5), sqrt(0.5), sin(0.5*TAU*p.y/aa)));
  vec3 ccol = vec3(1.0)*smoothstep(0.0, -aa, d2);
  if (a >= 0.5) ccol = 1.0-ccol;
  col = mix(col, ccol, smoothstep(0.0, -aa, d1));
  col = sqrt(col);
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = effect(p);

  fragColor = vec4(col, 1.0);
}

const vec2 target_resolution = vec2(940, 86);

vec4 correct(vec4 col1, vec4 col2, float p)
{
 	vec4 temp = col1 * col1;
    vec4 temp2 = col2 * col2;
    return sqrt(mix(temp, temp2, p));
}

float cubicPulse( float c, float w, float x ){
    x = abs(x - c);
    if( x>w ) return 0.0;
    x /= w;
    return 1.0 - x*x*(3.0-2.0*x);
}

float plot(vec2 st, float pct){
  return  smoothstep( pct-0.02, pct, st.y) -
          smoothstep( pct, pct+0.02, st.y);
}

mat4 saturationMatrix( float saturation ) {
    vec3 luminance = vec3( 0.3086, 0.6094, 0.0820 );
    float oneMinusSat = 1.0 - saturation;
    vec3 red = vec3( luminance.x * oneMinusSat );
    red.r += saturation;

    vec3 green = vec3( luminance.y * oneMinusSat );
    green.g += saturation;

    vec3 blue = vec3( luminance.z * oneMinusSat );
    blue.b += saturation;

    return mat4( 
        red,     0,
        green,   0,
        blue,    0,
        0, 0, 0, 1 );
}

void brightnessAdjust( inout vec4 color, in float b) {
    color.rgb += b;
}

void contrastAdjust( inout vec4 color, in float c) {
    float t = 0.5 - c * 0.5; 
    color.rgb = color.rgb * c + t;
}

int modi(int x, int y) {
    return x - y * (x / y);
}

int and(int a, int b) {
    int result = 0;
    int n = 1;
	const int BIT_COUNT = 32;

    for(int i = 0; i < BIT_COUNT; i++) {
        if ((modi(a, 2) == 1) && (modi(b, 2) == 1)) {
            result += n;
        }

        a >>= 1;
        b >>= 1;
        n <<= 1;

        if (!(a > 0 && b > 0))
            break;
    }
    return result;
}

// forked from https://www.shadertoy.com/view/llGSzK
// performance optimized by Ruofei
vec4 vibrance(vec4 inCol, float vibrance) //r,g,b 0.0 to 1.0,  vibrance 1.0 no change, 0.0 image B&W.
{
 	vec4 outCol;
    if (vibrance <= 1.0)
    {
        float avg = dot(inCol.rgb, vec3(0.3, 0.6, 0.1));
        outCol.rgb = mix(vec3(avg), inCol.rgb, vibrance); 
    }
    else // vibrance > 1.0
    {
        float hue_a, a, f, p1, p2, p3, i, h, s, v, amt, _max, _min, dlt;
        float br1, br2, br3, br4, br5, br2_or_br1, br3_or_br1, br4_or_br1, br5_or_br1;
        int use;

        _min = min(min(inCol.r, inCol.g), inCol.b);
        _max = max(max(inCol.r, inCol.g), inCol.b);
        dlt = _max - _min + 0.00001 /*Hack to fix divide zero infinities*/;
        h = 0.0;
        v = _max;

		br1 = step(_max, 0.0);
        s = (dlt / _max) * (1.0 - br1);
        h = -1.0 * br1;

		br2 = 1.0 - step(_max - inCol.r, 0.0); 
        br2_or_br1 = max(br2, br1);
        h = ((inCol.g - inCol.b) / dlt) * (1.0 - br2_or_br1) + (h*br2_or_br1);

		br3 = 1.0 - step(_max - inCol.g, 0.0); 

        br3_or_br1 = max(br3, br1);
        h = (2.0 + (inCol.b - inCol.r) / dlt) * (1.0 - br3_or_br1) + (h*br3_or_br1);

        br4 = 1.0 - br2*br3;
        br4_or_br1 = max(br4, br1);
        h = (4.0 + (inCol.r - inCol.g) / dlt) * (1.0 - br4_or_br1) + (h*br4_or_br1);

        h = h*(1.0 - br1);

        hue_a = abs(h); // between h of -1 and 1 are skin tones
        a = dlt;      // Reducing enhancements on small rgb differences

        // Reduce the enhancements on skin tones.    
        a = step(1.0, hue_a) * a * (hue_a * 0.67 + 0.33) + step(hue_a, 1.0) * a;                                    
        a *= (vibrance - 1.0);
        s = (1.0 - a) * s + a * pow(s, 0.25);

        i = floor(h);
        f = h - i;

        p1 = v * (1.0 - s);
        p2 = v * (1.0 - (s * f));
        p3 = v * (1.0 - (s * (1.0 - f)));

        inCol.rgb = vec3(0.0); 
        i += 6.0;
        //use = 1 << ((int)i % 6);
        use = int(pow(2.0,mod(i,6.0)));
        a = float(and(use , 1)); // i == 0;
        use >>= 1;
        inCol.rgb += a * vec3(v, p3, p1);

        a = float(and(use , 1)); // i == 1;
        use >>= 1;
        inCol.rgb += a * vec3(p2, v, p1); 

        a = float( and(use,1)); // i == 2;
        use >>= 1;
        inCol.rgb += a * vec3(p1, v, p3);

        a = float(and(use, 1)); // i == 3;
        use >>= 1;
        inCol.rgb += a * vec3(p1, p2, v);

        a = float(and(use, 1)); // i == 4;
        use >>= 1;
        inCol.rgb += a * vec3(p3, p1, v);

        a = float(and(use, 1)); // i == 5;
        use >>= 1;
        inCol.rgb += a * vec3(v, p1, p2);

        outCol = inCol;
    }
    return outCol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Same texCoord as in Minecraft
    vec2 texCoord = fragCoord/iResolution.xy;
    // Own uv variable
    vec2 uv = texCoord;

    // Center origin around screen center (range -0.5, 0.5)
    uv -= .5;

    // Squish image in excessive dimension
    float r = (iResolution.x/iResolution.y) / (target_resolution.x/target_resolution.y);
    if (r >= 1.) uv.x *= r;
    else uv.y /= r;

    // Recenter origin to start at 0 (range 0.0, 1.0)
    uv += .5;
    uv.y -= 0.5/r - 0.5;    

    float divisionLine;
    float d = cubicPulse(divisionLine,0.14,uv.x);
    vec3 maskColor = vec3(d);
    float pct = plot(uv,d);

    //
    maskColor = (1.0-pct)*maskColor+pct*vec3(0.0,1.0,0.0);
    //maskColor = maskColor+pct*vec3(0.0,1.0,0.0);

    //
    float PI = 3.1415926;
    float time = iTime * 0.08; 
    float scale = 1.3;

    vec4 vcolor0 = vec4(0.259,0.482,1.000,1.0);
    vec4 vcolor1 = vec4(0.278,0.471,1.000,1.0);
    vec4 vcolor2 = vec4(0.561,0.678,1.000,1.0);
    vec4 vcolor3 = vec4(1.000,0.612,0.400,1.0);
    vec4 vcolor4 = vec4(1.000,0.788,0.580,1.0);

    float step0 = 0.1 * scale;
    float step1 = 0.4 * scale;
    float step2 = 0.6 * scale;
    float step3 = 0.65  * scale;
    float step4 = 0.85  * scale;

    float offset = uv.x - time;
    float y = mod(offset,scale);

    vec4 darkColor; 
    if (y < step0) {
    float dist0 = smoothstep(0.0, step0, y);
    darkColor = mix(vcolor0, vcolor1, dist0);
    darkColor = correct(vcolor0, vcolor1, dist0);
    }

    if (y > step0 && y < step1) {
    float dist1 = smoothstep(step0, step1, y);
    darkColor = mix(vcolor1, vcolor2, dist1);
    //darkColor = correct(vcolor1, vcolor2, dist1);
    }

    else if (y > step1 && y < step2) {
    float dist2 = smoothstep(step1, step2, y);
    darkColor = mix(vcolor2, vcolor3, dist2);
    //darkColor = correct(vcolor2, vcolor3, dist2);
    }

    else if (y > step2 && y < step3) {
    float dist02 = smoothstep(step2, step3, y);
    darkColor = mix(vcolor3, vcolor4, dist02);
    darkColor = correct(vcolor3, vcolor4, dist02);
    }

    else if (y > step3 && y < step4){
    float dist3 = smoothstep(step3, step4, y);
    darkColor = mix(vcolor4, vcolor0, dist3);
    //darkColor = correct(vcolor4, vcolor0, dist3);
    }

    else if(y>step4){
    float dist3 = smoothstep(step3, step4, y);
    darkColor = vcolor0;
    }

    darkColor = sqrt(darkColor);

    //darkColorMask
    float darkMaskd = cubicPulse(divisionLine,0.24,uv.x);
    vec3 darkMaskColor = vec3(darkMaskd);
    float darkMaskpct = plot(uv,darkMaskd);

    vec3 darkColorMask = darkMaskColor+darkMaskpct*vec3(0.0,1.0,0.0);

    darkColor = mix(darkColor,vec4(0.361,0.639,1.000,1.),darkColorMask.r);

    //
    float qscale = 0.25;
    float qtime = iTime* 0.015;

    float qstep0 = 0.15 * qscale;
    float qstep1 = 0.35 * qscale;
    float qstep2 = 0.45 * qscale;
    float qstep3 = 0.65 * qscale;

    vec4 qcolor0 = vec4(0.722,0.804,1.000,1.0);

    vec4 qcolor1 = vec4(0.722,0.804,1.000,1.0);

    vec4 qcolor2 = vec4(1.000,0.663,0.522,1.0);

    vec4 qcolor3 = vec4(1.000,0.820,0.702,1.0);

    vec4 qcolor4 = vec4(1.000,1.000,1.000,1.0);

    float brightness4 = 0.15;
	float contrast4 = 1.0;
    float saturation4 = 1.;
    qcolor4 = saturationMatrix(saturation4) * qcolor4; 
    brightnessAdjust(qcolor4, brightness4); 
    contrastAdjust(qcolor4, contrast4); 

    float qoffset = uv.x-qtime;
    float qy = mod(qoffset,qscale);

    vec4 lightColor; 
    if (qy < qstep0) {
    float dist0 = smoothstep(0.0, qstep0, qy);
    //lightColor = vec4(dist0);
    //lightColor = mix(qcolor0, qcolor1, dist0);
    lightColor = correct(qcolor0, qcolor1, dist0);
    }

    if (qy > qstep0 && qy < qstep1) {
    float dist1 = smoothstep(qstep0, qstep1, qy);
    //lightColor = vec4(dist1);
    //lightColor = mix(qcolor1, qcolor2, dist1);
    lightColor = correct(qcolor1, qcolor2, dist1);
    }

    //
    else if (qy > qstep1 && qy < qstep2) {
    float dist2 = smoothstep(qstep1, qstep2, qy);
    //lightColor = mix(qcolor2, qcolor3, dist2);
    lightColor = correct(qcolor2, qcolor3, dist2);
    }

    //
    else if (qy > qstep2 && qy < qstep3) {
    float dist02 = smoothstep(qstep2, qstep3, qy);
    //lightColor = vec4(dist02);
    //lightColor = mix(qcolor3, qcolor4, dist02);
    lightColor = correct(qcolor3, qcolor4, dist02);
    }

    //
    else if (qy > qstep3){
    float dist3 = smoothstep(qstep3, 1.0 * qscale, qy);
    //lightColor = vec4(dist3);
    //lightColor = mix(qcolor4, qcolor0, dist3);
    lightColor = correct(qcolor4, qcolor0, dist3);
    }

    lightColor = sqrt(lightColor);
    //lightColor = vec4(1.0);

    //
    fragColor = mix(darkColor, lightColor, maskColor.r);

    //ending mask mask
    float endMask = smoothstep(0.62,1.0,uv.x);
    vec4 endMaskColor = vec4(0.741,0.812,1.000,1.0);
    //fragColor = mix(fragColor,endMaskColor,endMask);

    // Black bars at the sides
    if (uv.x < 0. || uv.x > 1. || uv.y < 0. || uv.y > 1.) fragColor = vec4(0);    


}

/* 
   Triangle Gradient Background by kbnt
   License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

   Inspired by / Remix of "ice and fire" by mattz (https://www.shadertoy.com/view/MdfBzl)
*/

const float scaleAmnt = 6.2;
const float motionSpeed = 0.1;
const float motionSize  = 0.45; // max 0.5
const float colourSpeed = 0.1;

/*
const float s6  = 0.866025388240814208984; // sin(60)
const float ti6 = 0.577350258827209472656; // cos(60) / sin(60)
const float si6 = 1.154700517654418945310; // 1 / sin(60)

const mat2 tri2cart = mat2(s6, -0.5, 0, 1);
const mat2 cart2tri = mat2(si6, ti6, 0, 1);
/**/

/**/
const float s3 = 1.7320508075688772; // 2 * sin(60)
const float i3 = 0.5773502691896258; // tan(60)

const mat2 tri2cart = mat2(1.0, 0.0, -0.5, 0.5*s3);
const mat2 cart2tri = mat2(1.0, 0.0, i3, 2.0*i3);
/**/

/*
const mat2 tri2cart = mat2(1, 0, 0, 1);
const mat2 cart2tri = mat2(1, 0, 0, 1);
/**/

//////////////////////////////////////////////////////////////////////
// cosine based palette 
// adapted from https://www.shadertoy.com/view/ll2GD3

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return clamp(a + b*cos( 6.28318*(c*t+d) ), 0.0, 1.0);
}

vec3 pala(in float t) {
    return pal( t, vec3(0.5),vec3(0.5),vec3(0.8, 0.8, 0.5),vec3(0, 0.2, 0.5) );
}

vec3 colourForPoint(vec2 uv) {
    float t = colourSpeed*iTime;

    // pallet sample
    vec3 col00 = pala( t+0.00 );
    vec3 col01 = pala( t+0.25 );
    vec3 col11 = pala( t+0.50 );
    vec3 col10 = pala( t+0.75 );

    // mix colours
    vec3 colorA = mix(col00,col10,uv.x);
    vec3 colorB = mix(col01,col11,uv.x);
    return mix(colorA, colorB, uv.y);
}

//////////////////////////////////////////////////////////////////////
// from https://www.shadertoy.com/view/4djSRW

#define HASHSCALE1 .1031
#define HASHSCALE3 vec3(443.897, 441.423, 437.195)

float hash12(vec2 p) {
    vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);   
}

vec2 hash23(vec3 p3) {
	p3 = fract(p3 * HASHSCALE3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);
}


//////////////////////////////////////////////////////////////////////
// generate a random point on a circle from 3 integer coords (x, y, t)

vec2 randCircle(vec3 p) {

    vec2 rt = hash23(p);

    float r = sqrt(rt.x);
    float theta = 6.283185307179586 * rt.y;

    return r*vec2(cos(theta), sin(theta));

}

//////////////////////////////////////////////////////////////////////
// make a time-varying cubic spline at integer coords p that stays
// inside a unit circle

vec2 randCircleSpline(vec2 p, float t) {

    // standard catmull-rom spline implementation
    float t1 = floor(t);
    t -= t1;

    vec2 pa = randCircle(vec3(p, t1-1.0));
    vec2 p0 = randCircle(vec3(p, t1));
    vec2 p1 = randCircle(vec3(p, t1+1.0));
    vec2 pb = randCircle(vec3(p, t1+2.0));

    vec2 m0 = 0.5*(p1 - pa);
    vec2 m1 = 0.5*(pb - p0);

    vec2 c3 = 2.0*p0 - 2.0*p1 + m0 + m1;
    vec2 c2 = -3.0*p0 + 3.0*p1 - 2.0*m0 - m1;
    vec2 c1 = m0;
    vec2 c0 = p0;

    return (((c3*t + c2)*t + c1)*t + c0) * 0.8;

}

//////////////////////////////////////////////////////////////////////
// perturbed point from index

vec2 triPoint(vec2 p) {
    float t0 = hash12(p);
    return tri2cart*p + motionSize*randCircleSpline(p, motionSpeed*iTime + t0);
}


//////////////////////////////////////////////////////////////////////
// https://stackoverflow.com/questions/2049582/how-to-determine-if-a-point-is-in-a-2d-triangle

float sgn (vec2 p1, vec2 p2, vec2 p3)
{
    return (p1.x - p3.x) * (p2.y - p3.y) - (p2.x - p3.x) * (p1.y - p3.y);
}

bool PointInTriangle (vec2 pt, vec2 v1, vec2 v2, vec2 v3)
{
    float d1, d2, d3;
    bool has_neg, has_pos;

    d1 = sgn(pt, v1, v2);
    d2 = sgn(pt, v2, v3);
    d3 = sgn(pt, v3, v1);

    has_neg = (d1 < 0.0) || (d2 < 0.0) || (d3 < 0.0);
    has_pos = (d1 > 0.0) || (d2 > 0.0) || (d3 > 0.0);

    return !(has_neg && has_pos);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // scale from screen space to scene space
    float scl = scaleAmnt / iResolution.y;

    // get 2D scene coords
    vec2 p = (fragCoord - 0.5*iResolution.xy) * scl;

    // get triangular base coords
    vec2 tfloor = floor(cart2tri * p + 0.5);

    // precompute 9 neighboring points
    vec2 pts[9];

    for (int i=0; i<3; ++i) {
        for (int j=0; j<3; ++j) {
            pts[3*i+j] = triPoint(tfloor + vec2(i-1, j-1));
        }
    }

    vec2 center;

    // note: must look at 4 quads cause when the triangle our current  
    //       pixel is in gets randomly nudged in the positive direction,
    //       our pixel may end up in another triangle
    // for each of the 4 quads:
    for (int i=0; i<2; ++i) {
        for (int j=0; j<2; ++j) {

            // look at lower and upper triangle in this quad
            vec2 t00 = pts[3*i+j  ];
            vec2 t10 = pts[3*i+j+3];
            vec2 t01 = pts[3*i+j+1];
            vec2 t11 = pts[3*i+j+4];

            // lower
            if (PointInTriangle(p, t00, t10, t11)) 
                center = (t00 + t10 + t11) / 3.0; // get centeroid of triangle

            // upper
            if (PointInTriangle(p, t00, t11, t01))
                center = (t00 + t11 + t01) / 3.0;

        }
    }


    // convert from scene space to uv to sample colour 
    center = center / scl;
    center += 0.5*iResolution.xy;
    center += 0.5;
    center = center / iResolution.xy;

    vec4 col = vec4(colourForPoint(center), 1.0);

    // final pixel color
    fragColor = col;

}

struct material{
    float d;
    float id;
    vec3 col;
    //
};
mat2 rot(float angle){
    float c= cos(angle);
    float s = sin(angle);
    return mat2(c,-s,s,c);
}
float smin(float a,float b,float k){
    float h = clamp(0.5+0.5*(a-b)/k,0.0,1.0);
    return mix(a,b,h)-k*h*(1.0-h);
}

material map(vec3 p){
    material mat;
    float sphere=  length(p)-1.0;
    if(sphere<=0.001) {
        mat.id = 1.0;
        mat.col = vec3(1.0,0.0,0.0);
    }
    float plane = p.y+1.5;
    if(plane<=0.001) {
        mat.id = 2.0;
        //y-1.5 
        mat.col = mod(floor(p.x)+floor(p.z),2.0)*vec3(1.0);
    };
    mat.d = min(sphere,plane);
    return mat;

}

material Raymarch(vec3 ro,vec3 rd){
    float d;
    material mat;
    for(int i =0;i<255;i++){
        vec3 p =ro+rd*d;
        mat = map(p);
        float d0 = mat.d;
        if(d0<=0.001||d>=40.) break;
        d+=d0;
    }
    mat.d = d;
    return mat;
}
vec3 GetNormal(vec3 p){
    float d = map(p).d;
    vec2 e = vec2(0.001,0.0);
    float dx = d-map(p-e.xyy).d;
    float dy = d-map(p-e.yxy).d;
    float dz = d-map(p-e.yyx).d;
    return normalize(vec3(dx,dy,dz));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;    
    uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;
    vec3 col = vec3(0.0);

    vec3 ro = vec3(0.0,0.0,-5);
    vec3 rd  = normalize(vec3(uv,1.0));

    material mat = Raymarch(ro,rd);
    if(mat.d<40.0){
        vec3 p = ro+rd*mat.d;
        vec3 n  = GetNormal(p);
        vec3 lightPos = vec3(0,5,-5.0); 
        vec3 lightdir = normalize(lightPos-p);
        float diffuse = dot(n,lightdir);
        diffuse =diffuse*.5+.5;
        col+=diffuse * mat.col;
    }


    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord/iResolution.xy;
    fragColor = textureLod(iChannel0, uv, 0.0);

}

