  col *= sat(rand()+.7);
  float sharp = iResolution.x*.5;
  uv.x+= sin(uv.y*5.+uv.x*2.+iTime)*.02*sat(uv.y);
  vec2 p = uv;
  for (float i = 0.;i<6.;++i)
  {
    float off = mix(.3,.05,i/7.);
    p+= vec2(0.,-off*.45);
    p*=r2d(.4*sat(i));
    p+= vec2(0.,-off*.45);
    p.x = abs(p.x);
    vec2 pos = p;

    float shape = _sqr(pos, vec2(.01,off*.45));
    if (i > 2.9)
        shape = mix(lenny(pos)-.1, length(pos)-.1, -1.+i*.3);
    col = mix(col, vec3(0.),1.-sat(shape*sharp));
    if (i > 2.9)
        col = mix(col, vec3(1.,.1,.2),1.-sat((shape+.05)*sharp));
  }
