    float n_lightness_y_1 = abs(o_fc.y - n_y_1); 
    n_lightness_y_1 = 1.-pow(n_lightness_y_1, 1./3.);

    float n_y_2 = sin(o_fc.x*n_tau+n_tau/3.)*0.2;
    float n_lightness_y_2 = abs(o_fc.y - n_y_2); 
    n_lightness_y_2 = 1.-pow(n_lightness_y_2, 1./3.);

    float n_y_3 = sin(o_fc.x*n_tau+(n_tau/3.)*2.)*0.2;
    float n_lightness_y_3 = abs(o_fc.y - n_y_3); 
    n_lightness_y_3 = 1.-pow(n_lightness_y_3, 1./3.);

    float n_y_comb = 
        (n_y_1 / 3. )
        + (n_y_2 / 3.)
        + (n_y_3 / 3.);
    float n_lightness_y_comb = abs(o_fc.y - n_y_comb); 
    n_lightness_y_comb = 1.-pow(n_lightness_y_comb, 1./10.);

    float n_white = n_lightness_y_comb;
    fragColor = vec4(
        vec4(n_lightness_y_1/3., 0.,0.,1.)
        + vec4(0., n_lightness_y_2/3., 0.,1.)
        + vec4(0.,0.,n_lightness_y_3/3., 1.)
    );
