vec3 rasterize(in vec2 coord)
{
    vec2 px = (2.0*coord-iResolution.xy) / iResolution.y;

    vec3 color = vec3(0.478,0.478,0.478);
    // clear zbuffer
    float mindist = -1000000.0;

    for (int i = 0; i < LONGITUDE * (LATITUDE - 1) * 2; i++)
    {
        ivec2 tCoord = ivec2(i % int(iResolution.x), i / int(iResolution.x) * 7);
        vec4 bounds = texelFetch(iChannel0, ivec2(tCoord.x, tCoord.y + 3), 0);
        if (px.x < bounds.x || px.x > bounds.y || px.y < bounds.z || px.y > bounds.w)
        {
            continue;
        }
        vec4 frb = texelFetch(iChannel0, ivec2(tCoord.x, tCoord.y + 1), 0);
        vec4 frc = texelFetch(iChannel0, ivec2(tCoord.x, tCoord.y + 2), 0);
        vec2 cp0 = frb.xy;
        vec2 cp1 = frb.zw;
        vec2 cp2 = frc.xy;

        vec3 di = vec3( cross2d( cp1 - cp0, px - cp0 ), 
					    cross2d( cp2 - cp1, px - cp1 ), 
					    cross2d( cp0 - cp2, px - cp2 ) );

        if( all(greaterThan(di,vec3(0.0))) )
        {
            vec4 fra = texelFetch(iChannel0, tCoord, 0);
            float w0 = fra.x;
            float w1 = fra.y;
            float w2 = fra.z;

            // calc barycentric coordinates
            vec3 ba = di.yzx / (di.x+di.y+di.z);

            // barycentric interpolation of attributes and 1/z
            float iz = ba.x*w0 + ba.y*w1 + ba.z*w2;

            // recover interpolated attributes
            float z = 1.0/iz;
			// depth (-1/z) buffer test
			if( z>mindist )
			{
				mindist = z;

                vec3 a = texelFetch(iChannel2, ivec2(tCoord.x, tCoord.y), 0).xyz;
                vec3 b = texelFetch(iChannel2, ivec2(tCoord.x, tCoord.y + 1), 0).xyz;
                vec3 c = texelFetch(iChannel2, ivec2(tCoord.x, tCoord.y + 2), 0).xyz;

                vec3 p0 = a * w0;
                vec3 p1 = b * w1;
                vec3 p2 = c * w2;

                vec3 na = texelFetch(iChannel2, ivec2(tCoord.x, tCoord.y + 3), 0).xyz * w0;
                vec3 nb = texelFetch(iChannel2, ivec2(tCoord.x, tCoord.y + 4), 0).xyz * w1;
                vec3 nc = texelFetch(iChannel2, ivec2(tCoord.x, tCoord.y + 5), 0).xyz * w2;

                vec3 n = ba.x*na + ba.y*nb + ba.z*nc;

				// perform lighting/shading 
                vec3 cameraPos = texelFetch(iChannel1, ivec2(0,4), 0).rgb;
                vec3 col = texelFetch(iChannel2, ivec2(tCoord.x, tCoord.y + 6), 0).xyz;
                vec3 worldPos = p0 * ba.x + p1 * ba.y + p2 * ba.z;
                worldPos *= z;
                n = normalize(n*z);
				color = shader(normalize(-cameraPos-worldPos), n, col);

			}
        }
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //fragColor = texelFetch(iChannel2, ivec2(fragCoord/2.), 0)*-5.;
    fragColor = vec4(rasterize(fragCoord), 1);
}

