vec4 processTriangle(ivec2 coord, int subdata)
{
	mat4 mvp = mat4(
        texelFetch(iChannel0,ivec2(0,0),0),
        texelFetch(iChannel0,ivec2(0,1),0),
        texelFetch(iChannel0,ivec2(0,2),0),
        texelFetch(iChannel0,ivec2(0,3),0)
    );
    vec3 a = texelFetch(iChannel1, ivec2(coord.x, coord.y), 0).xyz;
    vec3 b = texelFetch(iChannel1, ivec2(coord.x, coord.y + 1), 0).xyz;
    vec3 c = texelFetch(iChannel1, ivec2(coord.x, coord.y + 2), 0).xyz;
    vec3 ep0 = (mvp * vec4(a,1.0)).xyz;
    vec3 ep1 = (mvp * vec4(b,1.0)).xyz;
    vec3 ep2 = (mvp * vec4(c,1.0)).xyz;

    // transform to clip space
    float w0 = 1.0/ep0.z;
    float w1 = 1.0/ep1.z;
    float w2 = 1.0/ep2.z;

    if (subdata == 0)
    {
        return vec4(w0,w1,w2,0);
    }

    vec2 cp0 = 2.0*ep0.xy * -w0;
    vec2 cp1 = 2.0*ep1.xy * -w1;
    vec2 cp2 = 2.0*ep2.xy * -w2;
    if (subdata == 1)
    {
        return vec4(cp0, cp1);
    }
    else if (subdata == 2)
    {
        return vec4(cp2, 0, 0);
    }
    float minY = min(min(cp0.y, cp1.y), cp2.y);
    float maxY = max(max(cp0.y, cp1.y), cp2.y);
    float minX = min(min(cp0.x, cp1.x), cp2.x);
    float maxX = max(max(cp0.x, cp1.x), cp2.x);
    return vec4(minX, maxX, minY, maxY);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 coord = ivec2(fragCoord);
    int index = coord.y / 7 * int(iResolution.x) + coord.x;
    int subdata = coord.y % 7;
    if (index >= LONGITUDE * (LATITUDE - 1) * 2 || subdata > 3)
    {
        discard;
    }
    fragColor = processTriangle(ivec2(index % int(iResolution.x), index / int(iResolution.x) * 7), subdata);
}

